Binding.Expression:
LET $allQuestionData := [Question Data]; 
LET $answerTypes := ARRAY("Freetext", "Multi line freetext", "Yes / No", "Date", "Dropdown");


LET $styles := 
ARRAY_SELECT(
  ARRAY_CONCAT("Summary", HASH_KEYS($allQuestionData), "Submit"), $(k, key) 

  LET $questionInGroup := HASH_GET(ARRAY_FIRST([Current Question Data]), "Question Group");
  LET $data := ARRAY_FIRST(HASH_GET($allQuestionData, $key)); 

  LET $questionAnswered := CALL($isQuestionAnswered, $data); 

  LET $selected := 
  OR(
	//Summary selected
	AND([View] = "Submitting", $key = "Submit"), 
	//Summary selected
	AND([Current Question Number] = 0, $key = "Summary"), 
	//Exact question selected
	$key = HASH_GET(ARRAY_FIRST([Current Question Data]), "Question GUID"),
	//Question within group selected
	$key = $questionInGroup
  ); 

  LET $answered := ARRAY_INDEXOF([Questions Answered], $key) > 0; 

  LET $css := 
  IF( 
	$selected = TRUE, 
	"[data-questionkey='" & $key & "'] > [data-state='selected'] {display: inline-block !important;}",

	OR(
      AND($key = "Submit", [Questionnaire Status] = "Complete"), 
	  $answered = TRUE, 
	), 
	"[data-questionkey='" & $key & "'] > [data-state='answered'] {display: inline-block !important;}",

    "[data-questionkey='" & $key & "'] > [data-state='notselected'] {display: inline-block !important;}"
  );

  RETURN $css

); 

RETURN "<style>" & ARRAY_JOIN($styles) & "</style>"

