		HASH_KEYS([Question Data]), $(i,item)

		LET $incomplete :=
		ARRAY_SELECTMANY(
			HASH_GET([Question Data], $item), 
			$(j, jitem)
			LET $respondentName := HASH_GET($jitem, "Evaluated Party");
			LET $answer := HASH_GET($jitem, "Answer");
			RETURN 
			IF(
				$answer = NULL, 
				$respondentName
			)	
		);
		RETURN IF(ARRAY_LENGTH($incomplete)>0, $i)

);

RETURN
HASH(
  "Question Number", 	COALESCE( ARRAY_FIRST($unanswered), 1), //either the 1st unanswered Q or 1
  "Display By Key",		IF( OR( [Type] = "Evaluation", [Type] = "Pre-Assessment"), "Evaluated Party UUID", "RecordID"),
  "Display By Data",	IF( OR( [Type] = "Evaluation", [Type] = "Pre-Assessment"), [Respondents], [Questionnaire RecordIDs])

)

