LET $paperclip:= "<svg " & " style = "" margin-left: 5px; display: inline-block; line-height: normal; vertical-align: bottom; width: 21px;height: 21px;""" &" viewBox=""0 0 21 21"" fill=""none"" xmlns=""http://www.w3.org/2000/svg""><circle cx=""10.5"" cy=""10.5"" r=""10.5"" fill=""#F0F0F0""/><path d=""M5.57031 15.4922C6.92969 16.8516 9.13281 16.8516 10.4922 15.4922C12.0391 13.9453 13.5391 12.4453 15.0859 10.8984C15.2031 10.7812 15.2031 10.6172 15.0859 10.5L14.6875 10.1016C14.5703 9.98438 14.4062 9.98438 14.2891 10.1016C12.7422 11.6484 11.2422 13.1484 9.69531 14.6953C8.78125 15.6094 7.28125 15.6094 6.36719 14.6953C5.45312 13.7812 5.45312 12.2812 6.36719 11.3672L11.6406 6.09375C12.25 5.48438 13.2344 5.48438 13.8438 6.09375C14.4531 6.70312 14.4531 7.6875 13.8438 8.29688L9.41406 12.7266C9.10938 13.0312 8.61719 13.0312 8.3125 12.7266C8.03125 12.4453 8.03125 11.9531 8.3125 11.6484L12.0859 7.89844C12.2031 7.78125 12.2031 7.61719 12.0859 7.5L11.6875 7.10156C11.5703 6.98438 11.4062 6.98438 11.2891 7.10156L7.53906 10.8516C6.78906 11.6016 6.78906 12.7969 7.53906 13.5234C8.26562 14.2734 9.46094 14.2734 10.1875 13.5234L14.6406 9.09375C15.6953 8.03906 15.6953 6.35156 14.6406 5.29688C13.5859 4.24219 11.8984 4.24219 10.8438 5.29688L5.57031 10.5703C4.21094 11.9297 4.21094 14.1328 5.57031 15.4922Z"" fill=""#474747""/></svg>";
