LET $icons:=  
HASH(

  "Unanswered",
  "<div style=""" &  $divStyle & """>"  &  REPLACESUBSTRING("currentColor", $circleSvg, $greyColour )& "</div>",

  "Answered",
  "<div style=""" &  $divStyle & """>"  & REPLACESUBSTRING("currentColor", $checkCircleSvg, $completeColour ) & "</div>",

  "Selected",
   "<div style=""" &  $divStyle & """>"  & REPLACESUBSTRING("currentColor", $rightArrowCircleSvg, $greyColour )& "</div>",

  "Selected - Answered",
   "<div style=""" &  $divStyle & """>"  & REPLACESUBSTRING("currentColor", $rightArrowCircleSvg, $completeColour )& "</div>"

);
