LET $getStatus:= $(Qnumber)
(
	LET $question:= ARRAY_INDEX(HASH_KEYS([Question Data]), $Qnumber);
  	LET $isSelected := $Qnumber = [Current Question Number];
	LET $questionData:= HASH_GET([Question Data], $question);
	LET $questionAnswered := ARRAY_DISTINCT(
	  								ARRAY_SELECT( 
									  	$questionData, $(i,item) 	
									  	LET $displayID := HASH_GET($item, IF( OR( [Type]= "Evaluation", [Type] = "Pre-Assessment"), "Evaluated Party UUID", "RecordID") );
									  	LET $docMandatory := HASH_GET( $item,"Document Upload Mandatory");
									  	LET $docAttached := IF( 
																$isSelected <> TRUE, HASH_GET($item, "Document Attached to Reference"),
		  														CURRENT_VALUE( "Document Attached to Reference", ARRAY_INDEXOF(EACH [Display ID], T($displayID) ) )
															);
									  	LET $docValidationPass:= IF( $docMandatory == TRUE, $docAttached==TRUE, TRUE );

									  	LET $answerType:= HASH_GET($item, "Answer Type");
									  	LET $answerCol:= IF(
															$answerType = "Date", "Answer (Date)",
															$answerType = "Number", "Answer (Number)",
															$answerType = "Yes / No", "Answer (Bool)",
										  					$answerType = "Image", "Image Attached to Reference",
															"Answer"
		  													);
									  	LET $answer := IF( 
															$isSelected <> TRUE, HASH_GET($item, $answerCol),
															CURRENT_VALUE( $answerCol, ARRAY_INDEXOF(EACH [Display ID], T($displayID) ) )
															);
									  	LET $answered := IF(
									  							$answerType = "Image", $answer == TRUE, AND( $answer != NULL, $answer != "")
										  					);
									  	RETURN AND( $answered, $docValidationPass)

  								));
	RETURN 
  	IF(
	  	//AND( $Qnumber = 0, [Question Data] <> NULL), "Answered", AND( $Qnumber = 0, [Question Data] = NULL), "Unanswered", 
	  	$questionAnswered = ARRAY(TRUE), "Answered", 
	  	"Unanswered"
  	)

);
//-------------
LET $questions:= 
ARRAY_SELECT(
	RANGE(0, [Question Count]+1), $(i,item)
	LET $text:= IF( $item = 0, "Summary", "Question "& $item);
	LET $status:= CALL($getStatus, $item);
	LET $selected := $item = [Current Question Number];
	LET $icon:= HASH_GET($icons, IF( AND ($status = "Answered", $selected = TRUE), "Selected - Answered", $selected = TRUE, "Selected", $status ));
	LET $color:= IF( $item = 0, $blackColour, $status = "Answered", $themeColour, $selected = TRUE, $blackColour, $greyColour );
	RETURN 
	"<div style= ""display: table-row;  cursor: pointer; "" >" &
	$icon & 
	"<div data-clickid="""& $item & """  style= "" padding: 0px; margin-left: 5px; color: " & $color &"; "&  $divStyle  &""">"&
  	$text &"</div>"&
	"</div>" 
	);

RETURN 
"<div style= "" font-size:10pt; background: #F8F8FF; padding: 15px; border-radius: 5px; display: block; height: 280px; position: relative; z-index: 2; width:200px; overflow-y:auto; overflow-x: hidden"">" &
	"<div style="" display: table; line-height:20px; "">"&
	"<div style= ""display: table-row;  border: 2px solid red; font-weight: bold; "" >" & IF( [Type] = "Standard", "Questionnaire", COALESCE([Type], "Evaluation")) &"</div>" &
	ARRAY_JOIN( $questions, "") & "</div>" &
"</div>"

TriggerCustomEvent.EventArgumentsFormula:
HASH(
  	"edit", FALSE
  )

HASH(
  "edit", FALSE
  )

TriggerCustomEvent.TargetRecordsFormula:
LET $completed:= 
ARRAY_WHERE(
  	HASH_KEYS([Questionnaire Header Data]), $(i,item)
  	LET $data:= HASH_GET([Questionnaire Header Data], $item);
  	RETURN HASH_GET($data, "Status") = "Complete"
);
RETURN
ARRAY_SELECT(
  	$completed, 
  	$(i,item) ARRAY("Questionnaire", $item)
  )

