	font-weight: bold;
    height: 100%;
    overflow: auto;
    display: grid;
    margin: auto;
    align-items: center;
    //justify-content: center;
    text-align: left; ";
LET $question:= HASH_GET(HASH_GET([Questions], [Selected Question]), "Question");
RETURN
"<div style=""" & $style & """>" & 
REPLACESUBSTRING(NEWLINE(), $question, "<br>") & 
"</div>"

