LET $getStatus:= $(Qnumber)
(
	LET $question:= ARRAY_INDEX(HASH_KEYS([Question Data]), $Qnumber);
  	LET $isSelected := $Qnumber = [Current Question Number];
	LET $questionData:= HASH_GET([Question Data], $question);
	LET $questionAnswered := ARRAY_DISTINCT(
	  								ARRAY_SELECT( 
									  	$questionData, $(i,item) 	
									  	LET $displayID := HASH_GET($item, IF( OR( [Type]= "Evaluation", [Type] = "Pre-Assessment"), "Evaluated Party UUID", "RecordID") );
									  	LET $docMandatory := HASH_GET( $item,"Document Upload Mandatory");
									  	LET $docAttached := IF( 
																$isSelected <> TRUE, HASH_GET($item, "Document Attached to Reference"),
		  														CURRENT_VALUE( "Document Attached to Reference", ARRAY_INDEXOF(EACH [Display ID], T($displayID) ) )
															);
									  	LET $docValidationPass:= IF( $docMandatory == TRUE, $docAttached==TRUE, TRUE );

									  	LET $answerType:= HASH_GET($item, "Answer Type");
									  	LET $answerCol:= IF(
															$answerType = "Date", "Answer (Date)",
															$answerType = "Number", "Answer (Number)",
															$answerType = "Yes / No", "Answer (Bool)",
										  					$answerType = "Image", "Image Attached to Reference",
															"Answer"
		  													);
									  	LET $answer := IF( 
															$isSelected <> TRUE, HASH_GET($item, $answerCol),
															CURRENT_VALUE( $answerCol, ARRAY_INDEXOF(EACH [Display ID], T($displayID) ) )
															);
									  	LET $answered := IF(
									  							$answerType = "Image", $answer == TRUE, AND( $answer != NULL, $answer != "")
										  					);
									  	RETURN AND( $answered, $docValidationPass)

  								));
	RETURN 
  	IF(
	  	//AND( $Qnumber = 0, [Question Data] <> NULL), "Answered", AND( $Qnumber = 0, [Question Data] = NULL), "Unanswered", 
	  	$questionAnswered = ARRAY(TRUE), "Answered", 
	  	"Unanswered"
  	)

);
