	LET $selected := $item = [Current Question Number];
	LET $icon:= HASH_GET($icons, IF( AND ($status = "Answered", $selected = TRUE), "Selected - Answered", $selected = TRUE, "Selected", $status ));
	LET $color:= IF( $item = 0, $blackColour, $status = "Answered", $themeColour, $selected = TRUE, $blackColour, $greyColour );
	RETURN 
	"<div style= ""display: table-row;  cursor: pointer; "" >" &
	$icon & 
	"<div data-clickid="""& $item & """  style= "" padding: 0px; margin-left: 5px; color: " & $color &"; "&  $divStyle  &""">"&
  	$text &"</div>"&
	"</div>" 
	);

