LET $upper:= HASH_GET([Question Display Data], "Range - Upper Limit");

LET $values := 
IF( 
  $type = "Range",  
  RANGE($lower, $upper-$lower + 1),

  HASH_GET([Question Display Data], "Options")
);

RETURN 
COALESCE(
  $values, 
  ARRAY()
)

LET $type:= HASH_GET([Question Display Data], "Answer Type");
RETURN
OR(
  $type = "Range",
  $type = "Tabstrip"
)

