LET $type:= HASH_GET([Question Display Data], "Answer Type");
LET $lower:= HASH_GET([Question Display Data], "Range - Lower Limit");
LET $upper:= HASH_GET([Question Display Data], "Range - Upper Limit");

LET $values := 
IF( 
  $type = "Range",  
  RANGE($lower, $upper-$lower + 1),

  HASH_GET([Question Display Data], "Options")
);

RETURN 
COALESCE(
  $values, 
  ARRAY()
)

LET $type:= HASH_GET([Question Display Data], "Answer Type");
RETURN
OR(
  $type = "Range",
  $type = "Tabstrip"
)

IF(
  [Image Attached to Reference]#[LineNumber] == TRUE, "View Images", "Upload" 
  )

AND(
    HASH_GET([Question Display Data],"Answer Type") = "Image",
	HASH_GET([Question Display Data],"Image Enabled") == TRUE
	//[Image Reference RecordID] == NULL()
)

IF(
  [Document Attached to Reference]#[LineNumber] == TRUE, "View Documents", "Upload" 
  )

LET $fails:=
ARRAY_SELECTMANY(
	HASH_KEYS([Question Data]), $(q, question)
	LET $valueArrays := HASH_GET([Question Data], $question);
  	RETURN
	ARRAY_SELECTMANY( 
		$valueArrays, $(i,item)
	  	LET $docMandatory := HASH_GET( $item,"Document Upload Mandatory");
		LET $mandatory := HASH_GET($item, "Mandatory");
		LET $displayID := HASH_GET($item, IF( OR( [Type]= "Evaluation", [Type] = "Pre-Assessment"), "Evaluated Party UUID", "RecordID") );
		LET $isSelected := [Current Question Number] = $q;
	  	LET $answerType:= HASH_GET($item, "Answer Type");
	  	LET $answerCol:= IF(
		  					$answerType = "Date", "Answer (Date)",
		  					$answerType = "Number", "Answer (Number)",
		  					$answerType = "Yes / No", "Answer (Bool)",
		  					$answerType = "Image", "Image Attached to Reference",
		  					"Answer"
		  				);
		LET $answer := IF( 
						$isSelected <> TRUE, HASH_GET($item, $answerCol),
		  				CURRENT_VALUE( $answerCol, ARRAY_INDEXOF(EACH [Display ID], T($displayID) ) )
						);
	  	LET $docAttached := IF( 
						$isSelected <> TRUE, HASH_GET($item, "Document Attached to Reference"),
		  				CURRENT_VALUE( "Document Attached to Reference", ARRAY_INDEXOF(EACH [Display ID], T($displayID) ) )
						);
	  	LET $notAnswered := IF( $answerType = "Image", $answer != TRUE, OR( $answer == NULL, $answer = "") );

	  	LET $docValidationFail:= IF( AND( $docMandatory = TRUE, $docAttached = FALSE),TRUE );

		RETURN 
		IF(
		OR(
		  AND( 
			$mandatory = TRUE,
			$notAnswered,
			),
		  $docValidationFail= TRUE
		  ),
			$q & $displayID & $answerCol & " - "& T($answer) & IF( $docValidationFail = TRUE, " - attachment required")
		)
	)	
 );
RETURN
IF( 
	ARRAY_LENGTH($fails) >0 ,
	TRUE,
	FALSE
)

LET $questionText := 
ARRAY_SELECT(
  HASH_KEYS([Questions]), $(i ,key)
  HASH_GET(HASH_GET([Questions], $key), "Question")
);

RETURN
COALESCE(
  ARRAY_SELECT(
	RANGE(0, [Question Count] + 1), $(i,item)
	 HASH(
		"value", $item,
	  	"text", 
	       IF(
			 $item = 0, "Summary", 
			 "Question "& $item & " - " & ARRAY_INDEX($questionText, $item)
	       )
		)	
	),
  ARRAY()
)

