  "edit", FALSE
  )

Binding.Expression:
LET $number:= ARRAY_LENGTH([Result Filter]);
RETURN
"Showing: "& IF( $number >0, $number, "All") & " respondents"

LET $columns := HASH_GET([Questionnaire Data], "columns");
LET $values := HASH_GET([Questionnaire Data], "values");

LET $ids:=
  ARRAY_INDEX(
	ARRAY_TRANSPOSE($values),
	ARRAY_INDEXOF($columns, "Evaluated Party UUID")
	);

LET $names:= 
  ARRAY_INDEX(
	ARRAY_TRANSPOSE($values),
	ARRAY_INDEXOF($columns, "Evaluated Party")
  );
LET $hash:= 
ARRAY_TO_HASH_GROUPED(
  		ARRAY_TRANSPOSE(ARRAY($ids, $names)), $(i, item) ARRAY_FIRST($item),
		$(i,item) ARRAY_FIRST($item) & " ("& ARRAY_INDEX($item, 2) & ")"
		);

RETURN 
ARRAY_SELECT(
	HASH_KEYS($hash), $(k, key) 
	HASH(
		"value", $key,
		"text", ARRAY_FIRST(HASH_GET($hash, $key))
	)
  )

