FieldEntry.Formula:
LET $questions := HASH_KEYS([Questions]);
LET $questionData := [Question Data];
LET $discrepancyThreshold := HASH_GET([Evaluation Settings], "Discrepancy Threshold");
LET $categories := 
ARRAY_DISTINCT(
  ARRAY_SELECT(
    $questions, $(q, question)
    HASH_GET(HASH_GET([Questions], $question), "Category")
  )
); 

LET $evaluatedParties := 
(
  LET $columns := HASH_GET([Questionnaire Data], "columns");
  LET $values := HASH_GET([Questionnaire Data], "values");

  RETURN
  ARRAY_DISTINCT(
    ARRAY_INDEX(
  	ARRAY_TRANSPOSE($values),
  	ARRAY_INDEXOF($columns, "Evaluated Party UUID")
  	)
  )
); 

LET $questionScores := 
ARRAY_SELECTMANY(
  $questions, $(q, question)
  LET $data := HASH_GET($questionData, $question);
  LET $questionData :=  
  ARRAY_SELECT(
	$data, $(e, evaluatorResult)
	LET $questionGUID := HASH_GET($evaluatorResult, "Question GUID"); 
	LET $question := HASH_GET($evaluatorResult, "Question"); 
	LET $score := HASH_GET($evaluatorResult, "Answer Score"); 
	LET $points := HASH_GET($evaluatorResult, "Score"); 
	LET $maxPoints := HASH_GET($evaluatorResult, "Answer Max Score"); 
	LET $assessor := HASH_GET($evaluatorResult, "Assessor"); 
	LET $evaluatedParty := HASH_GET($evaluatorResult, "Evaluated Party UUID"); 
	LET $category := HASH_GET($evaluatorResult, "Question Category"); 

	RETURN 
	HASH(
	  "Question GUID", $questionGUID,
	  "Question", $question,
	  "Assessor", $assessor,
	  "Evaluated Party UUID", $evaluatedParty,
	  "Question Number", $q,
	  "Answer Score", COALESCE($score, 0),
	  "Points", $points,
	  "Max Points", $maxPoints
	)
  );

  RETURN $questionData
);

LET $respondentToQuestionHash := 
ARRAY_TO_HASH_GROUPED(
  $questionScores, 
  $(i, item) 
  ARRAY(
    HASH_GET($item, "Evaluated Party UUID"),
    HASH_GET($item, "Question GUID")
  ), 
  $(i, item) $item
);

LET $organised :=
ARRAY_SELECT(
  $categories, $(c, category)

  (
	LET $questions := 
    ARRAY_WHERE(
	  $questions, $(q, questionGUID)
	  HASH_GET(ARRAY_FIRST(HASH_GET([Question Data], $questionGUID)), "Question Category") = $category
    ); 

    LET $questionData := 
    ARRAY_SELECT(
      $questions, $(q, questionGUID) 

      LET $category := HASH_GET(ARRAY_FIRST(HASH_GET($questionData, $questionGUID)), "Question Category"); 

      LET $partyResults := 
      ARRAY_SELECT(
    	$evaluatedParties, $(p, party)
    	LET $result := 
    	(
    	  LET $matchingData := 
		  HASH_GET(
		    $respondentToQuestionHash, 
		    ARRAY($party, $questionGUID)
		  );  
		  //ARRAY_WHERE(
    		//$questionScores, $(i, item)
    		//AND(
    		//  HASH_GET($item, "Evaluated Party UUID") = $party,
    		//  HASH_GET($item, "Question GUID") = $questionGUID
    		//)
    	  //); 

    	  LET $maxPoints := MAX(ARRAY_SELECT($matchingData, $(i, item) HASH_GET($item, "Max Points")));
          LET $highestScore := MAX(ARRAY_SELECT($matchingData, $(i, item) HASH_GET($item, "Answer Score") / $maxPoints));
          LET $lowestScore := MIN(ARRAY_SELECT($matchingData, $(i, item) HASH_GET($item, "Answer Score") / $maxPoints));
          LET $range := $highestScore - $lowestScore;
          LET $discrepancy := AND($discrepancyThreshold <> NULL, $range >= $discrepancyThreshold);
    	  LET $average := AVERAGE(ARRAY_SELECT($matchingData, $(i, item) HASH_GET($item, "Answer Score") / $maxPoints));
    	  LET $averagePoints := AVERAGE(ARRAY_SELECT($matchingData, $(i, item) HASH_GET($item, "Points")));

    	  RETURN 
    		HASH(
    		  "Average", ROUND($average, 5), 
    		  "Average Points", ROUND($averagePoints, 5), 
    		  "Max Points", $maxPoints, 
          	  "Range", $range,
			  "Discrepancy", $discrepancy//,
          	  //"Highest Score", $highestScore
    		)
    	);
    	RETURN $result
      );

      LET $groupAverage := 
      AVERAGE(
    	ARRAY_SELECT(
    	  $partyResults, $(i, item)
    	  HASH_GET($item, "Average")
        )
      ); 

	  LET $groupHighestAverage := 
      MAX(
    	ARRAY_SELECT(
    	  $partyResults, $(i, item)
    	  HASH_GET($item, "Average")
        )
      ); 

      LET $groupHighestAveragePoints := 
      GREATEST(
    	ARRAY_SELECT(
    	  $partyResults, $(i, item)
    	  HASH_GET($item, "Average Points")
        )
      ); 

	  LET $groupAveragePoints := 
      AVERAGE(
    	ARRAY_SELECT(
    	  $partyResults, $(i, item)
    	  HASH_GET($item, "Average Points")
        )
      ); 

	  LET $maxPoints := 
      MAX(
    	ARRAY_SELECT(
    	  $partyResults, $(i, item)
    	  HASH_GET($item, "Max Points")
        )
      ); 

	  LET $discrepancy := 
      GREATEST(
    	ARRAY_SELECT(
    	  $partyResults, $(i, item)
    	  HASH_GET($item, "Discrepancy")
        )
      ); 

	  RETURN 
      HASH_MERGE(
        INDEXES_TO_KEYS($partyResults, $evaluatedParties), 
    	  HASH(
    	    "Discrepancy", $discrepancy,
    	    "Group Average", ROUND($groupAverage, 5),
    	    "Group Average Points", ROUND($groupAveragePoints, 5),
    	    "Max Points", $maxPoints, 
			"Group Highest Average", $groupHighestAverage,
			"Group Highest Average Points", $groupHighestAveragePoints
    	  )
        )
      );

    LET $criteriaMaxScore := 
    MAX(ARRAY_SELECT(
	    $evaluatedParties, $(p, party) 
      SUM(
        ARRAY_SELECT(
	        $questionData, $(i, item) 
	        LET $data := HASH_GET($item, $party); 
          RETURN HASH_GET($data, "Max Points")
        )
      )
    ));

	LET $questionHasDiscrepancy := 
    GREATEST(
	  ARRAY_SELECT(
	    $evaluatedParties, $(p, party) 
	    LET $discrepancy := 
	    GREATEST(ARRAY_SELECT(
		  $questionData, $(i, item) 
		  LET $data := HASH_GET($item, $party); 
          RETURN HASH_GET($data, "Discrepancy")
        ));
        RETURN $discrepancy
      )
	);

	LET $thisPartyAllQuestionPoints := 
    ARRAY_SELECT(
	  $evaluatedParties, $(p, party) 
	    LET $points := ARRAY_SELECT(
		    $questionData, $(i, item) 
			  LET $data := HASH_GET($item, $party); 
        RETURN 
        HASH_GET($data, "Average Points")
      );
      RETURN 
      NUMBERFORMAT(SUM($points), "N2") & " / " & NUMBERFORMAT($criteriaMaxScore, "N2")
    );

	LET $thisPartyAllQuestionScores := 
    ARRAY_SELECT(
	  $evaluatedParties, $(p, party) 
	    LET $points := ARRAY_SELECT(
		    $questionData, $(i, item) 
			  LET $data := HASH_GET($item, $party); 
        RETURN 
        HASH_GET($data, "Average Points")
      );
      RETURN 
      ROUND(IFERROR(SUM($points) / $criteriaMaxScore, 0), 4)
    );

	RETURN 
	HASH_MERGE(
	  INDEXES_TO_KEYS($questionData, $questions), 
	  HASH(
	  "Summary",
	    HASH(
		  "Discrepancy", 
		    $questionHasDiscrepancy,

		  "Criteria Score - Winners", 
		  (
			LET $highestScore := MAX($thisPartyAllQuestionScores); 
		    RETURN ARRAY_WHERE($evaluatedParties, $(i, item) ARRAY_INDEX($thisPartyAllQuestionScores, $i) = $highestScore)
		  ),
		  "Criteria Score - Percentage", 
		    HASH_MERGE(INDEXES_TO_KEYS($thisPartyAllQuestionScores, $evaluatedParties)),
		  "Criteria Score - Points",
			HASH_MERGE(INDEXES_TO_KEYS($thisPartyAllQuestionPoints, $evaluatedParties))  
	    )
	  )
	)
  )
);

LET $hashed := 
INDEXES_TO_KEYS(
  $organised, $categories
);

RETURN $hashed

LET $data := [Aggregate Results];

LET $rightArrow := 
"<svg style=""height: 16px;"" xmlns=""http://www.w3.org/2000/svg"" viewBox=""0 0 448 512""><!--! Font Awesome Pro 6.2.1 by @fontawesome - https://fontawesome.com License - https://fontawesome.com/license (Commercial License) Copyright 2022 Fonticons, Inc. --><path d=""M438.6 278.6c12.5-12.5 12.5-32.8 0-45.3l-160-160c-12.5-12.5-32.8-12.5-45.3 0s-12.5 32.8 0 45.3L338.8 224 32 224c-17.7 0-32 14.3-32 32s14.3 32 32 32l306.7 0L233.4 393.4c-12.5 12.5-12.5 32.8 0 45.3s32.8 12.5 45.3 0l160-160z""/></svg>";

