"<svg style =""height: 11px; padding-left: 2px;"" xmlns=""http://www.w3.org/2000/svg"" aria-hidden=""true"" focusable=""false"" data-prefix=""fas"" viewBox=""0 0 512 512""><path fill=""#FAA700"" d=""M256 512c141.4 0 256-114.6 256-256S397.4 0 256 0S0 114.6 0 256S114.6 512 256 512zm0-384c13.3 0 24 10.7 24 24V264c0 13.3-10.7 24-24 24s-24-10.7-24-24V152c0-13.3 10.7-24 24-24zm32 224c0 17.7-14.3 32-32 32s-32-14.3-32-32s14.3-32 32-32s32 14.3 32 32z""/></svg>";

LET $evaluatedParties := 
(
  LET $columns := HASH_GET([Questionnaire Data], "columns");
  LET $values := HASH_GET([Questionnaire Data], "values");

  RETURN
  ARRAY_DISTINCT(
    ARRAY_INDEX(
  	  ARRAY_TRANSPOSE($values),
  	  ARRAY_INDEXOF($columns, "Evaluated Party UUID")
  	)
  )
);

LET $drilldownLevel := [Summary Drilldown Level];
LET $summaryFormat := [Summary Result Format];

LET $header := 
ARRAY_CONCAT(
  "<th>Question #</th>",
  "<th>Criteria</th>", 
  ARRAY_SELECT($evaluatedParties, $(i, item) "<th>" & $item & "</th>"), 
  "<th></th>" //View column
  //"<th>Average</th>"
); 

LET $rows := 
ARRAY_SELECTMANY(
  HASH_KEYS($data), $(i, criteria)
  LET $questions := HASH_GET($data, $criteria);
  LET $numberOfQuestions := ARRAY_LENGTH($questions) - 1;

  LET $questionResults := 
  ARRAY_SELECT(
    ARRAY_TAKE($questions, $numberOfQuestions), $(q, questionData) 
    LET $questionUUID := ARRAY_FIRST($questionData);
	LET $questionNumber := ARRAY_INDEXOF(HASH_KEYS([Questions]), $questionUUID);
    LET $answerData := ARRAY_INDEX($questionData, 2);
    LET $highestScore := HASH_GET($answerData, "Group Highest Average");
	LET $extradataIndexes := 6; //skip the extra bits that are at the end of the array 

	LET $averages := 
    ARRAY_SELECTMANY(
      ARRAY_TAKE(HASH_KEYS($answerData), ARRAY_LENGTH($answerData) - $extradataIndexes), $(a, key)
      LET $data := HASH_GET($answerData, $key);
      LET $average := HASH_GET($data, "Average");
      LET $points := HASH_GET($data, "Average Points");
      LET $maxPoints := HASH_GET($data, "Max Points");
	  LET $discrepancy := HASH_GET($data, "Discrepancy") = TRUE;
	  LET $highScore := $average = $highestScore;
	  LET $classes := 
	  ARRAY_WHERE(
		ARRAY(
		  //IF($discrepancy = TRUE, "discrepancy"),
	      //IF($highScore = TRUE, "highscore")
        ), $(i, item) 
		$item <> NULL
	  ); 
	  RETURN 

	  "<td" & 
	    IF(ARRAY_LENGTH($classes) > 0, " class='" & ARRAY_JOIN($classes, " ") & "'") & 
	    " data-clickid='" & $questionNumber & "'" &
	  ">" & 
		IF(
		  $summaryFormat = "Percentage",
		    NUMBERFORMAT($average, "P"),
		    NUMBERFORMAT($points, "N2") & " / " & NUMBERFORMAT($maxPoints, "N2")
		) & 
	    IF( 
		  AND([Show Winners in Table] = TRUE, $highScore = TRUE, $highestScore > 0), 
		  $awardIcon
		) &
	    IF( 
		  AND([Show Discrepancies in Table] = TRUE, $discrepancy = TRUE), 
		  $exclamation
		) &
	  "</td>"
    );

    LET $averageResponse := HASH_GET($answerData, "Group Average");
	LET $averageResponseFormatted := 
	IF(
		$summaryFormat = "Percentage", NUMBERFORMAT($averageResponse, "P"), 
	    "Avg: " & NUMBERFORMAT($averageResponsePoints, "N2") & " / " & NUMBERFORMAT($averageResponseMaxPoints, "N2")
	);

    LET $averageResponseMaxPoints := HASH_GET($answerData, "Max Points");
    LET $averageResponsePoints := HASH_GET($answerData, "Group Average Points");

    RETURN 

    ARRAY_CONCAT(
	  "<td data-clickid='" & $questionNumber & "'>" & $questionNumber & "</td>",
	  "<td data-clickid='" & $questionNumber & "'>" & $criteria & "</td>", 
	  $averages,
	  "<td data-clickid='" & $questionNumber & "'>" & $rightArrow & "</td>"
	  //"<td>" & $averageResponseFormatted & "</td>"
	)
  );

  //Start criteria Aggregate Row
  LET $aggregateData := HASH_GET($questions, "Summary"); 
  LET $winners := HASH_GET($aggregateData, "Criteria Score - Winners"); 

  LET $aggregateData := 
  IF( 
    $summaryFormat = "Percentage", 
	HASH_GET($aggregateData, "Criteria Score - Percentage"), 
	HASH_GET($aggregateData, "Criteria Score - Points")
  ); 

  LET $aggregates :=
  ARRAY_SELECT(
    HASH_KEYS($aggregateData), $(k, key) 

	LET $highestScore := 
	AND(
	  [Show Winners in Table] = TRUE, 
	  ARRAY_INDEXOF($winners, ARRAY_INDEX($evaluatedParties, $k)) > 0
	);

	RETURN
    "<td style='font-weight: bold;'>" & 
	IF( 
	  $summaryFormat = "Percentage", 
	    NUMBERFORMAT(HASH_GET($aggregateData, $key), "P"), 
	  HASH_GET($aggregateData, $key)
	) & 
	IF( 
	  AND(
		HASH_GET($aggregateData, ARRAY_FIRST($winners)) > 0, 
		$highestScore = TRUE
	  ), 
	  $awardIcon
	) &
	"</td>"
  ); 

  LET $consensusRowResult := 
  ARRAY(ARRAY_CONCAT(
    "<td style='font-weight: bold;'>TOTAL</td>", 
    "<td style='font-weight: bold;'>" & $criteria & "</td>", 
    $aggregates, 
	"<td style='font-weight: bold;'></td>"
  ));
  //End criteria Aggregate Row

  RETURN 
  ARRAY_CONCAT(
	$questionResults, 
	$consensusRowResult
  )
);

LET $tableRows := 
ARRAY_CONCAT(
  ARRAY($header), 
  $rows
); 

RETURN
"<table id='aggregate-results-table'>" & 
ARRAY_JOIN(
  ARRAY_SELECT(
    $tableRows, $(i, item) 
    "<tr>" & 
	  ARRAY_JOIN($item) & 
    "</tr>"
  )
) & 
"</table>"

FormulaDrivenChangeFieldValues.FieldValuesFormula:
LET $typeMatch := 
OR(
  [Type] = "Evaluation", 
  [Type] = "Pre-Assessment"
);

LET $displayByKey  := 	IF($typeMatch = TRUE, "Evaluated Party UUID", "RecordID");
LET $displayByData := 	IF($typeMatch = TRUE, [Respondents], [Questionnaire RecordIDs]);

LET $currentQuestionNumber := N(COALESCE(HASH_GET($eventargs, "Question Number"), 1));

LET $view := 
IF(
  $currentQuestionNumber = 0, 
    "Summary",
  $currentQuestionNumber > ARRAY_LENGTH([Question Data]), 
    "Submitting",
  "Questions"
);


LET $pageNumber := [Page Number]; 
LET $pageLength := [Page Length]; 
LET $skipAmount := GREATEST(($pageNumber - 1), 0) * $pageLength;

LET $selectedQuestion := ARRAY_INDEX(HASH_KEYS([Questions]), $currentQuestionNumber);
LET $currentQuestionData := HASH_GET([Question Data], $selectedQuestion);

LET $displayIndexMultiEvaluator := 
ARRAY_SELECT(
	ARRAY_TAKE(ARRAY_SKIP($displayByData, $skipAmount), $pageLength), $(j, jitem)
	ARRAY_WHERE(
	  ARRAY_SELECT(
      	$currentQuestionData, $(i, item) 
      	IF(
		  HASH_GET($item, $displayByKey) = $jitem, 
		  $i
		)
  	  ), $(i,item) $item <> NULL
   )
); 

LET $getColumnDataMultiEvaluator := $(key)
ARRAY_SELECT( 
  $displayIndexMultiEvaluator, $(s, set) 
  ARRAY_SELECT(
    $set, $(i, index) 
	HASH_GET(
	  ARRAY_INDEX($currentQuestionData, $index), 
	  $key
	)
  )
);

LET $questionDataForThisPage := ARRAY_TAKE(ARRAY_SKIP($currentQuestionData, $skipAmount), $pageLength);

LET $individualEvaluatorResult :=  
INDEXES_TO_KEYS(
  ARRAY_TRANSPOSE(
    ARRAY_SELECT(
      $questionDataForThisPage, $(q, questionData)
      ARRAY(
		$currentQuestionNumber,
		$selectedQuestion, 
		$currentQuestionData, 


        HASH_GET($questionData, "Answer"), 
    	HASH_GET($questionData, "Answer (Bool)"), 
    	HASH_GET($questionData, "Answer (Date)"), 
    	HASH_GET($questionData, "Answer (Number)"), 
    	HASH_GET($questionData, "Image Reference RecordID"), 
    	HASH_GET($questionData, "Document Reference RecordID"), 
		HASH_GET($questionData, "Comments")
      )
    )
  ),
  ARRAY(
    "Current Question Number",
    "Selected Question",
    "Current Question Data",
    "Answer", 
    "Answer (Bool)", 
    "Answer (Date)", 
    "Answer (Number)", 
    "Image Reference RecordID", 
    "Document Reference RecordID", 
	"Comments"
  )
);

LET $multiEvaluatorResult := 
(
  LET $data := 
  ARRAY(
     CALL($getColumnDataMultiEvaluator, "Assessor"),
	 CALL($getColumnDataMultiEvaluator, "Assessor Name"),
	 CALL($getColumnDataMultiEvaluator, "Answer"),
     CALL($getColumnDataMultiEvaluator, "Answer (Bool)"),
     CALL($getColumnDataMultiEvaluator, "Answer (Date)"),
     CALL($getColumnDataMultiEvaluator, "Answer (Number)"),
     CALL($getColumnDataMultiEvaluator, "Image Reference RecordID"),
     CALL($getColumnDataMultiEvaluator, "Document Reference RecordID"),
     CALL($getColumnDataMultiEvaluator, "Comments"),
     CALL($getColumnDataMultiEvaluator, "Answer Score Percentage Formatted")
  );

  LET $result :=
  ARRAY_SELECT(
    $displayIndexMultiEvaluator, $(r, respondent) 
    ARRAY_SELECT(
	  ARRAY_TRANSPOSE(
	    ARRAY_SELECT(
          $data, $(i, item) 
  	      ARRAY_INDEX($item, $r)
        )
	  ), $(i, item)
      INDEXES_TO_KEYS(
		$item,
		ARRAY(
          "Assessor",
          "Assessor Name",
          "Answer",
          "Answer (Bool)",
          "Answer (Date)",
          "Answer (Number)",
          "Image Reference RecordID",
          "Document Reference RecordID",
          "Comments",
          "Answer Score Percentage Formatted"
        )
	  )
	)
  );

  RETURN 
  HASH(
	"Current Question Number", ARRAY($currentQuestionNumber),
    "Selected Question", ARRAY($selectedQuestion),
    "Current Question Data", ARRAY($currentQuestionData),
    "Display ID", $displayByData,
    "Answer Data", $result
  )
);

LET $result := 
IF( 
  [Selected Evaluators] <> NULL, 
    $multiEvaluatorResult, 

  $individualEvaluatorResult
);

LET $displayItems := ARRAY_TAKE(ARRAY_SKIP($displayByData, $skipAmount), $pageLength); 

RETURN 
HASH_MERGE(
  $result, 
  HASH(
	"View", $view, 
    "Display ID", $displayItems, 
    "Question Display Index", RANGE(($pageNumber - 1) * $pageLength + 1, ARRAY_LENGTH($displayItems))
  )
)

FieldEntry.Formula:
LET $new := [Current Question Number];
LET $old := [Visited Questions];
LET $combined := ARRAY_CONCAT($old, $new);
RETURN 
ARRAY_DISTINCT(ARRAY_SORT(ARRAY_WHERE($combined, $(i, item) $item <> NULL)))

LET $data := 
ARRAY_TO_HASH_GROUPED(
  [Current Question Data], 
  $(i, item) HASH_GET($item, "Evaluated Party UUID"), 
  $(i, item) $item
); 

LET $data := 
ARRAY_SELECT(
  HASH_KEYS($data), $(k, key) 
  ARRAY_FIRST(HASH_GET($data, $key))
);

RETURN 
ARRAY_SELECT(
  EACH [Question Display Index], $(i, index) 
  ARRAY_INDEX($data, $index)
)

LET $columns := ARRAY( "RecordID", "Status", "Participant", "Participant Name", "Assignee", "Evaluated Party", "Evaluated Party UUID", "Assessor Name", "Assessor", "Assessment Started", "Assessment Complete", "Completion - Allow Resubmit", "Locked" );
LET $filter := ARRAY_JOIN( ARRAY_SELECT([Questionnaire RecordIDs], $(i,item) "[RecordID] FAST_EQUAL_TO "& DB_ENCODECONSTANT($item) ), " OR ");
LET $lookup := DB_LOOKUPALL( $columns, "Questionnaire", "[LineNumber] FAST_EQUAL_TO 1 AND [Status] <> 'Deleted' AND ("& $filter & ")", HASH("sort", "[Evaluated Party] ASC")//[RecordID] IN "& DB_ENCODECONSTANT([Questionnaire RecordIDs]) );
RETURN ARRAY_TO_HASH( $lookup, $(i,item) ARRAY_INDEX($item, 1), //RecordID $(i,item) INDEXES_TO_KEYS($item, $columns) )

LET $update:= HASH_GET($eventargs, "update");
LET $columns := ARRAY( "RecordID", "LineNumber", "Question", "Question GUID", "Question Category", "Mandatory", "Attachment Enabled", "Guidance Document Reference RecordID", "Guidance Document Provided", "Document Upload Mandatory", "Answer Type", "Options", "Option Scores", "Question Type",
"Answer", "Comments", "Comments Applicable", "Answer Max Score", "Answer Score Percentage Formatted", "Answer Score", "Score", "Answer (Bool)", "Answer (Number)", "Answer (Date)", "Range - Lower Limit", "Range - Upper Limit", "Image Enabled", "Image Upload Unique Reference", "Image Reference RecordID", "Image Attached to Reference",
"Question Group", "Question Sort ID", "Group Sort ID", "Group Name",
"Question Dependency - Applicable", "Question Dependency - Question UUID", "Question Dependency - Rule", "Question Dependency - Rule Type",
"Document Upload Unique Reference", "Document Reference RecordID", "Document Attached to Reference",
"Evaluated Party", "Evaluated Party UUID", "Assessor Name", "Assessor", "Participant", "Participant Name", "Assignee", "Description", "Status", "Scored", "Axes", "Axis 1 Score Percentage", "Axis 2 Score Percentage", "Result", "Result Description", "Assessment Complete", "Max Score", "Total Score Percentage", "Four Quadrant Chart Applicable", "Show Category Results", "Completion - Allow Review", "Completion - Try Again" );
LET $contiguousIds := GREATEST(ARRAY_DISTINCT([Questionnaire RecordIDs])) - LEAST(ARRAY_DISTINCT([Questionnaire RecordIDs])) + 1 = ARRAY_LENGTH([Questionnaire RecordIDs]);
LET $filter := IF( $contiguousIds = TRUE, "[RecordID] <= " & DB_ENCODECONSTANT(GREATEST([Questionnaire RecordIDs])) & " AND [RecordID] >= " & DB_ENCODECONSTANT(LEAST([Questionnaire RecordIDs])),
"[RecordID] IN " & DB_ENCODECONSTANT(COALESCE([Questionnaire RecordIDs], ARRAY())) );
LET $data := IF( $update <> NULL, $update,
( LET $lookup := DB_LOOKUPALL( $columns, "Questionnaire", "[Question] <> NULL AND " & $filter & IF( [Category Filter] <> NULL, " AND [Question Category] = " & DB_ENCODECONSTANT([Category Filter]) ), HASH("sort", "[Evaluated Party] ASC, [LineNumber] ASC, [Evaluated Party UUID] ASC, [Assessor Name] ASC") );
RETURN $lookup ) );
RETURN HASH( "columns", $columns, "values", $data )

LET $columns := HASH_GET([Questionnaire Data], "columns");
LET $values := HASH_GET([Questionnaire Data], "values");

RETURN
ARRAY_TO_HASH_GROUPED(
  $values,
  $(i, item) ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Question GUID")),
  $(i, item) INDEXES_TO_KEYS($item, $columns)
)

LET $columns := HASH_GET([Questionnaire Data], "columns");
LET $values := HASH_GET([Questionnaire Data], "values");

//Combined set of info sections and question sections
LET $data :=
ARRAY_DISTINCT(
  ARRAY_SELECT(
    $values, $(i,item)
    ARRAY(
       ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Question GUID")),
       ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Question Category")),
       ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Question")),
       ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Question Type")),
       ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Group Name"))
     ) 
   )
);

//Extract only questions
LET $questionsOnly := 
ARRAY_WHERE(
  $data, $(i, item)
  ARRAY_INDEX($item, 4) <> "Info"
); 

LET $questionKeys := 
ARRAY_FIRST(ARRAY_TRANSPOSE($questionsOnly)); 

LET $groupedByCategory := 
ARRAY_TO_HASH_GROUPED(
  $questionsOnly, 
  $(i, item) ARRAY_INDEX($item, 2),
  $(i, item) ARRAY_INDEX($item, 1)
);

LET $groupedByQuestionGroup := 
ARRAY_TO_HASH_GROUPED(
  $questionsOnly, 
  $(i, item) ARRAY_INDEX($item, 5),
  $(i, item) ARRAY_INDEX($item, 1)
);

LET $result :=  
ARRAY_TO_HASH(
  $data,
  $(i,item) ARRAY_FIRST($item),
  $(i,item) 


  LET $questionKey := ARRAY_INDEX($item, 1);
  LET $category := ARRAY_INDEX($item, 2);
  LET $questionGroup := ARRAY_INDEX($item, 5);

  LET $categoryQuestions := HASH_GET($groupedByCategory, $category); 
  LET $groupQuestions := HASH_GET($groupedByQuestionGroup, $questionGroup); 

  LET $keys := 
  ARRAY(
	//Relates to data in Data
    "Category", 
    "Question", 
    "Question Type", 
	"Group Name",

	//Extra
    "Question Number", 
	"Question Number in Category", 
	"Question Number in Question Group", 
	"Number of Questions in Question Group"
  );

  LET $values := 
  ARRAY_CONCAT(
	ARRAY_SKIP($item,1), 
	ARRAY_INDEXOF($questionKeys, $questionKey), //question number in questionnaire
	ARRAY_INDEXOF($categoryQuestions, $questionKey), //question number in category
	ARRAY_INDEXOF($groupQuestions, $questionKey),  //question number in group
	ARRAY_LENGTH($groupQuestions) //questions in group
  );

  RETURN INDEXES_TO_KEYS($values, $keys)
);

RETURN $result

LET $allQuestionKeys := HASH_KEYS([Questions]);

LET $topLevelNavigation :=
ARRAY_WHERE(
  $allQuestionKeys, $(i, key) 
  AND(
    HASH_GET(HASH_GET([Questions], $key), "Question") <> NULL,
    OR(
      HASH_GET(HASH_GET([Questions], $key), "Question Type") = "Category",
      HASH_GET(HASH_GET([Questions], $key), "Question Number in Category") = 1,
      HASH_GET(HASH_GET([Questions], $key), "Question Number in Category") = NULL,
      HASH_GET(HASH_GET([Questions], $key), "Group Name") = NULL
    )
  )
);

LET $result :=  
ARRAY_SELECT(
  $topLevelNavigation, $(t, groupID)

  LET $matches := 
  ARRAY_WHERE(
    $allQuestionKeys, $(i, key) 
    (  
	  LET $data := HASH_GET([Question Data], $key);
	  RETURN HASH_GET(ARRAY_FIRST($data), "Question Group") = $groupID
    )
  );

  RETURN $matches
);

RETURN 
HASH(
  "Hierarchy", 
  INDEXES_TO_KEYS(
    $result,
    $topLevelNavigation
  ), 
  "Top Level", 
  $topLevelNavigation
)

LET $columns := HASH_GET([Questionnaire Data], "columns");
LET $values := HASH_GET([Questionnaire Data], "values");

RETURN
ARRAY_DISTINCT(
  ARRAY_INDEX(
	ARRAY_TRANSPOSE($values),
	ARRAY_INDEXOF($columns, "Evaluated Party UUID")
  )
)

LET $columns := HASH_GET([Questionnaire Data], "columns");
LET $values := HASH_GET([Questionnaire Data], "values");

RETURN 
ARRAY_SELECTMANY(
  [Respondents], $(i, respondentID) 

  ( 
	LET $match := 
    ARRAY_FIRST(ARRAY_WHERE(
  	  $values, $(i, item) 
  	  ARRAY_INDEX(
  	    $item, 
  	    ARRAY_INDEXOF($columns, "Evaluated Party UUID")
  	  ) = $respondentID
    )); 

    RETURN 
	ARRAY_INDEX(
	  $match, 
	  ARRAY_INDEXOF($columns, "Evaluated Party")
	)
  )
)

