LET $allQuestionData := [Question Data]; 
LET $answerTypes := ARRAY("Freetext", "Multi line freetext", "Yes / No", "Date", "Dropdown");

LET $isQuestionAnswered := $(data)
(
  LET $answerProvided := 
  ARRAY_INDEXOF(
	ARRAY_SELECTMANY(
	$data, $(i, item)
      OR(
      //input provided
        NOT(ISBLANK(HASH_GET($item, "Answer"))), 
        NOT(ISBLANK(HASH_GET($item, "Answer (Bool)"))), 
        NOT(ISBLANK(HASH_GET($item, "Answer (Number)"))), 
        NOT(ISBLANK(HASH_GET($item, "Answer (Date)")))
      )
    ), 
	FALSE
  ) = NULL; 

  LET $expectingAnAnswer :=  
  ARRAY_INDEXOF(
    $answerTypes, 
    HASH_GET(ARRAY_FIRST($data), "Answer Type")
  ) > 0;

  LET $documentRequiredAndProvided := 
  IF( 
    //document required
    HASH_GET(ARRAY_FIRST($data), "Document Upload Mandatory") = TRUE, 

    //document uploaded
    HASH_GET(ARRAY_FIRST($data), "Document Attached to Reference") = TRUE
  );

  LET $answered := 
  AND(
	IF(
	  $expectingAnAnswer = TRUE,
	  $answerProvided
	), 
    $documentRequiredAndProvided
  );

  RETURN $answered
); 

LET $answered := 
ARRAY_SELECT(
  ARRAY_CONCAT("Summary", HASH_KEYS($allQuestionData)), $(k, key) 

  LET $questionInGroup := HASH_GET(ARRAY_FIRST([Current Question Data]), "Question Group");
  LET $data := HASH_GET($allQuestionData, $key); 
  LET $childQuestions := HASH_GET(HASH_GET([Question Structure], "Hierarchy"), $key);
  LET $questionType := HASH_GET($data, "Question Type");

  LET $questionAnswered := CALL($isQuestionAnswered, $data); 

  LET $isCategoryAndQuestionsAreAnswered := 
  AND(
	$questionType = "Category",
    //childQuestions 
	ARRAY_SELECT(
	  $childQuestions, $(i, key) 
	  LET $data := ARRAY_FIRST(HASH_GET($allQuestionData, $key));
      RETURN CALL($isQuestionAnswered, $data)
	)
  ); 

  LET $answered := 
  IF( 
    $questionType = "Category", 
	  $isCategoryAndQuestionsAreAnswered, 

	$questionAnswered = TRUE
  ); 

  RETURN 
  IF( 
	$answered = TRUE, 
	  $key
  )
); 

RETURN 
ARRAY_WHERE(
  $answered, $(i, item) 
  $item <> NULL
)

LET $recordsToCheck:= HASH_GET($eventargs, "records");
LET $recordsCount := ARRAY_LENGTH($recordsToCheck);

LET $filter := ARRAY_JOIN( ARRAY_SELECT( $recordsToCheck, $(i,item) "[RecordID] FAST_EQUAL_TO "& DB_ENCODECONSTANT($item) ), " OR ");

LET $matchesCount:= 
ARRAY_FIRST(ARRAY_FIRST(
	DB_REPORT(
		"COUNT([Editor GUID])",
		"Questionnaire",
		"[LineNumber] FAST_EQUAL_TO 1 AND [Editor GUID] FAST_EQUAL_TO "& DB_ENCODECONSTANT([Editor GUID])
		&" AND (" &$filter& ")"
	)
));

RETURN $recordsCount = $matchesCount

//this columns has to be the same as question data - lookup
LET $columns := HASH_GET([Questionnaire Data], "columns");
LET $values := HASH_GET([Questionnaire Data], "values");

LET $groupedbyRecord:=
ARRAY_GROUP(
  $values, 
  $(item) ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "RecordID") )
);

//outer hash by recordID and inner has by column>value -- HASH($recordID>>HASH($column, ARRAY(values)))
LET $columnIndexed:=
ARRAY_TO_HASH(
  $groupedbyRecord, $(i,item) ARRAY_INDEX($item, 1), //recordID
  $(i,item) INDEXES_TO_KEYS( ARRAY_TRANSPOSE(ARRAY_INDEX($item, 2)), $columns)
);
//////////function to inject newValue into array of a multiline column
LET $buildArray := $(array, newValue, targetLine)(
	LET $before := 
	IF(
		$array = ARRAY(),
		ARRAY_SELECT(RANGE(0, $targetLine - 1), $(i, item) NULL()),
		ARRAY_TAKE($array, $targetLine - 1)
	);

	LET $before := 
	IF(
		ARRAY_LENGTH($before) < $targetLine - 1, 
		ARRAY_PAD($before, $targetLine - 1),
		$before
	);

	RETURN
	ARRAY_CONCAT(
		$before,
		$newValue,
		ARRAY_SKIP($array, $targetLine) 
	)
);
///
LET $updatedColumns:=
	ARRAY_SELECT(
		EACH [Question Display Data],
	    //ARRAY_WHERE( EACH [Question Display Data], $(l, line) HASH_GET($line, "Status") <> "Complete"), //filterout QRs that are already complete
		$(i,item) 
		IF( 
		  	OR([Can Update Scores] = TRUE, HASH_GET($item, "Status") <> "Complete"), //filterout QRs that are already complete
			HASH_MERGE( $item,
				HASH( "new",
					ARRAY_SELECTMANY( 
						[Columns To Check], $(c,column) 
						IF( HASH_GET($item, $column) != CURRENT_VALUE($column, $i), $column
					  	)
					)
				)
			)
		)	
	);

LET $newValues:= 
		ARRAY_SELECT(
			$updatedColumns, $(i,item)
			LET $recordID:= HASH_GET($item, "RecordID");
			LET $targetLine:= HASH_GET($item, "LineNumber");
			LET $new:= HASH_GET($item, "new");
			LET $newValues:= INDEXES_TO_KEYS(
									ARRAY_SELECT( $new, $(c,column) CURRENT_VALUE($column, $i)),
									$new
								);

			LET $valueArray:= HASH_GET($columnIndexed, $recordID);

			RETURN 
			IF(
				ARRAY_LENGTH($new)<1, NULL, //no updated Values
				ARRAY(
					$recordID,
					//HAsh of col-value pair
					ARRAY_TO_HASH(
						$new, $(c, column) $column,
						$(c, column) CALL ($buildArray, HASH_GET($valueArray,$column), HASH_GET($newValues, $column), $targetLine)
					)
				)
			)

	);
RETURN
INDEXES_TO_KEYS(
	ARRAY_TRANSPOSE(
		ARRAY_WHERE($newValues, $(i,item) $item <> NULL)
	),
  ARRAY("records", "data")
 )

LET $pageLength := 20; 

LET $storedData := HASH_GET([Questionnaire Data], "values"); 

LET $thisPageQuestionData := 
ARRAY_SELECT(
  EACH [Question Display Data], $(i, data) 
  ARRAY_SELECT(
    HASH_KEYS($data), $(k, key) 
	IF( 
	  ARRAY_INDEXOF([Columns To Check], $key) > 0, 
	    CURRENT_VALUE($key, $i),
	  HASH_GET($data, $key)
	)
  )
);

LET $keysForQuestionsOnThisPage := 
ARRAY_SELECT(
  $thisPageQuestionData, $(i, item) 
  ARRAY_TAKE($item, 2)
);

LET $notOnThisPage := 
ARRAY_WHERE(
  $storedData, $(i, item) 
  LET $key := ARRAY_TAKE($item, 2); 
  RETURN ARRAY_INDEXOF($keysForQuestionsOnThisPage, $key) = NULL
); 

LET $columns := HASH_GET([Questionnaire Data], "columns"); 

//Re-sort the set back to the original order in Questionnaire Data
//"[Evaluated Party] ASC, [LineNumber] ASC, [Assessor Name] ASC"
LET $mergedAndSorted := 
ARRAY_SORT(
  ARRAY_CONCAT($thisPageQuestionData, $notOnThisPage), 
  $(item) 
  ARRAY(
	ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Evaluated Party")),
    ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "LineNumber")),
	ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Evaluated Party UUID"))
  )
);

RETURN 
$mergedAndSorted

FormulaCriterion.Formula:
TRUE/*LET $pageLength := 20; 

LET $storedData := HASH_GET([Questionnaire Data], "values"); 

LET $thisQuestionStoredData := 
ARRAY_WHERE(
