  ARRAY_INDEX($item, 2) = [Current Question Number]
); 

LET $thisPageStoredData := 
ARRAY_TAKE(
  ARRAY_SKIP($thisQuestionStoredData, GREATEST(([Page Number] - 1), 0) * $pageLength), 
  $pageLength
);

LET $thisPageQuestionData := 
ARRAY_SELECT(
  EACH [Question Display Data], $(i, data) 
  ARRAY_SELECT(
    HASH_KEYS($data), $(k, key) 
	HASH_GET($data, $key)
  )
);

RETURN 
$thisPageStoredData
