FieldEntry.Formula:
LET $columns := HASH_GET([Questionnaire Data], "columns");
LET $values := HASH_GET([Questionnaire Data], "values");

RETURN
ARRAY_TO_HASH_GROUPED(
  $values, 
  $(i, item) 
    ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Question GUID")),
  $(i, item) 
    INDEXES_TO_KEYS($item, $columns)
)

LET $columns := HASH_GET([Questionnaire Data], "columns");
LET $values := HASH_GET([Questionnaire Data], "values");

RETURN
ARRAY_TO_HASH_GROUPED(
  $values, 
  $(i, item) 
    ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Question GUID")),
  $(i, item) 
    INDEXES_TO_KEYS($item, $columns)
)

LET $allQuestionData := [Question Data]; 
LET $answerTypes := ARRAY("Freetext", "Multi line freetext", "Yes / No", "Date", "Dropdown");

LET $isQuestionAnswered := $(data)
(
  LET $answerProvided := 
  ARRAY_INDEXOF(
	ARRAY_SELECTMANY(
	$data, $(i, item)
      OR(
      //input provided
        NOT(ISBLANK(HASH_GET($item, "Answer"))), 
        NOT(ISBLANK(HASH_GET($item, "Answer (Bool)"))), 
        NOT(ISBLANK(HASH_GET($item, "Answer (Number)"))), 
        NOT(ISBLANK(HASH_GET($item, "Answer (Date)")))
      )
    ), 
	FALSE
  ) = NULL; 

  LET $expectingAnAnswer :=  
  ARRAY_INDEXOF(
    $answerTypes, 
    HASH_GET(ARRAY_FIRST($data), "Answer Type")
  ) > 0;

  LET $documentRequiredAndProvided := 
  IF( 
    //document required
    HASH_GET(ARRAY_FIRST($data), "Document Upload Mandatory") = TRUE, 

    //document uploaded
    HASH_GET(ARRAY_FIRST($data), "Document Attached to Reference") = TRUE
  );

  LET $answered := 
  AND(
	IF(
	  $expectingAnAnswer = TRUE,
	  $answerProvided
	), 
    $documentRequiredAndProvided
  );

  RETURN $answered
); 

LET $answered := 
ARRAY_SELECT(
  ARRAY_CONCAT("Summary", HASH_KEYS($allQuestionData)), $(k, key) 

  LET $questionInGroup := HASH_GET(ARRAY_FIRST([Current Question Data]), "Question Group");
  LET $data := HASH_GET($allQuestionData, $key); 
  LET $childQuestions := HASH_GET(HASH_GET([Question Structure], "Hierarchy"), $key);
  LET $questionType := HASH_GET($data, "Question Type");

  LET $questionAnswered := CALL($isQuestionAnswered, $data); 

  LET $isCategoryAndQuestionsAreAnswered := 
  AND(
	$questionType = "Category",
    //childQuestions 
	ARRAY_SELECT(
	  $childQuestions, $(i, key) 
	  LET $data := ARRAY_FIRST(HASH_GET($allQuestionData, $key));
      RETURN CALL($isQuestionAnswered, $data)
	)
  ); 

  LET $answered := 
  IF( 
    $questionType = "Category", 
	  $isCategoryAndQuestionsAreAnswered, 

	$questionAnswered = TRUE
  ); 

  RETURN 
  IF( 
	$answered = TRUE, 
	  $key
  )
); 

RETURN 
ARRAY_WHERE(
  $answered, $(i, item) 
  $item <> NULL
)

FormulaDrivenChangeFieldValues.FieldValuesFormula:
LET $typeMatch := 
OR(
  [Type] = "Evaluation", 
  [Type] = "Pre-Assessment"
);

LET $displayByKey  := 	IF($typeMatch = TRUE, "Evaluated Party UUID", "RecordID");
LET $displayByData := 	IF($typeMatch = TRUE, [Respondents], [Questionnaire RecordIDs]);

//LET $currentQuestionData := [Current Question Data];

LET $currentQuestionNumber := N(COALESCE(HASH_GET($eventargs, "Question Number"), 1));
LET $selectedQuestion := ARRAY_INDEX(HASH_KEYS([Questions]), $currentQuestionNumber);
LET $currentQuestionData := HASH_GET([Question Data], $selectedQuestion);

LET $pageNumber := [Page Number]; 
LET $pageLength := 20; 
LET $skipAmount := GREATEST(($pageNumber - 1), 0) * $pageLength;

//CONSENSUS VIEW
LET $displayIndexMultiEvaluator := 
ARRAY_SELECT(
	ARRAY_TAKE(ARRAY_SKIP($displayByData, $skipAmount), $pageLength), $(j, jitem)
	ARRAY_WHERE(
	  ARRAY_SELECT(
      	$currentQuestionData, $(i, item) 
      	IF(
		  HASH_GET($item, $displayByKey) = $jitem, 
		  $i
		)
  	  ), $(i,item) $item <> NULL
   )
); 

LET $getColumnDataMultiEvaluator := $(key)
ARRAY_SELECT( 
  $displayIndexMultiEvaluator, $(s, set) 
  ARRAY_SELECT(
    $set, $(i, index) 
	HASH_GET(
	  ARRAY_INDEX($currentQuestionData, $index), 
	  $key
	)
  )
);

LET $multiEvaluatorResult := 
(
  LET $data := 
  ARRAY(
     CALL($getColumnDataMultiEvaluator, "Assessor"),
	 CALL($getColumnDataMultiEvaluator, "Assessor Name"),
	 CALL($getColumnDataMultiEvaluator, "Answer"),
     CALL($getColumnDataMultiEvaluator, "Answer (Bool)"),
     CALL($getColumnDataMultiEvaluator, "Answer (Date)"),
     CALL($getColumnDataMultiEvaluator, "Answer (Number)"),
     CALL($getColumnDataMultiEvaluator, "Image Reference RecordID"),
     CALL($getColumnDataMultiEvaluator, "Document Reference RecordID"),
     CALL($getColumnDataMultiEvaluator, "Comments"),
     CALL($getColumnDataMultiEvaluator, "Answer Score Percentage Formatted")
  );

  LET $result :=
  ARRAY_SELECT(
    $displayIndexMultiEvaluator, $(r, respondent) 
    ARRAY_SELECT(
	  ARRAY_TRANSPOSE(
	    ARRAY_SELECT(
          $data, $(i, item) 
  	      ARRAY_INDEX($item, $r)
        )
	  ), $(i, item)
      INDEXES_TO_KEYS(
		$item,
		ARRAY(
          "Assessor",
          "Assessor Name",
          "Answer",
          "Answer (Bool)",
          "Answer (Date)",
          "Answer (Number)",
          "Image Reference RecordID",
          "Document Reference RecordID",
          "Comments",
          "Answer Score Percentage Formatted"
        )
	  )
	)
  );

  RETURN 
  HASH(
	"Question Display Index", RANGE($skipAmount + 1, LEAST(ARRAY_LENGTH($displayIndexMultiEvaluator), 20)),
    "Display ID", ARRAY_TAKE(ARRAY_SKIP($displayByData, $skipAmount), LEAST(ARRAY_LENGTH($displayIndexMultiEvaluator), 20)),
    //"Question Display Index", $displayIndex,
    "Answer Data", $result//,
    //"View", ARRAY($view)
  )
);

//Individual View
//faster than the old method, haven't yet converted the multi evaluator logic but it should look like this later
LET $individualEvaluatorResult :=  
INDEXES_TO_KEYS(
  ARRAY_TRANSPOSE(
    ARRAY_SELECT(
      ARRAY_TAKE(ARRAY_SKIP($currentQuestionData, $skipAmount), $pageLength), $(q, questionData)
      ARRAY(
		//$currentQuestionNumber,
		//$selectedQuestion, 
		//$currentQuestionData, 

		ARRAY_INDEX($displayByData, [Question Display Index]#$q), //Display ID, 
		[Question Display Index]#$q,

		HASH_GET($questionData, "Answer"), 
    	HASH_GET($questionData, "Answer (Bool)"), 
    	HASH_GET($questionData, "Answer (Date)"), 
    	HASH_GET($questionData, "Answer (Number)"), 
    	HASH_GET($questionData, "Image Reference RecordID"), 
    	HASH_GET($questionData, "Document Reference RecordID"), 
		HASH_GET($questionData, "Comments"), 

      )
    )
  ),
  ARRAY(
    //"Current Question Number",
    //"Selected Question",
    //"Current Question Data",

	"Display ID", 
	"Question Display Index",

	"Answer", 
    "Answer (Bool)", 
    "Answer (Date)", 
    "Answer (Number)", 
    "Image Reference RecordID", 
    "Document Reference RecordID", 
	"Comments"
  )
);

LET $result :=  
IF( 
  [Selected Evaluators] <> NULL, 
    $multiEvaluatorResult, 

  $individualEvaluatorResult
);

RETURN 
HASH_MERGE(
  $result,
  HASH("Current Question Data", ARRAY($currentQuestionData))
)

FieldEntry.Formula:
LET $data := 
ARRAY_TO_HASH_GROUPED(
  [Current Question Data], 
  $(i, item) HASH_GET($item, "Evaluated Party UUID"), 
  $(i, item) $item
); 

LET $data := 
ARRAY_SELECT(
  HASH_KEYS($data), $(k, key) 
  ARRAY_FIRST(HASH_GET($data, $key))
);

RETURN 
ARRAY_SELECT(
  EACH [Question Display Index], $(i, index) 
  ARRAY_INDEX($data, $index)
)
