Column.Formula:
IF( 
  AND(
	[Attachment Enabled] == TRUE, 
	[Document Reference RecordID] <> NULL, 
  ), 
  HASH_GET(
    HASH_GET([Documents Data], "Documents"), 
	[Document Reference RecordID]
  )
)

IF( 
  AND(
	[Image Enabled] == TRUE, 
	[Image Reference RecordID] <> NULL, 
  ), 
  HASH_GET(
    HASH_GET([Documents Data], "Images"), 
	[Image Reference RecordID]
  )
)

Column.ValidationFormula:
LET $maximumAttempts := 
DB_LOOKUP(
  ARRAY(
    "Maximum Number of Attempts", 
    "Maximum Attempts - Period Measure"
  ), 
  "Standard Questionnaire",
  "[RecordID] = " & DB_ENCODECONSTANT([Standard Questionnaire RecordID]) & " AND [Maximum Attempts Enforced] = TRUE AND [LineNumber] = 1"
);

LET $maxAttemptsMet := 
(
  LET $maxAttempts := $maximumAttempts;
  LET $maxAttempts := ARRAY_INDEX($maxAttempts, 1);
  RETURN AND([Participant Attempts] >= $maxAttempts, $maxAttempts > 0)
); 

LET $maxAttempts := ARRAY_INDEX($maxAttempts, 1);
LET $incomingMeasure := ARRAY_INDEX($maxAttempts, 2);

LET $measure := 
IF(
  $measure = "Day", "day", 
  $measure = "Week", "day", 
  $measure = "Month", "month", 
  $measure = "Year", "year", 
  "day"
);  

RETURN 
IF( 
  $maxAttemptsMet = TRUE, 
  "Maximum number of attempts of this questionnaire has been reached. You are allowed " & $maxAttempts & " attempt(s) per " & $measure
)

Column.Formula:
LET $getDocs := $(recordIDs, eFormType)
(
  LET $ids := 
  ARRAY_WHERE(
    $recordIDs, $(i,item) 
	$item <> NULL
  );

  LET $filter :=
  ARRAY_JOIN(
    ARRAY_SELECT(
  	  $ids, $(i,item) 
      "[RecordID] = "& DB_ENCODECONSTANT($item) 
    ), " OR "
  );

  LET $lookup := 
  IF( 
	$ids <> NULL, 
    DB_LOOKUPALL(
      "Document Attached", 
      $eFormType, 
      $filter
    )
  );

  LET $hashed := 
  INDEXES_TO_KEYS(
    $lookup,
    $ids
  );

  RETURN $hashed
);

//-----------

LET $images := 
IF( 
  ARRAY_INDEXOF(EACH [Image Enabled], TRUE) > 0, 
  CALL(
    $getDocs, 
    EACH [Image Reference RecordID], 
    "Image Upload"
  )
); 

LET $documents := 
IF( 
  ARRAY_INDEXOF(ARRAY_CONCAT(EACH [Guidance Document Provided], EACH [Attachment Enabled]), TRUE) > 0, 
  CALL(
    $getDocs, 
    ARRAY_CONCAT(EACH [Guidance Document Reference RecordID], EACH [Document Reference RecordID]), 
    "Document Upload"
  )
); 

RETURN 
HASH(
 "Documents", $documents,
 "Images", $images
)

Column.DefaultValueFormula:
LET $cuser:= EBMS_GET_CURRENT_USER();
LET $cuser_display:= EBMS_GET_ROLE_DISPLAYNAME($cuser);
LET $currentRoles:= EBMS_GET_ANCESTOR_ROLES($cuser);
LET $mapping:= 
INDEXES_TO_KEYS(
  ARRAY_SELECT($currentRoles, $(i, item) TRUE),
  $currentRoles
);

RETURN
HASH(
  	"Current User", $cuser,
  	"Current User Name", $cuser_display,
	"Mapping", $mapping
)

Binding.Expression:
LET $numberInRow := 1; 
LET $process := ARRAY_INDEX([Line Processes], $numberInRow);
RETURN NOT(HASH_GET([Process Accessible], $process))

LET $numberInRow := 1; 
LET $process := ARRAY_INDEX([Line Processes], $numberInRow);
LET $processData := HASH_GET([Process Data], $process);
LET $displayName := HASH_GET($processData, "Process Display Name");
RETURN $displayName

LET $numberInRow := 2; 
LET $process := ARRAY_INDEX([Line Processes], $numberInRow);
RETURN NOT(HASH_GET([Process Accessible], $process))

LET $numberInRow := 2; 
LET $process := ARRAY_INDEX([Line Processes], $numberInRow);
LET $processData := HASH_GET([Process Data], $process);
LET $displayName := HASH_GET($processData, "Process Display Name");
RETURN $displayName

LET $numberInRow := 3; 
LET $process := ARRAY_INDEX([Line Processes], $numberInRow);
RETURN NOT(HASH_GET([Process Accessible], $process))

LET $numberInRow := 3; 
LET $process := ARRAY_INDEX([Line Processes], $numberInRow);
LET $processData := HASH_GET([Process Data], $process);
LET $displayName := HASH_GET($processData, "Process Display Name");
RETURN $displayName

LET $numberInRow := [LineNumber]; 

LET $process := ARRAY_INDEX([Line Processes], $numberInRow); 
LET $accessible := HASH_GET([Process Accessible], $process);
LET $complete := HASH_GET([Process Completion Map], $process);
LET $selected := ARRAY_INDEXOF([View], $process) > 0;
LET $todo := AND($complete, $selected) <> TRUE;
LET $locked := $accessible <> TRUE; 

LET $iconToGet :=  
IF(
  AND($complete = TRUE, $locked = TRUE), "Locked - Complete", 
  $locked = TRUE, "Locked", 
  AND($complete = TRUE, $selected = TRUE), "Selected - Answered", 
  $complete = TRUE, "Answered", 
  $selected = TRUE, "Selected", 
  "To do"
);

RETURN HASH_GET([Icons], $iconToGet)

