RETURN HASH_GET([Icons], $iconToGet)

LET $mapping:=  HASH_GET([User to Group Mapping], "Mapping");
 RETURN
  AND(
	HASH_GET($mapping, "Administrators") <> TRUE,
	HASH_GET($mapping, "Questionnaire Admin") <> TRUE
	)

LET $mapping:=  HASH_GET([User to Group Mapping], "Mapping");
 RETURN
  AND(
	HASH_GET($mapping, "Administrators") <> TRUE,
	HASH_GET($mapping, "Questionnaire Admin") <> TRUE
	)

LET $mapping:=  HASH_GET([User to Group Mapping], "Mapping");
 RETURN
  AND(
	HASH_GET($mapping, "Administrators") <> TRUE,
	HASH_GET($mapping, "Questionnaire Admin") <> TRUE
	)

LET $maximumAttempts := 
DB_LOOKUP(
  ARRAY(
    "Maximum Number of Attempts", 
    "Maximum Attempts - Period Measure"
  ), 
  "Standard Questionnaire",
  "[RecordID] = " & DB_ENCODECONSTANT([Standard Questionnaire RecordID]) & " AND [Maximum Attempts Enforced] = TRUE AND [LineNumber] = 1"
);

LET $maxAttempts := ARRAY_INDEX($maxAttempts, 1);
LET $incomingMeasure := ARRAY_INDEX($maxAttempts, 2);

LET $measure := 
IF(
  $measure = "Day", "day", 
  $measure = "Week", "day", 
  $measure = "Month", "month", 
  $measure = "Year", "year", 
  "day"
);  

LET $number := 
IF( 
  $incomingMeasure = "Week", 
  1 * 7, 
  1
);

LET $fromDate := DATEADD($measure, $number * -1, NOW());

RETURN 
IF( 
  AND(
	[Standard Questionnaire RecordID] <> NULL,
	[Status] = "Not Started", 
	$maximumAttempts <> NULL	
  ),
  ARRAY_FIRST(ARRAY_FIRST(
    DB_REPORT(
    "COUNT([RecordID])",
    "Questionnaire", 
    "[Participant] = CURRENT_USER AND [LineNumber] = 1 AND [Standard Questionnaire RecordID] = " & DB_ENCODECONSTANT([Standard Questionnaire RecordID]) & " AND [Date Assessment Started] > " & DB_ENCODECONSTANT($fromDate)
  )
   ))
)

LET $maximumAttempts := 
DB_LOOKUP(
  ARRAY(
    "Maximum Number of Attempts", 
    "Maximum Attempts - Period Measure"
  ), 
  "Standard Questionnaire",
  "[RecordID] = " & DB_ENCODECONSTANT([Standard Questionnaire RecordID]) & " AND [Maximum Attempts Enforced] = TRUE AND [LineNumber] = 1"
);

LET $maxAttempts := ARRAY_INDEX($maxAttempts, 1);
LET $incomingMeasure := ARRAY_INDEX($maxAttempts, 2);

LET $measure := 
IF(
  $measure = "Day", "day", 
  $measure = "Week", "day", 
  $measure = "Month", "month", 
  $measure = "Year", "year", 
  "day"
);  

LET $number := 
IF( 
  $incomingMeasure = "Week", 
  1 * 7, 
  1
);

LET $fromDate := DATEADD($measure, $number * -1, NOW());

LET $participantAttempts := 
IF( 
  AND(
	[Standard Questionnaire RecordID] <> NULL,
	[Status] = "Not Started", 
	$maximumAttempts <> NULL	
  ),
  ARRAY_FIRST(ARRAY_FIRST(
    DB_REPORT(
    "COUNT([RecordID])",
    "Questionnaire", 
    "[Participant] = CURRENT_USER AND [LineNumber] = 1 AND [Standard Questionnaire RecordID] = " & DB_ENCODECONSTANT([Standard Questionnaire RecordID]) & " AND [Date Assessment Started] > " & DB_ENCODECONSTANT($fromDate)
  )
   ))
);

RETURN 
GREATEST(ARRAY_FIRST($maximumAttempts) - $participantAttempts, 0)

LET $values := IF( [Answer Type] = "Range", RANGE([Range - Lower Limit], [Range - Upper Limit] - [Range - Lower Limit] + 1),
[Options] );
RETURN COALESCE( $values, ARRAY() )

LET $style :=
"font-size: 10pt;
    height: 100%;
    overflow: auto;
    display: grid;
    margin: auto;
    align-items: center;
    text-align: left; ";
RETURN
"<div style=""" & $style & """>" & 
REPLACESUBSTRING(NEWLINE(), [Question], "<br>") & 
IF( 
  [Mandatory] = TRUE,
  "<span style='display: contents; color: red; font-size: 16pt;'>*</span>"
) &
"</div>"

LET $cat := 
IF(
  AND(
	[Type] = "Evaluation",
	[Question Category] = "Price"
  ),
  "COST",

  [Question Category] <> NULL(), 
  UPPER([Question Category])
);
LET $axis := IF([Scoring Axis] <> NULL(), UPPER([Scoring Axis]));
LET $combined := ARRAY_WHERE(ARRAY($cat, $axis), $(i, item) $item <> NULL()); 
LET $string := ARRAY_JOIN($combined, " - ");

RETURN "Question " & [Question Number] & ". " & IF($string <> NULL(), $string)

IF(
  [Image Reference RecordID]#[LineNumber] <> NULL, "View Images", "Upload Images" 
  )

FormulaDrivenChangeFieldValues.FieldValuesFormula:
LET $columns := ARRAY( "Question GUID", "Question", "Answer Type", "Options", "Option Scores", "Scoring Axis", "Question Category", "Mandatory", "Attachment Enabled", "Guidance Document Reference RecordID", "Document Upload Mandatory", "Range - Lower Limit", "Range - Upper Limit", "Answer Max Score", "Answer", "Comments", "Image Upload Unique Reference", "Answer (Date)", "Answer Result", "Document Upload Unique Reference", "Answer Score", "Answer (Bool)", "Image Reference RecordID", "Correct Answer", "Answer (Number)", "Document Reference RecordID", "Image Enabled", "Question Number", "Question Change Log", "Question Change Log Array", "Question Category UUID" );
