LET $thisQuestion := [LineNumber];
LET $filtered := ARRAY_WHERE( $allData, $(i, item) $i <> $thisQuestion );
RETURN INDEXES_TO_KEYS(ARRAY_TRANSPOSE($filtered), $columns)

