FormulaDrivenExcelFile.ExcelFileFormula:
LET $transformedAnswers := 
ARRAY_SELECT(
  EACH [Answer], $(i, item) 
  IF(
	[Answer Type]#$i = "Yes / No", 
	  IF(
		$item = "TRUE", "Yes",  
		$item = "FALSE", "No"
	  ), 
	$item
  )
); 



LET $data := 
ARRAY(
  ARRAY("#", RANGE(1, ARRAY_LENGTH(EACH [Question]))),
  ARRAY("Question", EACH [Question]),
  IF(EACH [Question Category] <> ARRAY(), ARRAY("Category", EACH [Question Category])),
  ARRAY("Answer Format", EACH [Answer Type]),
  ARRAY("Answer", $transformedAnswers),
  ARRAY("Comments", EACH [Comments])
); 

LET $data := ARRAY_WHERE($data, $(i, item) $item <> NULL()); 

LET $columns := ARRAY_SELECT($data, $(i, item) ARRAY_INDEX($item, 1));
LET $questionnaireData := ARRAY_SELECT($data, $(i, item) ARRAY_INDEX($item, 2));

RETURN
HASH(
	"sheets",
	    ARRAY(
		    HASH(
			    "name",  
			  LEFT(REPLACESUBSTRING(
				" ", 
				COALESCE(
				  [Standard Questionnaire Name], 
				  [Assessor Name] & " evaluation of " & [Evaluated Party]
			    ), 
				""
			  ), 30),
			    "content", 
				ARRAY_CONCAT(
					ARRAY($columns),
					ARRAY_TRANSPOSE(
						$questionnaireData
					)
				)
			)
		)
	)

FormulaDrivenExcelFile.FilenameTemplate:
{
URLENCODE(
  COALESCE(
	[Standard Questionnaire Name], 
	[Assessor Name] & " evaluation of " & [Evaluated Party]
  )
)
}.xlsx

FieldEntry.Formula:
LET $keys := ARRAY_FIRST(ARRAY_TAKE([Import], 1));
LET $data := ARRAY_TRANSPOSE(ARRAY_SKIP([Import], 1));
LET $hashed := INDEXES_TO_KEYS($data, $keys);
RETURN $hashed

LET $answers := HASH_GET([Import], "Answer");

//validate against allow answers 
LET $questions := EACH [Question]; 
LET $validOptions := EACH [Options]; 
LET $answerType := EACH [Answer Type]; 

LET $validAnswers := 
ARRAY_SELECT(
  $questions, $(i, question)
  LET $answer := ARRAY_INDEX($answers, $i); 
  LET $responseType := ARRAY_INDEX($answerType, $i); 
  LET $responseOptions := ARRAY_INDEX($validOptions, $i); 

  RETURN 
  IF( 
	OR(
	  $responseType = "Freetext", 
	  $responseType = "Multi line freetext"
	),
	$answer,

	OR(
	  $responseType = "Image",
	  $responseType = NULL
	), 
	NULL, 

	AND(
	  $responseOptions <> NULL,
	  ARRAY_INDEXOF($responseOptions, $answer) > 0, $answer
	), 
	$answer  
  )
);

RETURN $validAnswers

LET $answers := HASH_GET([Import], "Answer");

//validate against allow answers 
LET $questions := EACH [Question]; 
LET $validOptions := EACH [Options]; 
LET $answerType := EACH [Answer Type]; 

LET $validAnswers := 
ARRAY_SELECT(
  $questions, $(i, question)
  LET $answer := ARRAY_INDEX($answers, $i); 
  LET $responseType := ARRAY_INDEX($answerType, $i); 
  LET $responseOptions := ARRAY_INDEX($validOptions, $i); 

  RETURN 
  IF( 
	AND(
	  $responseType = "Date", 
	  OR(
		ISERROR(D($answer)) <> TRUE, 
		ISDATETIME($answer)
	  )
	), 
	$answer	  
  )
);

RETURN $validAnswers

LET $answers := HASH_GET([Import], "Answer");

//validate against allow answers 
LET $questions := EACH [Question]; 
LET $validOptions := EACH [Options]; 
LET $answerType := EACH [Answer Type]; 

LET $validAnswers := 
ARRAY_SELECT(
  $questions, $(i, question)
  LET $answer := ARRAY_INDEX($answers, $i); 
  LET $responseType := ARRAY_INDEX($answerType, $i); 
  LET $responseOptions := ARRAY_INDEX($validOptions, $i); 

  RETURN 
  IF( 
	AND(
	  $responseType = "Yes / No", 
		OR(
		  LOWER($answer) = "yes", 
		  $answer = TRUE,
		  LOWER($answer) = "true"
		)
	), 
	TRUE, 

	AND(
	  $responseType = "Yes / No", 
		OR(
		  LOWER($answer) = "no", 
		  $answer = FALSE,
		  LOWER($answer) = "false"
		)
	), 
	FALSE
  )
);

RETURN $validAnswers

LET $answers := HASH_GET([Import], "Answer");

//validate against allow answers 
