  $questions, $(i, question)
  LET $answer := ARRAY_INDEX($answers, $i); 
  LET $responseType := ARRAY_INDEX($answerType, $i); 
  LET $responseOptions := ARRAY_INDEX($validOptions, $i); 

  RETURN 
  IF( 
	AND(
	  $responseType = "Number", 
	  ISNUMBER($answer)
    ),
	$answer
  )
);

RETURN $validAnswers

