LET $indicatorHeight := 45; 
LET $indicatorWidth := 45; 

LET $width := 670;
LET $height := 313;

LET $maxYAxisScore := [Max Score - Axis 2];
LET $maxXAxisScore := [Max Score];

LET $yAxisScore := [Axis 2 Score];
LET $xAxisScore := [Axis 1 Score];

LET $percentageYScore := IFERROR(($maxYAxisScore - $yAxisScore) / $maxYAxisScore, 0);
LET $percentageXScore := IFERROR($xAxisScore / $maxXAxisScore, 0);

LET $heightRescaled := $height - $indicatorHeight;
LET $widthRescaled := $width - $indicatorWidth;

LET $top := $heightRescaled * $percentageYScore;
LET $left := $widthRescaled * $percentageXScore;

RETURN
"<table id=""q-graph"">
 <tbody>
   <tr>
    <td><div class=""segmentHeading"">" & ARRAY_INDEX([Quadrant Names], 1) & "</div></td>
    <td><div class=""segmentHeading"">" & ARRAY_INDEX([Quadrant Names], 2) & "</div></td>
  </tr>
  <tr>
    <td><div class=""segmentHeading"">" & ARRAY_INDEX([Quadrant Names], 3) & "</div></td>
    <td><div class=""segmentHeading"">" & ARRAY_INDEX([Quadrant Names], 4) & "</div></td>
  </tr>
  <td class=""result"" style=""top: " & COALESCE($top, "147") & "px; left: " & COALESCE($left, 298) & "px""></td>
</tbody>
</table>
<style>
