FieldEntry.Formula:
LET $line := HASH_GET($eventargs, "LineNumber"); 

LET $boolOptions := 
ARRAY_SELECT( 
  [Options], $(i, item) 
  IF(
    OR(
	  LOWER($item) = "yes", 
	  LOWER($item) = "true"
	), TRUE, 
    OR(
	  LOWER($item) = "no", 
	  LOWER($item) = "false"
	), FALSE
  )
);

LET $targetIndex := 
IF( 
  [Answer Type]#$line = "Yes / No", 
  ARRAY_INDEXOF($boolOptions, [Answer (Bool)]#$line),
  ARRAY_INDEXOF([Options]#$line, [Answer]#$line)
);

RETURN COALESCE(ARRAY_INDEX([Option Scores]#$line, $targetIndex), 0)

ARRAY_LENGTH(
  ARRAY_WHERE(
	EACH [Answer (Bool)], $(i, item) $item = TRUE
  )
) & "/" &  ARRAY_LENGTH(EACH [Question])

LET $parentCheck := 
IF(
  [Linked Entity EFormType] = "Evaluation", 
  HASH("parent", ARRAY([Linked Entity EFormType], [Linked Entity RecordID]))
);

LET $questions := EACH [Question GUID]; 

RETURN
ARRAY_SELECT(
  $questions, $(i, item) 

  LET $answer := 
  COALESCE(
    [Answer (Bool)]#$i, 
	[Answer (Date)]#$i, 
	[Answer (Number)]#$i,
	[Answer]#$i
  );

  LET $filter := 
  "[Question GUID] = " & DB_ENCODECONSTANT($item) &
  IF( 
    [Standard Questionnaire RecordID] <> NULL, 
    " AND [Standard Questionnaire RecordID] = " & DB_ENCODECONSTANT([Standard Questionnaire RecordID])
  );

  LET $answer := 
  IF(
    $answer = TRUE, "Yes", 
    $answer = FALSE, "No", 
    $answer = "TRUE", "Yes", 
    $answer = "FALSE", "No", 
    $answer
  ); 

  LET $scoreLookup := 
  DB_LOOKUP(
	"Score", 
    "Standard Question", 
	"[Answer] = " & DB_ENCODECONSTANT($answer) & " AND " & $filter, 
    IF( 
      $parentCheck <> NULL, 
      $parentCheck
    )
  );

  LET $optionScore := 
  ARRAY_INDEX(
    [Option Scores]#$i, 
    ARRAY_INDEXOF([Options]#$i, $answer)
  ); 

  RETURN 
  COALESCE($optionScore, $scoreLookup, 0)
)

LET $parentCheck := 
IF(
  [Linked Entity EFormType] = "Evaluation", 
  HASH("parent", ARRAY([Linked Entity EFormType], [Linked Entity RecordID]))
);

LET $questions := EACH [Question GUID]; 

LET $result := 
ARRAY_SELECT(
  $questions, $(i, item) 

  LET $filter := 
    "[Question GUID] = " & DB_ENCODECONSTANT($item) &
    IF( 
      [Standard Questionnaire RecordID] <> NULL, 
      " AND [Standard Questionnaire RecordID] = " & DB_ENCODECONSTANT([Standard Questionnaire RecordID])
    );

  RETURN
  DB_LOOKUP(
	"Answer", 
    "Standard Question", 
	$filter, 
	HASH_MERGE(
	  HASH(
	    "sort", "[Score] DESC", 
		$parentCheck
	  )	  
	)
  )
);

RETURN $result

LET $scores := 
ARRAY_TRANSPOSE(
  ARRAY(
    EACH [Score],
    EACH [Scoring Axis]
  )
); 

LET $score := SUM(ARRAY_INDEX(ARRAY_TRANSPOSE(ARRAY_WHERE($scores, $(i, item) ARRAY_INDEX($item, 2) = ARRAY_INDEX([Axes], 1))), 1));

RETURN $score

LET $scores := 
ARRAY_TRANSPOSE(
  ARRAY(
    EACH [Score],
    EACH [Scoring Axis]
  )
); 

LET $score := SUM(ARRAY_INDEX(ARRAY_TRANSPOSE(ARRAY_WHERE($scores, $(i, item) ARRAY_INDEX($item, 2) = ARRAY_INDEX([Axes], 2))), 1));

RETURN $score

LET $scores := 
ARRAY_TRANSPOSE(
  ARRAY(
    EACH [Score],
    EACH [Scoring Axis]
  )
); 

LET $score := SUM(ARRAY_INDEX(ARRAY_TRANSPOSE($scores), 1));

RETURN $score

LET $singleAxisScore := 
(
  LET $score := [Total Score];

  LET $filter := 
	  $score & " >= [Lower Score] AND " &
	  "(" & $score & " <= [Upper Score] OR [Upper Score] IS EMPTY" & ")";

  LET $result := 
  IF(
	[Standard Questionnaire RecordID] <> NULL, 
    DB_LOOKUP(
	  "Result", 
	  "Standard Questionnaire", 
	  "[RecordID] = " & [Standard Questionnaire RecordID] & " AND " & 
	  $filter
    )
  );

  RETURN $result
);

LET $dualAxisScore := 
(
  LET $axisOneScore := DB_ENCODECONSTANT([Axis 1 Score]);
  LET $axisTwoScore := DB_ENCODECONSTANT([Axis 2 Score]);

  LET $filter := 
  "(" &
  $axisOneScore & " >= [Lower Score] AND " &
  "(" & $axisOneScore & " <= [Upper Score] OR [Upper Score] IS EMPTY" & ")" & 
  ")" & 
  " AND " &
  "(" &
  $axisTwoScore & " >= [Lower Score - Axis 2] AND " &
  "(" & $axisTwoScore & " <= [Upper Score - Axis 2] OR [Upper Score - Axis 2] IS EMPTY" & ")" &
  ")";

  LET $result := 
  IF(
	[Standard Questionnaire RecordID] <> NULL, 
	DB_LOOKUP(
	  "Result", 
	  "Standard Questionnaire", 
	  "[RecordID] = " & [Standard Questionnaire RecordID] & " AND " & 
	  $filter
	)
  );

  RETURN $result
);

RETURN 
IF(
  ARRAY_LENGTH([Axes]) > 1, 
  $dualAxisScore, 
  $singleAxisScore
)

LET $singleAxisScore := 
(
  LET $score := [Total Score];

  LET $filter := 
	  $score & " >= [Lower Score] AND " &
	  "(" & $score & " <= [Upper Score] OR [Upper Score] IS EMPTY" & ")";

  LET $result := 
  IF(
	[Standard Questionnaire RecordID] <> NULL, 
	DB_LOOKUP(
	  "Result Description", 
	  "Standard Questionnaire", 
	  "[RecordID] = " & [Standard Questionnaire RecordID] & " AND " & 
	  $filter
    )
  );

  RETURN $result
);

LET $dualAxisScore := 
(
  LET $axisOneScore := DB_ENCODECONSTANT([Axis 1 Score]);
  LET $axisTwoScore := DB_ENCODECONSTANT([Axis 2 Score]);

  LET $filter := 
  "(" &
  $axisOneScore & " >= [Lower Score] AND " &
  "(" & $axisOneScore & " <= [Upper Score] OR [Upper Score] IS EMPTY" & ")" & 
  ")" & 
  " AND " &
  "(" &
  $axisTwoScore & " >= [Lower Score - Axis 2] AND " &
  "(" & $axisTwoScore & " <= [Upper Score - Axis 2] OR [Upper Score - Axis 2] IS EMPTY" & ")" &
  ")";

  LET $result := 
  DB_LOOKUP(
	"Result Description", 
	"Standard Questionnaire", 
	"[RecordID] = " & [Standard Questionnaire RecordID] & " AND " & 
	$filter
  );

  RETURN $result
);

RETURN 
IF(
  ARRAY_LENGTH([Axes]) > 1, 
  $dualAxisScore, 
  $singleAxisScore
)

LET $cats := EACH [Question Category];
LET $scores := EACH [Score];

LET $set := ARRAY_TRANSPOSE(ARRAY($cats, $scores)); 

RETURN 
ARRAY_SELECT(
  EACH [Category Name], $(i, cat) 
  SUM(ARRAY_WHERE($set, $(i, item) ARRAY_INDEX($item, 1) = $cat))
)

LET $data := 
ARRAY_TRANSPOSE(
  ARRAY(
    EACH [Category Score], 
    EACH [Category Max Score]
  )
);

RETURN 
ARRAY_SELECT(
  $data, $(i, item) 
  IFERROR(ARRAY_INDEX($item, 1) / ARRAY_INDEX($item, 2) , NULL())
)

FormulaCriterion.Formula:
LET $data := DB_REPORT( "[RecordID]", "Questionnaire", "[Type] = 'Evaluation'" & " AND [Linked Entity RecordID] = " & DB_ENCODECONSTANT([Linked Entity RecordID]) & " AND [Linked Entity EFormType] = " & DB_ENCODECONSTANT([Linked Entity EFormType]) & " AND [LineNumber] = 1" & " AND [RecordID] <> " & DB_ENCODECONSTANT([RecordID]) & " AND [Status] <> 'Complete'" & " AND [Status] <> 'Deleted'" );
LET $resultCount := ARRAY_LENGTH($data) = 0;
RETURN $resultCount

FieldEntry.Formula:
IF(
  //priortise information passed in
  NOT(ISNULL(HASH_GET($eventargs, "Team Questionnaire Data"))),
	HASH_GET($eventargs, "Team Questionnaire Data"),

  //if nothing provided then lookup
  HASH_GET(HASH_GET(
    DB_LOOKUP(
      "Team Questionnaire Data", 
      [Linked Entity EFormType], 
      HASH("RecordID", [Linked Entity RecordID])
    ), 
    [Team]), "Question List"
  )
)

AttachFiles.TargetRecordsFormula:
ARRAY(
  ARRAY(
    [Linked Entity EFormType], 
    [Linked Entity RecordID]
  )
)

FieldEntry.Formula:
LET $values:= ARRAY_TRANSPOSE(ARRAY( EACH [Question GUID], EACH [Answer], EACH [Answer (Date)], EACH [Answer (Bool)], EACH [Answer (Number)], EACH [Comments], EACH [Image Upload Unique Reference], EACH [Document Upload Unique Reference], ));
LET $values:= ARRAY_WHERE($values, $(i,item) ARRAY_INDEX($item, 1) <> NULL);
LET $colKeys:= ARRAY( "Answer", "Answer (Date)", "Answer (Bool)", "Answer (Number)", "Comments", "Image Upload Unique Reference", "Document Upload Unique Reference" );
RETURN ARRAY_TO_HASH( $values, $(i,item) ARRAY_INDEX($item, 1), //guid $(i,item) INDEXES_TO_KEYS( ARRAY_SKIP($item, 1), $colKeys) )

LET $columns := HASH_KEYS([Multiline Question Fields]);
LET $GUIDColumn := ARRAY_INDEXOF($columns, "Question GUID"); 
LET $questionColumn := ARRAY_INDEXOF($columns, "Question"); 
LET $categoryIndex := ARRAY_INDEXOF($columns, "Question Category"); 

LET $onlyRefreshUnansweredQuestions := [Scoring Type] = "Supplementary Data";
LET $preserveDeletedQuestions := [Scoring Type] = "Supplementary Data";

LET $answeredQuestions := 
ARRAY_FIRST(
ARRAY_TRANSPOSE(
  ARRAY_WHERE(
    ARRAY_TRANSPOSE(
      ARRAY(
        EACH [Question GUID],
        EACH [Answer]
      )
    ), $(i,item) 
    ARRAY_INDEX($item, 2) <> NULL
  )
)); 

LET $sortByCategory := 
DB_LOOKUP(
  "Sort by Category", 
  "Standard Questionnaire", 
  "[RecordID] = " & DB_ENCODECONSTANT([Standard Questionnaire RecordID]),
  HASH("credentials", "48a701c6-b97f-481c-9354-982ff6529f67")
);

LET $filter := 
IF(
	$preserveDeletedQuestions = TRUE, 
	"[Question GUID] IN " & DB_ENCODECONSTANT(EACH [Question GUID]) & " OR "
 ) & 
"[Standard Questionnaire RecordID] = " & DB_ENCODECONSTANT([Standard Questionnaire RecordID]);

LET $data := 
DB_LOOKUPALL(
  $columns, 
  "Standard Question", 
  $filter,
  HASH("sort", "[RecordID] ASC, [LineNumber] ASC", "credentials", "48a701c6-b97f-481c-9354-982ff6529f67") 
);

LET $data := 
IF( 
  $onlyRefreshUnansweredQuestions = TRUE, 
  (
	LET $unansweredQuestionsUpdatedData := 
	ARRAY_WHERE(
      $data, $(i, item) 
	  ARRAY_INDEXOF($answeredQuestions, ARRAY_INDEX($item, $GUIDColumn)) = NULL
    );

    LET $fields :=
    ARRAY_SELECT(
      HASH_KEYS([Multiline Question Fields]), $(i, item) 
      HASH_GET([Multiline Question Fields], $item) 
    );

    LET $currentQuestionData :=  
    ARRAY_TRANSPOSE(
      ARRAY_SELECT(
        $fields, $(f, field) 
        EVALUATE("EACH [" & $field & "]")
      )
    );

    LET $answeredQuestionsExistingData := 
    ARRAY_WHERE(
      $currentQuestionData, $(i, item) 
      ARRAY_INDEXOF($answeredQuestions, ARRAY_INDEX($item, $GUIDColumn)) > 0
    );

	RETURN 
	ARRAY_CONCAT(
	  $unansweredQuestionsUpdatedData, 
	  $answeredQuestionsExistingData
	)
  ),
  $data
); 

LET $data := 
ARRAY_SORT(
  ARRAY_SORT(
    $data, $(item) 
    ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Question Sort ID"))
  ), $(item) 
  ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Group Sort ID"))
);

LET $sortOnCategories := $(data)
(
  LET $categories := 
  DB_LOOKUPALL(
	  "Question Category", 
	  "Standard Questionnaire", 
	  "[RecordID] = " & DB_ENCODECONSTANT([Standard Questionnaire RecordID]),
  HASH("credentials", "48a701c6-b97f-481c-9354-982ff6529f67")
  );

  LET $addSortID := 
  ARRAY_SELECT(
	$data, $(i, item) 
	LET $questionCategory := ARRAY_INDEX($item, $categoryIndex); 
	LET $sortID := ARRAY_INDEXOF($categories, $questionCategory); 
	RETURN 
	ARRAY_CONCAT(
	  $item, 
	  COALESCE($sortID, 100)
	)
  );

  LET $result := ARRAY_SORT($addSortID, $(item) ARRAY_LAST($item));
  LET $result := ARRAY_SELECT($result, $(i, item) ARRAY_TAKE($item, ARRAY_LENGTH($item) - 1));

  RETURN $result
);

LET $data := 
IF(
  $sortByCategory = TRUE, 
  CALL($sortOnCategories, $data), $data
);

LET $distinctQuestions := 
ARRAY_DISTINCT(
	ARRAY_TRANSPOSE(
	  ARRAY(
		  ARRAY_INDEX(ARRAY_TRANSPOSE($data), $questionColumn),
		  ARRAY_INDEX(ARRAY_TRANSPOSE($data), $GUIDColumn)
	  )
    )
);

LET $questions := ARRAY_FIRST(ARRAY_TRANSPOSE($distinctQuestions));

LET $transformed :=
ARRAY_SELECT(
	$data, $(i, item)

	LET $question := ARRAY_INDEX($item, 1);
	LET $guid := ARRAY_INDEX($item, $GUIDColumn);

	LET $keys := ARRAY_CONCAT(ARRAY_SKIP($columns, 1), ARRAY("Question Number")); //all column names with question number appended
	LET $values := ARRAY_SKIP($item, 1); //contains the results, skips the question itself as that is the key
	LET $values := ARRAY_CONCAT($values, ARRAY(ARRAY_INDEXOF($questions, $question))); //adds question number into list

	LET $hashData :=
	HASH_MERGE(
	  INDEXES_TO_KEYS(
		$values, 
		$keys
	  )
	);

	RETURN 
	ARRAY(
		ARRAY($question, $guid),
		$hashData
	)
);

LET $result :=  
ARRAY_TO_HASH_GROUPED(
	$transformed, 
	$(i, item) ARRAY_INDEX($item, 1), 
	$(i, item) ARRAY_INDEX($item, 2)
);

RETURN $result

LET $columns := HASH_KEYS([Header Questionnaire Fields]);

LET $data := 
DB_LOOKUP(
	$columns, 
	"Standard Questionnaire", 
	"[RecordID] = " & DB_ENCODECONSTANT([Standard Questionnaire RecordID]),
	HASH("sort", "[LineNumber] ASC", "credentials", "48a701c6-b97f-481c-9354-982ff6529f67")
);

LET $hashData :=
INDEXES_TO_KEYS(
	$data, 
	$columns
);

LET $result :=  
ARRAY_TO_HASH_GROUPED(
	$hashData, 
	$(i, item) ARRAY_INDEX($item, 1), 
	$(i, item) ARRAY_INDEX($item, 2)
);

RETURN $result

FormulaDrivenChangeFieldValues.FieldValuesFormula:
LET $values := 
ARRAY_SELECT(
  HASH_KEYS([Questionnaire Data]), $(i,item) 
  HASH_GET([Questionnaire Data], $item)
);

LET $columns := 
ARRAY_SELECT(
  HASH_KEYS([Header Questionnaire Fields]), $(i,item) 
  HASH_GET([Header Questionnaire Fields], $item)
);

RETURN 
INDEXES_TO_KEYS($values, $columns)

LET $storageTypes := 
(
  LET $set := 
  ARRAY_SELECT(
    HASH_KEYS([Multiline Question Fields]), $(i, item) 
    LET $type := 
    IF(
  	OR(
  	  $item = "Answer",  
  	  $item = "Score"
  	), 
  	"Array"
    );
    RETURN 
    ARRAY($item, $type)
  );

  LET $transposed := ARRAY_TRANSPOSE($set);

  RETURN
  INDEXES_TO_KEYS(ARRAY_LAST($transposed), ARRAY_FIRST($transposed))
); 

LET $fields := ARRAY_CONCAT(HASH_KEYS([Multiline Question Fields]), ARRAY("Question Number"));

LET $targets := 
ARRAY_WHERE(
	$fields, 
	$(i, item) 
	$item <> "Question"
);

LET $questions := HASH_KEYS([Question Data]);

LET $extractData := $(target)
(
	ARRAY_SELECT(
		$questions, $(k, key) 

		LET $set := HASH_GET([Question Data], $key);

		LET $arrayWrapped :=  
		ARRAY_SELECT(
			$set, $(i, item)
			HASH_GET($item, $target)
		);

		LET $result :=  
		IF(
		    ARRAY_FIRST(ARRAY_FIRST($arrayWrapped)) <> ARRAY_FIRST($arrayWrapped), 
			  ARRAY_FIRST($arrayWrapped),

		    HASH_GET($storageTypes, $target) = "Array", 
		      $arrayWrapped, 

		    ARRAY_FIRST($arrayWrapped)
		);

		RETURN $result
	)
);

LET $results :=  
ARRAY_SELECT(
	$targets, $(i, item) 
	CALL($extractData, $item)
);

LET $popFields := 
ARRAY_SELECT(
	$fields, $(i, key) 
	IF( 
		$i = ARRAY_LENGTH($fields), 
			"Question Number", 

		HASH_GET([Multiline Question Fields], $key)
	)
); 

LET $result :=  
INDEXES_TO_KEYS(
	$results, 
	ARRAY_SKIP($popFields, 1)
);

RETURN $result

