IF( [Type] = "Evaluation", ( LET $columns := ARRAY( "Panel Member Team UUID", "Panel Member Team", "Team UUID", "Team Name", "Evaluate Using Teams" );
LET $data := DB_LOOKUP( $columns, "Evaluation", "[RecordID] FAST_EQUAL_TO " & DB_ENCODECONSTANT([Linked Entity RecordID]) & " AND [Panel Member Username] = " & DB_ENCODECONSTANT([Assessor]) );
RETURN INDEXES_TO_KEYS($data, $columns) ) )

LET $userTeam := [Team]; 
LET $targetRecordID := [Linked Entity RecordID];

RETURN
IF( 
  [Type] = "Pre-Assessment", 
  (
      LET $criteriaUUIDs := 
      DB_LOOKUPALL(
        "Gateway Criteria UUID", 
        "Evaluation", 
		"[RecordID] FAST_EQUAL_TO " & DB_ENCODECONSTANT($targetRecordID)
      );

      LET $criteriaUUIDs := 
      IF( 
        $priceCriteriaUUID <> NULL, 
        ARRAY_CONCAT($priceCriteriaUUID, $criteriaUUIDs),
        $criteriaUUIDs
      );

      //non-price questions
      LET $questionIDs :=  
      DB_LOOKUPALL(
        "RecordID", 
        "Standard Question", 
        "[Unique Reference] IN " & DB_ENCODECONSTANT($criteriaUUIDs) & " AND [LineNumber] FAST_EQUAL_TO 1 AND [Status] <> 'Deleted'",   
        HASH("parent", ARRAY("Evaluation", $targetRecordID), "sort", "[RecordID] ASC")
      );

      RETURN $questionIDs
  ),

  [Type] = "Evaluation", 
  IF(
    //priortise information passed in
    NOT(ISNULL(HASH_GET($eventargs, "Team Questionnaire Data"))),
  	HASH_GET($eventargs, "Team Questionnaire Data"),

    //if nothing provided then lookup
    (


	  //price criteria UUID
      LET $priceCriteriaUUID := 
      DB_LOOKUP(
        "Pricing Criteria UUID", 
        "Evaluation", 
        "(" & 
		  DB_ENCODECONSTANT([Team UUID]) & " IN ARRAY ([Evaluating Teams - Price]) OR " & 
		  DB_ENCODECONSTANT([Team]) & " IN ARRAY ([Evaluating Teams - Price])
		)		
		AND [Price Scoring Method] = 'Manual' AND [RecordID] FAST_EQUAL_TO " & DB_ENCODECONSTANT($targetRecordID)
      );

      //non-price criteria
      LET $criteriaUUIDs := 
      DB_LOOKUPALL(
        "Criteria UUID", 
        "Evaluation", 
        "(" & 
		  DB_ENCODECONSTANT([Team UUID]) & " IN ARRAY ([Evaluating Teams]) OR " & 
		  DB_ENCODECONSTANT([Team]) & " IN ARRAY ([Evaluating Teams])
		)
		AND [RecordID] FAST_EQUAL_TO " & DB_ENCODECONSTANT($targetRecordID)
      );

      LET $criteriaUUIDs := 
      IF( 
        $priceCriteriaUUID <> NULL, 
        ARRAY_CONCAT($priceCriteriaUUID, $criteriaUUIDs),
        $criteriaUUIDs
      );

      //non-price questions
      LET $questionIDs :=  
      DB_LOOKUPALL(
        "RecordID", 
        "Standard Question", 
        "[Unique Reference] IN " & DB_ENCODECONSTANT($criteriaUUIDs) & " AND [LineNumber] FAST_EQUAL_TO 1 AND [Status] <> 'Deleted'",   
        HASH("parent", ARRAY("Evaluation", $targetRecordID), "sort", "[RecordID] ASC")
      );

      RETURN $questionIDs
	)

  )
)

LET $values:= ARRAY_TRANSPOSE(ARRAY( EACH [Question GUID], EACH [Answer], EACH [Answer (Date)], EACH [Answer (Bool)], EACH [Answer (Number)], EACH [Comments], EACH [Image Upload Unique Reference], EACH [Document Upload Unique Reference], ));
LET $values:= ARRAY_WHERE($values, $(i,item) ARRAY_INDEX($item, 1) <> NULL);
LET $colKeys:= ARRAY( "Answer", "Answer (Date)", "Answer (Bool)", "Answer (Number)", "Comments", "Image Upload Unique Reference", "Document Upload Unique Reference" );
RETURN ARRAY_TO_HASH( $values, $(i,item) ARRAY_INDEX($item, 1), //guid $(i,item) INDEXES_TO_KEYS( ARRAY_SKIP($item, 1), $colKeys) )

LET $columns := HASH_KEYS([Multiline Question Fields]);
LET $GUIDColumn := ARRAY_INDEXOF($columns, "Question GUID"); 
LET $questionColumn := ARRAY_INDEXOF($columns, "Question"); 
LET $categoryIndex := ARRAY_INDEXOF($columns, "Question Category"); 

LET $sortByCategory := TRUE;

LET $questionFilter := 
IF( 
  OR(
	[Type] = "Evaluation", 
	[Type] = "Pre-Assessment"
  ),
  "[RecordID] IN " & DB_ENCODECONSTANT([Override Question List])
);

LET $data := 
DB_LOOKUPALL(
	$columns, 
	"Standard Question", 

	  IF( 
		[Type] = "Pre-Assessment", 
		  "[Type] = 'Gateway Criteria'",
		[Type] = "Evaluation", 
	      "[Type] = 'Evaluation Criteria'", 
		"1 = 1"
	  ) & 
    " AND [Status] <> 'Deleted' AND [Linked Entity RecordID] = " & DB_ENCODECONSTANT([Linked Entity RecordID]) & 
	" AND [Linked Entity EFormType] = " & DB_ENCODECONSTANT([Linked Entity EFormType]) 
    & IF($questionFilter <> NULL, " AND " & $questionFilter),

  	HASH("parent", ARRAY([Linked Entity EFormType], [Linked Entity RecordID]), "sort", "[RecordID] ASC, [LineNumber] ASC", "credentials", "48a701c6-b97f-481c-9354-982ff6529f67") 
);

LET $sortOnCategories := $(data)
(
  LET $categories := 
  ARRAY_DISTINCT(
    ARRAY_SELECT(
      $data, $(i ,item) 
      ARRAY_INDEX($item, ARRAY_INDEXOF($columns, "Question Category"))
    )
  );

  LET $addSortID := 
  ARRAY_SELECT(
	$data, $(i, item) 
	LET $questionCategory := ARRAY_INDEX($item, $categoryIndex); 
	LET $sortID := ARRAY_INDEXOF($categories, $questionCategory); 
	RETURN 
	ARRAY_CONCAT(
	  $item, 
	  COALESCE($sortID, 100)
	)
  );

  LET $result := ARRAY_SORT($addSortID, $(item) ARRAY_LAST($item));
  LET $result := ARRAY_SELECT($result, $(i, item) ARRAY_TAKE($item, ARRAY_LENGTH($item) - 1));

  RETURN $result
);

LET $data := IF($sortByCategory = TRUE, CALL($sortOnCategories, $data), $data);

LET $distinctQuestions := 
ARRAY_DISTINCT(
	ARRAY_TRANSPOSE(
	  ARRAY(
		  ARRAY_INDEX(ARRAY_TRANSPOSE($data), $questionColumn),
		  ARRAY_INDEX(ARRAY_TRANSPOSE($data), $GUIDColumn)
	  )
    )
);

//LET $questions := ARRAY_FIRST(ARRAY_TRANSPOSE($distinctQuestions));
LET $guids := ARRAY_INDEX(ARRAY_TRANSPOSE($distinctQuestions), 2);

LET $transformed :=
ARRAY_SELECT(
	$data, $(i, item)

	LET $question := ARRAY_INDEX($item, 1);
	LET $guid := ARRAY_INDEX($item, $GUIDColumn);

	LET $keys := ARRAY_CONCAT(ARRAY_SKIP($columns, 1), ARRAY("Question Number")); //all column names with question number appended
	LET $values := ARRAY_SKIP($item, 1); //contains the results, skips the question itself as that is the key
	LET $values := ARRAY_CONCAT($values, ARRAY(ARRAY_INDEXOF($guids, $guid)) ); //adds question number into list

    LET $answer := HASH("Answer", COALESCE(ARRAY_FIRST(HASH_GET($existingAnswerMapping, $guid)), NULL));
    LET $score := HASH("Score", COALESCE(ARRAY_LAST(HASH_GET($existingAnswerMapping, $guid)), NULL));

	LET $hashData :=
	  INDEXES_TO_KEYS(
		$values, 
		$keys
	  );

    LET $questionCategory := HASH_GET($hashData, "Question Category"); 

	RETURN 
    ARRAY(
		ARRAY($question, $guid, $questionCategory),
		$hashData
	)
);

LET $result :=  
ARRAY_TO_HASH_GROUPED(
	$transformed, 
	$(i, item) ARRAY_INDEX($item, 1), 
	$(i, item) ARRAY_INDEX($item, 2)
);

RETURN $result

FormulaDrivenChangeFieldValues.FieldValuesFormula:
LET $values := 
ARRAY_SELECT(
  HASH_KEYS([Questionnaire Data]), $(i,item) 
  HASH_GET([Questionnaire Data], $item)
);

LET $columns := 
HASH_KEYS([Questionnaire Data]);

RETURN 
INDEXES_TO_KEYS($values, $columns)

LET $storageTypes := 
(
  LET $set := 
  ARRAY_SELECT(
    HASH_KEYS([Multiline Question Fields]), $(i, item) 
    LET $type := 
    IF(
  	OR(
  	  $item = "Answer",  
  	  $item = "Score"
  	), 
  	"Array"
    );
    RETURN 
    ARRAY($item, $type)
  );

  LET $transposed := ARRAY_TRANSPOSE($set);

  RETURN
  INDEXES_TO_KEYS(ARRAY_LAST($transposed), ARRAY_FIRST($transposed))
); 

LET $fields := ARRAY_CONCAT(HASH_KEYS([Multiline Question Fields]), ARRAY("Question Number"));

LET $targets := 
ARRAY_WHERE(
	$fields, 
	$(i, item) 
	$item <> "Question"
);

LET $questions := HASH_KEYS([Question Data]);

LET $extractData := $(target)
(
	ARRAY_SELECT(
		$questions, $(k, key) 

		LET $set := HASH_GET([Question Data], $key);

		LET $arrayWrapped :=  
		ARRAY_SELECT(
			$set, $(i, item)
			HASH_GET($item, $target)
		);

		LET $result :=  
		IF(
			HASH_GET($storageTypes, $target) = "Array", $arrayWrapped, 
			ARRAY_FIRST($arrayWrapped)
		);

		RETURN $result
	)
);

LET $results :=  
ARRAY_SELECT(
	$targets, $(i, item) 
	CALL($extractData, $item)
);

LET $popFields := 
ARRAY_SELECT(
	$fields, $(i, key) 
	IF( 
		$i = ARRAY_LENGTH($fields), 
			"Question Number", 

		HASH_GET([Multiline Question Fields], $key)
	)
); 

LET $result :=  
INDEXES_TO_KEYS(
	$results, 
	ARRAY_SKIP($popFields, 1)
);

RETURN $result

