LET $parentCheck := 
IF(
  [Linked Entity EFormType] = "Evaluation", 
  HASH("parent", ARRAY([Linked Entity EFormType], [Linked Entity RecordID]))
);

LET $questions := EACH [Question GUID]; 

RETURN
ARRAY_SELECT(
  $questions, $(i, item) 

  LET $answer := [Answer]#$i;

  LET $filter := 
  "[Question GUID] = " & DB_ENCODECONSTANT($item) &
  IF( 
    [Standard Questionnaire RecordID] <> NULL, 
    " AND [Standard Questionnaire RecordID] = " & DB_ENCODECONSTANT([Standard Questionnaire RecordID])
  );

  LET $answer := 
  IF(
    $answer = "TRUE", "Yes", 
    $answer = "FALSE", "No", 
    $answer
  ); 

  LET $scoreLookup := 
  DB_LOOKUP(
	"Score", 
    "Standard Question", 
	"[Answer] = " & DB_ENCODECONSTANT($answer) & " AND " & $filter, 
    IF( 
      $parentCheck <> NULL, 
      $parentCheck
    )
  );

  LET $optionScore := 
  ARRAY_INDEX(
    [Option Scores]#$i, 
    ARRAY_INDEXOF([Options]#$i, $answer)
  ); 

  RETURN 
  COALESCE($optionScore, $scoreLookup, 0)
)

LET $parentCheck := 
IF(
  [Linked Entity EFormType] = "Evaluation", 
  HASH("parent", ARRAY([Linked Entity EFormType], [Linked Entity RecordID]))
);

LET $questions := EACH [Question GUID]; 

LET $result := 
ARRAY_SELECT(
  $questions, $(i, item) 

  LET $filter := 
    "[Question GUID] = " & DB_ENCODECONSTANT($item) &
    IF( 
      [Standard Questionnaire RecordID] <> NULL, 
      " AND [Standard Questionnaire RecordID] = " & DB_ENCODECONSTANT([Standard Questionnaire RecordID])
    );

  RETURN
  DB_LOOKUP(
	"Answer", 
    "Standard Question", 
	$filter, 
	HASH_MERGE(
	  HASH(
	    "sort", "[Score] DESC", 
		$parentCheck
	  )	  
	)
  )
);

RETURN $result

LET $data := 
ARRAY_TRANSPOSE(
  ARRAY(
	EACH [Options], 
	EACH [Option Scores], 
	EACH [Answer Type],
	EACH [Answer (Bool)],
	EACH [Answer]
  )  
); 

RETURN
ARRAY_SELECT(
  $data, $(i, item) 
  LET $options := ARRAY_INDEX($item, 1); 
  LET $optionScores := ARRAY_INDEX($item, 2); 
  LET $answerType := ARRAY_INDEX($item, 3); 
  LET $answerBool := ARRAY_INDEX($item, 4); 
  LET $answer := ARRAY_INDEX($item, 5); 

  LET $boolOptions := 
  ARRAY_SELECT( 
    $options, $(i, item) 
    IF(
      OR(
  	    LOWER($item) = "yes", 
  	    LOWER($item) = "true"
      ), 
	    TRUE, 
      OR(
    	LOWER($item) = "no", 
    	LOWER($item) = "false"
      ), 
	  FALSE
    )
  );

  LET $targetIndex := 
  IF( 
    $answerType = "Yes / No", 
    ARRAY_INDEXOF($boolOptions, $answerBool),
    ARRAY_INDEXOF($options, $answer)
  );

  LET $result := COALESCE(ARRAY_INDEX($optionScores, $targetIndex), 0);

  RETURN $result
)

LET $data := 
ARRAY_TRANSPOSE(
  ARRAY(
	EACH [Answer Score], 
	EACH [Answer Max Score]
  )  
); 

RETURN
ARRAY_SELECT(
  $data, $(i, item) 
  LET $score := ARRAY_INDEX($item, 1); 
  LET $maxScore := ARRAY_INDEX($item, 2); 
  RETURN
	IF( 
      AND(
    	$score = $maxScore,
    	$maxScore > 0
      ), 
      "Best", 

      $score > 0, 
      "Partial",

      $maxScore > 0,
      "Incorrect", 

      NULL
    )
)

LET $data := 
ARRAY_TRANSPOSE(
  ARRAY(
	EACH [Answer Score], 
	EACH [Answer Max Score]
  )  
); 

RETURN
ARRAY_SELECT(
  $data, $(i, item) 
  LET $score := ARRAY_INDEX($item, 1); 
  LET $maxScore := ARRAY_INDEX($item, 2); 
  RETURN
  IF( 
    $maxScore > 0, 
    $score / $maxScore,
    0
  )
)

LET $scores := 
