ARRAY_SELECT(
  EACH [Question], $(i, item) 

  LET $options := [Options]#$i; 

  LET $randomNumber := 
  MOD(CEILING(RAND() * 10), ARRAY_LENGTH($options)) + 1;

  RETURN
  IF( 
    $options <> NULL, 
	ARRAY_INDEX($options, $randomNumber),
    NULL // no prefill for questions with no options
  )
)

ARRAY_SELECT(
  EACH [Question], $(i, item) 

  LET $options := ARRAY(TRUE,FALSE); 

  LET $randomNumber := 
  MOD(CEILING(RAND() * 10), ARRAY_LENGTH($options)) + 1;

  RETURN
  IF( 
    ARRAY_INDEX(EACH [Answer Type], $i) = "Yes / No", 
	ARRAY_INDEX($options, $randomNumber),
    NULL // no prefill for questions with no options
  )
)

LET $parentCheck := 
IF(
  [Linked Entity EFormType] = "Evaluation", 
  HASH("parent", ARRAY([Linked Entity EFormType], [Linked Entity RecordID]))
);

LET $questions := EACH [Question GUID]; 

RETURN
ARRAY_SELECT(
  $questions, $(i, item) 

  LET $answer := [Answer]#$i;

  LET $filter := 
  "[Question GUID] = " & DB_ENCODECONSTANT($item) &
  IF( 
    [Standard Questionnaire RecordID] <> NULL, 
    " AND [Standard Questionnaire RecordID] = " & DB_ENCODECONSTANT([Standard Questionnaire RecordID])
  );

  LET $answer := 
  IF(
    $answer = "TRUE", "Yes", 
    $answer = "FALSE", "No", 
    $answer
  ); 

  LET $scoreLookup := 
  DB_LOOKUP(
	"Score", 
    "Standard Question", 
	"[Answer] = " & DB_ENCODECONSTANT($answer) & " AND " & $filter, 
    IF( 
      $parentCheck <> NULL, 
      $parentCheck
    )
  );

  LET $optionScore := 
  ARRAY_INDEX(
    [Option Scores]#$i, 
    ARRAY_INDEXOF([Options]#$i, $answer)
  ); 

  RETURN 
  COALESCE($optionScore, $scoreLookup, 0)
)

LET $parentCheck := 
IF(
  [Linked Entity EFormType] = "Evaluation", 
  HASH("parent", ARRAY([Linked Entity EFormType], [Linked Entity RecordID]))
);

LET $questions := EACH [Question GUID]; 

LET $result := 
ARRAY_SELECT(
  $questions, $(i, item) 

  LET $filter := 
    "[Question GUID] = " & DB_ENCODECONSTANT($item) &
    IF( 
      [Standard Questionnaire RecordID] <> NULL, 
      " AND [Standard Questionnaire RecordID] = " & DB_ENCODECONSTANT([Standard Questionnaire RecordID])
    );

  RETURN
  DB_LOOKUP(
	"Answer", 
    "Standard Question", 
	$filter, 
	HASH_MERGE(
	  HASH(
	    "sort", "[Score] DESC", 
		$parentCheck
	  )	  
	)
  )
);

RETURN $result

ARRAY_LENGTH(
  ARRAY_WHERE(
	EACH [Answer (Bool)], $(i, item) $item = TRUE
  )
) & "/" &  ARRAY_LENGTH(EACH [Question])

LET $line := HASH_GET($eventargs, "Questionnaire LineNumber"); 

RETURN
COALESCE(
  HASH_GET($eventargs, "Comments"), 
  [Comments]#$line
)

LET $line := HASH_GET($eventargs, "Questionnaire LineNumber"); 

RETURN
COALESCE(
  HASH_GET($eventargs, "Answer (Bool)"), 
  [Answer (Bool)]#$line
)

LET $line := HASH_GET($eventargs, "Questionnaire LineNumber"); 

RETURN
COALESCE(
  HASH_GET($eventargs, "Answer (Date)"), 
  [Answer (Date)]#$line
)

LET $line := HASH_GET($eventargs, "Questionnaire LineNumber"); 

RETURN
COALESCE(
  HASH_GET($eventargs, "Answer (Number)"), 
  [Answer (Number)]#$line
)

LET $ln := HASH_GET($eventargs, "Questionnaire LineNumber");
LET $newAnswer := COALESCE( [Answer (Bool)]#$ln, [Answer (Number)]#$ln, [Answer (Date)]#$ln, [Answer]#$ln );
LET $oldAnswer := COALESCE( loaded:[Answer (Bool)]#$ln, loaded:[Answer (Number)]#$ln, loaded:[Answer (Date)]#$ln, loaded:[Answer]#$ln );
LET $newMessage := HASH( "Date Changed", NOW(), "Old Answer", $oldAnswer, "Old Comment", loaded:[Comments]#$ln,
"New Answer", $newAnswer, "New Comment", [Comments]#$ln,
"Changed by", EBMS_GET_CURRENT_USER(), "Reason", HASH_GET($eventargs, "Change Reason") );
RETURN IF( [Question Change Log Array]#$ln <> NULL, ARRAY_CONCAT([Question Change Log Array]#$ln, ARRAY($newMessage)), ARRAY($newMessage) )

LET $ln := HASH_GET($eventargs, "Questionnaire LineNumber"); 

LET $newAnswer :=
COALESCE(
  [Answer (Bool)]#$ln,
  [Answer (Number)]#$ln,
  [Answer (Date)]#$ln,
  [Answer]#$ln
);

LET $oldAnswer := 
COALESCE(
  loaded:[Answer (Bool)]#$ln,
  loaded:[Answer (Number)]#$ln,
  loaded:[Answer (Date)]#$ln,
  loaded:[Answer]#$ln
);

LET $oldComment := loaded:[Comments]#$ln;
LET $newComment := [Comments]#$ln;

LET $newMessage := 
DATEFORMAT(NOW(), "dd-MMM-yyyy hh:mm:ss tt") & "</br>" & 
IF( 
  $oldAnswer <> $newAnswer, 
  "Answer updated: " & HTMLENCODE(COALESCE($oldAnswer, "<Not provided>") & " -> " & COALESCE($newAnswer, "<Not provided>")) & "</br>"
) & 
IF( 
  $oldComment <> $newComment, 
  "Comment updated: " & HTMLENCODE(COALESCE($oldComment, "<Not provided>") & " -> " & COALESCE($newComment, "<Not provided>")) & "</br>" 
) & 
"Changed by: " & HTMLENCODE(EBMS_GET_CURRENT_USER()) & "</br>" & 
"Reason: " & HTMLENCODE(HASH_GET($eventargs, "Change Reason"));

RETURN
IF( 
  [Question Change Log]#$ln <> NULL, 
  [Question Change Log]#$ln & "</br></br>" & $newMessage,
  $newMessage
)

LET $line := HASH_GET($eventargs, "Questionnaire LineNumber"); 

LET $boolOptions := 
ARRAY_SELECT( 
  [Options]#$line, $(i, item) 
  IF(
    OR(
	  LOWER($item) = "yes", 
	  LOWER($item) = "true"
	), TRUE, 
    OR(
	  LOWER($item) = "no", 
	  LOWER($item) = "false"
	), FALSE
  )
);

LET $targetIndex := 
IF( 
  [Answer Type]#$line = "Yes / No", 
  ARRAY_INDEXOF($boolOptions, [Answer (Bool)]#$line),
  ARRAY_INDEXOF([Options]#$line, [Answer]#$line)
);

RETURN 
IF( 
  AND(
	[Answer]#$line = NULL, 
	[Answer (Number)]#$line = NULL, 
	[Answer (Date)]#$line = NULL, 
	[Answer (Bool)]#$line = NULL
  ), 
    NULL,

  COALESCE(
    ARRAY_INDEX(
  	  [Option Scores]#$line, 
  	  $targetIndex
    ), 
    0
  )
)

ARRAY_LENGTH(
  ARRAY_WHERE(
	EACH [Answer (Bool)], $(i, item) $item = TRUE
  )
) & "/" &  ARRAY_LENGTH(EACH [Question])
