Column.Formula:
LET $result := 
IF( 
  [Reminder Type] = "Context Date",
  DB_LOOKUP(
  	[Relative Date], 
  	[Parent EFormType], 
  	HASH("RecordID", [Parent RecordID])
  )
); 

LET $result := 
IFERROR(
  TODAY($result), 
  NULL()
);

RETURN 
IF( 
  $result < DATE(YEAR(2000)),
  NULL(), 
  $result
)

LET $parentResult := 
ARRAY_SELECTMANY(
  	[Reminder Recipients], 
  	$(i, item) 
    IFERROR(
	SPLIT(EBMS_GET_ROLE_EMAIL_ADDRESSES(DB_LOOKUP(
	  $item, 
	  [Parent EFormType], 
	  HASH("RecordID", [Parent RecordID])
	)), ","), 
	NULL()
  )
);
LET $roleResult := 
ARRAY_SELECTMANY(
  	[Reminder Recipients - Roles], 
  	$(i, item)   	
	SPLIT(IFERROR(EBMS_GET_ROLE_EMAIL_ADDRESSES($item), NULL()), ",")
);

LET $digestResult := 
IF(
  AND(
	[Digest Email] = TRUE, 
	OR(
	  [Array of Email Addresses Check] = TRUE,
      [Email to Grouped User] = TRUE
	)
  ),
  [Digest Recipient]
);


LET $emailAddresses := 
ARRAY_WHERE(
  ARRAY_DISTINCT(
	ARRAY_CONCAT($digestResult, $parentResult, $roleResult, SPLIT([Reminder Recipients - Specific], ","))), 
  $(i, item) 
  $item <> NULL()
);

RETURN 
COALESCE(
  ARRAY_JOIN($emailAddresses, ", "),
  "None"
)

LET $period := IF( [When] = "Before", -[Reminder 1],
[When] = "After", [Reminder 1],
0 );
LET $period := IF( [Measure] = "week", $period * 7, $period );
LET $measure := IF( [Measure] = "week", "day", [Measure] );
LET $result := IF( [Reminder Type] = "Static Date", TODAY([Reminder Date]), DATEADD($measure, $period, TODAY([Relative Date Result])) );
RETURN IF( $result < DATE(YEAR(2000)), NULL(), $result )

Column.DefaultValueFormula:
LET $data := 
DB_LOOKUPALL(
  ARRAY("Username", "DisplayName"), 
  "sys.Roles", 
  "[Type] <> 'GUEST' AND [Active] = TRUE AND ([IsSystem] <> TRUE OR [Username] = 'Administrators' OR [Username] = 'System Builders' OR [Username] = 'Everyone') AND ([DisplayName] IS NOT EMPTY OR [Type] = 'GROUP')"
);

RETURN 
ARRAY_SELECT(
	$data, $(i, item) 
	LET $value := ARRAY_INDEX($item, 1);
	LET $text := 
	IF(
		ISBLANK(ARRAY_INDEX($item, 2)), 
		ARRAY_INDEX($item, 1), 
		ARRAY_INDEX($item, 2)
	);

	RETURN 
	HASH(
	  "value", $value,
	  "text", $text
  )
)

Column.Formula:
LET $period := [Reminder Period]; 

LET $period := 
IF( 
  [Reminder Period Measure] = "week", 
  $period * 7, 
  $period
); 

LET $measure := 
IF( 
  [Reminder Period Measure] = "week", 
  "day", 
  [Reminder Period Measure]
); 

RETURN
IF( 
  AND(
	[Reminder Date] <> NULL(), 
	[End Date Type] = "Period"
  ), 

  DATEADD($measure,$period,[Reminder Date]), 
  [Reminder Date]
)

LET $period := [Frequency]; 

LET $period := 
IF( 
  [Frequency Measure] = "week", 
  $period * 7, 
  $period
); 

LET $measure := 
IF( 
  [Frequency Measure] = "week", 
  "day", 
  [Frequency Measure]
); 

LET $reminderDates := 
ARRAY_SELECT(
  RANGE(0, 500), $(i, item) 
  DATEADD($measure, $period * ($i - 1), [Reminder Date])
);

LET $validDates :=  
ARRAY_WHERE(
	$reminderDates, $(i,item) 
	AND(
	  $item >= TODAY(),
	  $item > ARRAY_LAST([Date Email Sent]), //TODAY() > ARRAY_LAST([Date Email Sent]),
	  IF(
			OR([End Date] = NULL(), [End Date Type] = "Indefinite"), 
			TRUE, 
			$item <= [End Date]
		)
	 )
);

LET $nextDatewithFrequency :=  
ARRAY_FIRST(
	$validDates
);

LET $onceOffDate := 
IF( 
	TODAY() <= [Reminder Date], [Reminder Date], 
	NULL()
);

RETURN 
IF( 
  AND([Reminder Date] = NULL, ARRAY_LENGTH([System Wide Matches]) > 0), TODAY(),

  [Reminder Frequency] = "Recurring", $nextDatewithFrequency, 

  [Reminder Frequency] = "Once-Off", $onceOffDate
)

LET $relativeDate:= 
IF( 
	[Relative Date] = NULL(),
	DB_ENCODECONSTANT([Reminder Date]),
	DB_ENCODEIDENTIFIER([Relative Date])
); 

LET $measureConversion := $(measure)
(
	IF( 
		$measure = "day", "day", 
		$measure = "week", "day", 
		$measure = "month", "month",
		$measure = "year", "year",
		"day"
	)
);

LET $beforeAfterConversion := $(period, beforeOrAfter)
(
	IF( 
		$beforeOrAfter = "Before", ($period),
		$beforeOrAfter = "After", -($period),
		$beforeOrAfter = "On", 0
	)
);
LET $periodMeasure :=  IF([End Date Type] = "Indefinite", "year", CALL($measureConversion, [Reminder Period Measure]));
LET $periodDays := IF([End Date Type] = "Indefinite", "10", [Reminder Period]);
LET $periodDays := IF([Reminder Period Measure] = "week", $periodDays * 7, $periodDays);	

LET $frequencyMeasure := CALL($measureConversion, [Frequency Measure]);
LET $frequencyDays := [Frequency];
LET $frequencyDays := IF([Frequency Measure] = "week", $frequencyDays * 7, $frequencyDays);	

LET $contextMeasure := CALL($measureConversion, [Measure]); 
LET $contextBeforeOrAfter := [When];
LET $contextDays := CALL($beforeAfterConversion, [Reminder 1], $contextBeforeOrAfter); 
LET $contextDays := IF([Measure] = "week", $contextDays * 7, $contextDays);	

LET $set := 
ARRAY(
	ARRAY($periodMeasure, $periodDays),
	ARRAY($contextMeasure, $contextDays)
);

LET $offset := 
//get base date which accounts for the before / after period
DATEADD($contextMeasure, $contextDays, TODAY()); 

LET $checkRange :=  
//Range of possible dates that the context date could be in order to trigger an email
ARRAY(
	DATEADD($periodMeasure, $periodDays * -1, $offset),
	DATEADD($contextMeasure, $contextDays, TODAY()) 
); 

LET $startDateRange := LEAST($checkRange); 
LET $endDateRange := GREATEST($checkRange); 

LET $diff := DATEDIFF_PARTS($endDateRange, $startDateRange);

LET $occurences := 
IF( 
	$frequencyMeasure = "day", 
	CEILING(($endDateRange - $startDateRange) / $frequencyDays),

	$frequencyMeasure = "week", 
	CEILING($endDateRange - $startDateRange / 7) / $frequencyDays,

	$frequencyMeasure = "month", 
	CEILING(
		(
			ARRAY_INDEX($diff, 1) * 12 +
			ARRAY_INDEX($diff, 2) +
			CEILING(ARRAY_INDEX($diff, 3) / 31)
		) / $frequencyDays
	), 

	$frequencyMeasure = "year", 
	CEILING(
		(
			ARRAY_INDEX($diff, 1) +
			CEILING(ARRAY_INDEX($diff, 2) / 12)
		) / $frequencyDays
	)
);

LET $datelist := 
ARRAY_SELECT(
	RANGE(0, LEAST($occurences + 1, 1000)), $(i, item)
	DATEADD($frequencyMeasure, $frequencyDays * ($i - 1) * -1, $endDateRange)
);

LET $filterString := 
IF( 
  NOT(ISBLANK(TRIM([Filter String]))),
  	" AND " & [Filter String],
  [Filter String]
);

LET $lower := DATEADD($contextMeasure, $contextDays, TODAY());
LET $upper := DATEADD("day", 1, $lower);

LET $lookupSize := 100;

LET $data :=
IF(
  	[Reminder Frequency] = "Recurring",
		(ARRAY_SELECTMANY(
			RANGE(0, CEILING(ARRAY_LENGTH($datelist) / $lookupSize)), $(i, item)

			LET $dateFilter := 
				ARRAY_JOIN(
					ARRAY_SELECT(
						ARRAY_TAKE(ARRAY_SKIP($datelist, $item * $lookupSize), $lookupSize), $(i, date) 
						"(" & 
							$relativeDate & " >= " & DB_ENCODECONSTANT($date)
							& " AND " & 
							$relativeDate & " < " & DB_ENCODECONSTANT(DATEADD("day", 1, $date)) & 
						")"
					), " OR "
				);

			LET $filter := $dateFilter & $filterString & " AND [LineNumber] = 1";

		  	RETURN
			DB_LOOKUPALL(
				"RecordID",
				[Context Form],
				$filter
			)
		)),

	(LET $dateFilter :=
		"(" & 
			$relativeDate & " >= " & DB_ENCODECONSTANT($lower) 
		& " AND " & 
			$relativeDate & " < " & DB_ENCODECONSTANT($upper) & 
		")";

	LET $filter := $dateFilter & $filterString & " AND [LineNumber] = 1";

	RETURN
	DB_LOOKUPALL(
		"RecordID",
		[Context Form],
		$filter
	))
);

LET $cleaned := 
ARRAY_WHERE(
	$data, $(i, item) 
	NOT(ISERROR($item))
);

LET $result := 
ARRAY_SELECT(
	$cleaned, $(i, item) 
	ARRAY([Context Form], $item) 
);

LET $result :=  
IF( 
  	[Reminder Enabled] = TRUE, $result,
	NULL()
);

RETURN
IF(
  	[System Wide Matches] <> NULL(),
  		[System Wide Matches],
	$result
)

 HASH_GET( [DG Report Lookup], "Columns Widths")
/*DB_LOOKUP(
  "Columns Widths", 
  "Settings - Automation", 
  "[DG - Unique Reference] = " & DB_ENCODECONSTANT([Unique Reference]) & " AND [DG - Unique Reference] IS NOT EMPTY"
)

LET $field := [Group on User];

LET $data := 
IF( 
  [Array of Email Addresses Check] = TRUE, 
  DB_LOOKUPALL(
  	$field, 
  	[Context Form],
  	IF(
  		ISBLANK([Filter String]), 
  		"1=1", 
  		[Filter String]
  	)
  ),
  DB_REPORT(
  	"[" & $field & "]", 
  	[Context Form],
  	IF(
  		ISBLANK([Filter String]), 
  		"1=1", 
  		[Filter String]
  	)
  )
);

LET $distinctList :=  
ARRAY_DISTINCT(ARRAY_SELECTMANY(
  $data, $(i, item) 
  IF( 
    [Array of Email Addresses Check] = TRUE, 
      ARRAY_SELECTMANY($item, $(i, item) SPLIT($item, ",")),
    ARRAY_FIRST($item)
  )
));

LET $result :=  
IF( 
  $field <> NULL, 
    IFERROR(ARRAY_WHERE($distinctList, $(i,item) $item <> NULL), 
  NULL)
);

RETURN $result

HASH_GET([DG Report Lookup], "DG - Column Alias")
/*DB_LOOKUP(
  "DG - Column Alias", 
  "Settings - Automation", 
  "[DG - Unique Reference] = " & DB_ENCODECONSTANT([Unique Reference]) & " AND [DG - Unique Reference] IS NOT EMPTY"
)

