FieldEntry.Formula:
LET $targetEForm := [Context Form];
LET $systemForm := ARRAY_INDEXOF(ARRAY("sys.Audit"), $targetEForm) > 0;
LET $columns := HASH_GET([DG Report Lookup], "Output Column Names");
LET $lookupColumns := IF($systemForm <> TRUE, ARRAY_CONCAT($columns, "RecordID"), $columns);
LET $filter := [Filter String];
LET $sort := HASH_GET([DG Report Lookup], "DG - Order By");
LET $sortType := HASH_GET([DG Report Lookup], "DG - Order By Type");
LET $sortData := ARRAY_TRANSPOSE(ARRAY($sort, $sortType));

LET $sortString := 
IF( 
	AND(
		$sortData <> NULL(),
		$sortData <> ARRAY(ARRAY(NULL(), NULL()))
	), 
	ARRAY_JOIN(
	ARRAY_SELECT(
		$sortData, $(i,item)
		DB_ENCODEIDENTIFIER(ARRAY_INDEX($item, 1)) & " " & COALESCE(ARRAY_INDEX($item, 2), "ASC")
	), 
	", ",
	),
	AND(
		$sort <> NULL(),
		$sortType <> NULL()
	), 
	DB_ENCODEIDENTIFIER($sort) & " " & $sortType 
);

LET $format := HASH_GET([DG Report Lookup], "DG - Format");

LET $filter := 
IF( 
    ISBLANK([Targeted Digest Filter]), NULL(), 
    [Targeted Digest Filter] & " AND "
  ) &  

  COALESCE($filter, "1 = 1") & " AND " & 
  IF(
    AND($systemForm = TRUE, ISBLANK([Filter String])),
    "1 = 2",

    AND($systemForm = TRUE, NOT(ISBLANK([Filter String]))),
    "1 = 1",

    [Multiline Check] = TRUE, 
    "[LineNumber] >= 1",

    "[LineNumber] = 1"
);


LET $getData := $(sort)(
   DB_LOOKUPALL(
		$lookupColumns,
		$targetEForm,
		$filter,
		HASH_MERGE(
		  HASH(
			"limit", 
			200
		  ), 
		  HASH(
		    "sort", 
			IF($sort = TRUE, $sortString, IF($systemForm <> TRUE, "[RecordID] ASC", "[DateCreated] ASC"))
	      )
		)
	)
);

LET $data :=
IF( 
	OR(
		ISBLANK($targetEForm),
		ISBLANK($columns)
	),
	ARRAY(NULL()),

   LET $data := IFERROR(CALL($getData, TRUE), CALL($getData, FALSE));

   RETURN $data
);

RETURN $data

LET $columns := HASH_GET([DG Report Lookup], "Output Column Names");
LET $alias := HASH_GET([DG Report Lookup], "DG - Column Alias");
LET $numberOfColumns := ARRAY_LENGTH($columns); 
LET $widths := SPLIT(HASH_GET([DG Report Lookup], "Columns Widths"), ",");
LET $targetEForm := [Context Form];
LET $systemForm := ARRAY_INDEXOF(ARRAY("sys.Audit"), $targetEForm) > 0;
LET $showViewLink := IF($systemForm <> TRUE, [Show View Link]);
LET $format := HASH_GET([DG Report Lookup], "DG - Format");
LET $data := [Digest Results];

LET $thStyle := "text-align:left;background-color:#d0d0d0;border:solid #a0a0a0 1px;";
LET $tdStyle := "text-align:left;background-color:white;border:solid #e0e0e0 1px;";
LET $evenColumnWidth  := (100 / $numberOfColumns);

LET $columns := 
IF(
  $systemForm = TRUE,
  $columns, 

  $showViewLink = TRUE, 
  ARRAY_CONCAT("View", $columns),

  $columns
);
LET $widths := 
IF(
  $systemForm = TRUE,
  $widths, 

  $showViewLink = TRUE, 
  ARRAY_CONCAT("5", $widths),
  $widths
);

LET $data := 
ARRAY_SELECT(
  $data, $(i,item) 
  IF(
	  AND($systemForm <> TRUE, $showViewLink = TRUE), 
	  ARRAY_CONCAT(ARRAY("View"), $item), 
	  $item
  )
); 

LET $columnsTableFormatted := 
ARRAY_JOIN(
	ARRAY_SELECT(
		$columns, $(c, column) 

		LET $width := 
		IF(
		  HASH_GET([DG Report Lookup], "Columns Widths"), 
		  $evenColumnWidth,

		  (
		    LET $result := TRIM(ARRAY_INDEX($widths, $c));

		    RETURN 
		    IF(
		      ISBLANK($result), 
		      25, 
		      N($result)
		    )
		  )
		);

		LET $result := 		
		"<th style=""width: " & $width  & "%;" & $thStyle & """>" & 
			COALESCE(HASH_GET($alias, $column), $column) & 
		"</th>";

		RETURN $result
	)
);

LET $dataTableFormatted := 
ARRAY_JOIN(
	ARRAY_SELECT(
		$data, $(r, row) 
	    LET $recordID := ARRAY_LAST($row); 

	  	RETURN 
		"<tr>" & 
			ARRAY_JOIN(
				ARRAY_SELECT(
				  	IF(
					  $systemForm <> TRUE,
				      ARRAY_TAKE($row, ARRAY_LENGTH($row) - 1), 
					  $row
					), 
				    $(c,cell)

					  LET $cellFormatting := 
				  	ARRAY_INDEX(
						$format, 
						IF($showViewLink = TRUE, $c - 1, $c)
				  	);		

				  	LET $viewColumn := 
				  	AND(
 						$showViewLink = TRUE, 
 						$c = 1
					); 

				   LET $formattedCellValue := 
				   IF(
					 OR($cellFormatting = "Text", ISBLANK($cellFormatting)), $cell, 
					 $cellFormatting = "URL", "<a href=""" & HTMLENCODE($cell) & """>Link" & "</a>", 					 
					 $cellFormatting = "N", IFERROR(NUMBERFORMAT($cell, "N2"\), $cell), 
					 $cellFormatting = "C", IFERROR(NUMBERFORMAT($cell, "C"), $cell),
					 $cellFormatting = "Full DateTime", IFERROR(DATEFORMAT($cell, "dd MMMM yyyy hh:mm tt"), $cell),
					 $cellFormatting = "Dollar no cents", "$" & IFERROR(NUMBERFORMAT($cell, "#,##0"), $cell),
					 $cellFormatting = "Display Name", IFERROR(EBMS_GET_ROLE_DISPLAYNAME($cell), $cell),
				  	 $cell 
				   );

				  	RETURN 
					"<td style=""" & $tdStyle & """>" & 
					IF(
						$viewColumn = TRUE, 
							"<a href=""" & HTMLENCODE(EBMS_GET_BASE_URL() & "/records/" & URLENCODE($targetEForm) & "/" & $recordID) & """>View</a>",

					    $cellFormatting = "URL",
					  		$formattedCellValue,

					    HTMLENCODE(COALESCE($formattedCellValue, " "))
					)
				  & 
				"</td>"
				)
			) &
		"</tr>"
	)
);

LET $table := 
"<table cellspacing=""0"" style=""border-collapse: collapse; width: 100%"">" &
	$columnsTableFormatted &
	$dataTableFormatted &
"</table>";

RETURN 
IFERROR($table, NULL())

LET $data := 
IF(
  	[Reminder Special Recipients] <> ARRAY(),
  		ARRAY_SELECTMANY(
		  	[Reminder Special Recipients], $(i, item)
  			DB_LOOKUPALL(ARRAY($item, "Insurance RecordIDs"), "Contract", "[LineNumber] = 1 AND [" & $item & "] IS NOT NULL")
		),

  	NULL()
);

LET $formattedData :=
ARRAY_DISTINCT(
ARRAY_SELECTMANY(
  	$data, $(i, item)	//loop across each contract record found
  	ARRAY_SELECT(
	  	ARRAY_INDEX($item, 2), $(j, jitem) 	//loop across each insurance recordid per contract
	  		ARRAY(
			  	$jitem, 				//return Insurance RecordID
			  	ARRAY_INDEX($item, 1)	//return Contract Owner Username
	  		)
  	)
));


LET $matched :=
ARRAY_SELECT(
	[System Wide Matches], $(i, item)
	LET $matches := 
		ARRAY_WHERE(
			$formattedData, $(m, match)
			ARRAY_INDEX($item, 2) = ARRAY_INDEX($match, 1)
		);

	LET $indexedMatches :=
		ARRAY_SELECTMANY($matches, $(j, jitem)
			ARRAY_INDEX($jitem, 2)
		);

	RETURN
	$indexedMatches
);

RETURN
IF(
  	[Reminder Special Recipients] = ARRAY(),
  		NULL(),
	$matched
)

TriggerCustomEvent.EventArgumentsFormula:
LET $toSet := 
HASH(
  "Target Form", [Reminder Recipients],
  "System Roles", 
  		ARRAY_DISTINCT(
			ARRAY_CONCAT(
				COALESCE([Reminder Recipients - Roles], ARRAY()),
				ARRAY_INDEX([Reminder Special Recipients Data], $count)
		  	)
		),
  "Static Email Addresses", [Reminder Recipients - Specific]
);

LET $ccSet := 
HASH(
  "Cc Target Form", [Cc Recipients],
  "Cc System Roles", [Cc Recipients - Roles],
  "Cc Static Email Addresses", [Cc Recipients - Specific]
);

LET $bccSet := 
HASH(
  "Bcc Target Form", [Bcc Recipients],
  "Bcc System Roles", [Bcc Recipients - Roles],
  "Bcc Static Email Addresses", [Bcc Recipients - Specific]
);

LET $replyToSet := 
HASH(
  "ReplyTo Target Form", [ReplyTo Recipients],
  "ReplyTo System Roles", [ReplyTo Recipients - Roles],
  "ReplyTo Static Email Addresses", [ReplyTo Recipients - Specific]
);

LET $emailType := IF([Content Type] = "Email Template", [Email Template], "Custom Reminder");

LET $emailAttachments:= HASH(
  	"Record PDF", 
  	IF(
	  	[Attach Record PDF - Alternate View] = TRUE, [Attach Record PDF - Alternate View Name],
	  	[Attach Record PDF - Default View] = TRUE, "Default", 
	  	NULL()
  	),
  	"Record File",
 	IF( 
	  	[Attach Record File] = TRUE, [Attach Record File Path Name],
	  	NULL()
  	)

);

RETURN
ARRAY(
  $emailType, 
  HASH_MERGE(
	$toSet, 
    IF([Cc Applicable] = TRUE,		$ccSet), 
    IF([Bcc Applicable] = TRUE, 	$bccSet), 
    IF([ReplyTo Applicable] = TRUE, $replyToSet),
	$emailAttachments
  )
)

