DisplayMessage.MessageTemplate:
{
LET $lookup := 
(
  LET $targetEForm := [Context Form];
  LET $systemForm := ARRAY_INDEXOF(ARRAY("sys.Audit"), $targetEForm) > 0;
  LET $filter := [Filter String];

  LET $filter := 
  IF( 
    ISBLANK([Targeted Digest Filter]), NULL(), 
    [Targeted Digest Filter] & " AND "
  ) &  
  COALESCE($filter, "1 = 1") & " AND " & 
  IF(
    AND($systemForm = TRUE), "1 = 1",
    [Multiline Check] = TRUE, "[LineNumber] >= 1",
    "[LineNumber] = 1"
  );

  RETURN 
  DB_REPORT(
    "COUNT([DateCreated])", 
    $targetEForm, 
    $filter
  )
); 

RETURN 
IF(
  ISERROR($lookup), "Filter is not valid", 
  NOT(ISERROR($lookup)), "Filter is valid, returned " & ARRAY_FIRST(ARRAY_FIRST($lookup)) & " results"
)
}
