Column.Formula:
LET $fieldMappings := 
HASH(
	"Extension",				ARRAY("Status"),
	"Request for Information",  ARRAY("Status"),
	"Variation",  				ARRAY("Status"),
	"Work Order", 				ARRAY("Status")
);

LET $relatedForms := HASH_KEYS($fieldMappings);

LET $relatedData := 
ARRAY_SELECTMANY(
	$relatedForms, $(r, relatedEFormType)
	(
		LET $targetFields := HASH_GET($fieldMappings, $relatedEFormType);

		LET $fieldData := 
		ARRAY_SELECTMANY(
			$targetFields, $(f, field)

			LET $result := 
			DB_LOOKUPALL(
				ARRAY("EFormType", "RecordID", $field),
				$relatedEFormType,
				"[LineNumber] FAST_EQUAL_TO 1 AND [Source Request for Information RecordID] = " & DB_ENCODECONSTANT([RecordID])
			);

			LET $cleaned := 
			ARRAY_WHERE(
				$result, $(i, item) 
				ARRAY_INDEX($item, 3) <> NULL()
			);

			LET $injected := 
			ARRAY_SELECT(
				$cleaned, $(i, item)
				ARRAY_CONCAT(
					ARRAY_TAKE($item, 2), 
					$field, 
					ARRAY_INDEX($item, 3)
				)
			);

			RETURN $result
		);

		LET $cleaned := 
		ARRAY_WHERE(
			$fieldData, $(i, item) 
			ARRAY_LENGTH($item) > 0
		);

		RETURN $cleaned
	)
);

LET $sorted := ARRAY_SORTDESC($relatedData, $(item) ARRAY_INDEX($item, 4));

RETURN $sorted

IF( 
  [Vendor Action List] = NULL, 
  (
	LET $data := 
  DB_LOOKUP(
	"Vendor Action List", 
	"Contract", 
	"[RecordID] = " & DB_ENCODECONSTANT([Contract RecordID])
  );

  RETURN 
  ARRAY_SELECTMANY(
	ARRAY_WHERE(
	  $data, $(i, item) 
	  ARRAY_INDEX($item, 2) = TRUE
	), $(i, item) 
	ARRAY_INDEX($item, 1)
  )
), 
  [Vendor Action List]
 )

LET $parentData := 
	DB_LOOKUP(
	  ARRAY(
			"Register Selection Mode",
			"Contract Type",
			"Register Override"
		),
	  	"Contract",
	  	"[RecordID] = " & DB_ENCODECONSTANT([Contract RecordID])
	);

LET $registerSelectionMode := ARRAY_FIRST($parentData);
LET $contractType := ARRAY_INDEX($parentData, 2);
LET $registerOverride := ARRAY_LAST($parentData);
LET $addVariationExtension := ARRAY("Variations", "Extensions"); //these registers are always enabled

LET $enabledRegisters :=
	IF(
	  	$registerSelectionMode = "Manual", $registerOverride,
	  	DB_LOOKUP(
		  	"Enabled Registers",
		  	"Contract Type",
		  	"[Name] = " & DB_ENCODECONSTANT($contractType)
		)
	);

RETURN ARRAY_CONCAT($enabledRegisters, $addVariationExtension)

LET $current := ARRAY([EFormType], [RecordID]);
LET $descriptionColumns := 
HASH(
	"Contract",  					"Contract Number", 
	"Vendor", 						"Company Name",
	"Procurement", 					"Procurement Number",
	"Project", 						"Project Number",
    "Evaluation", 		    		"Description",
  	"Information Request",  		"File No.", 
	"Subpoena", 					"File No.",
	"Privacy Incident", 			"File No."
); 

LET $altFormName:= HASH(
  	"Insurance",	"Compliance"
  );

LET $getLabelText := $(eFormType, recordID)
(
  	LET $targetColumn := HASH_GET($descriptionColumns, $eFormType);
  	RETURN 
  	IF( $targetColumn = NULL, NULL,
		LET $isthisRecord := ARRAY($eFormType, $recordID) = $current;
		LET $title:= 
		IF( $isthisRecord = TRUE, 
			CURRENT_VALUE($targetColumn),
			ARRAY_FIRST(DB_LOOKUP(
				ARRAY($targetColumn),
				$eFormType, 
				"[RecordID] FAST_EQUAL_TO " & $recordID
			))
		  );
		//shortern the descriptive label/ title
		LET $charCount := LEN($title);
		LET $titleArray := SPLIT($title, " ");
		LET $wordCount := ARRAY_LENGTH($titleArray);

		LET $charLimit := 30; 
		LET $wordLimit := ARRAY_LENGTH( SPLIT( LEFT($title, $charLimit), " ") );

	  RETURN //ARRAY_TAKE($titleArray, $wordLimit)
		IF(
			$wordCount <= $wordLimit, $title,
			ARRAY_JOIN(
				ARRAY_TAKE($titleArray, $wordLimit), " "
			)& " ..."
		)
	  )

); 

LET $getReadAccess := $(eFormType, recordID)
( EBMS_GET_EFORMRECORD_PERMISSION($eFormType, $recordID, "read"));

LET $currentDes := CALL($getLabelText, [EFormType], [RecordID]);
LET $currentAccess := FALSE;

RETURN
IF(
	[Parent Structure] <> NULL, [Parent Structure],
  	(

	  LET $parent := 
	  IF( 
		[RecordID] > 0, 
		(
		  LET $record := EBMS_GET_PARENT_RECORD(ARRAY_FIRST($current), ARRAY_INDEX($current, 2));
		  RETURN 
			  IF(  ARRAY_FIRST($record) == NULL, NULL,
				ARRAY_CONCAT( 
				  $record, 
				  CALL($getLabelText, ARRAY_FIRST($record), ARRAY_INDEX($record, 2)),
				  CALL($getReadAccess, ARRAY_FIRST($record), ARRAY_INDEX($record, 2))
				  )

			  )
	  )); 

	  LET $gParent := 
	  IF( 
		 $parent <> NULL, 
		(
		  LET $record := EBMS_GET_PARENT_RECORD(ARRAY_FIRST($parent), ARRAY_INDEX($parent, 2));
		  RETURN 
			  IF(  ARRAY_FIRST($record) == NULL, NULL,
				ARRAY_CONCAT( 
				  $record, 
				  CALL($getLabelText, ARRAY_FIRST($record), ARRAY_INDEX($record, 2)),
				  CALL($getReadAccess, ARRAY_FIRST($record), ARRAY_INDEX($record, 2))
			  )
		  )
	  )); 

	  LET $hash :=  
	  HASH_MERGE(
		  IF( $parent <> NULL, HASH("parent", 	$parent)),
		  IF( $gParent <> NULL, HASH("gparent", 	$gParent))
	  );

	  LET $parentStructure := 	//2nd instance
	  IF( 
		  ARRAY_LENGTH(HASH_KEYS($hash)) >0, $hash, NULL
	  );

	  LET $parentData:= 
	  ARRAY_WHERE(
		ARRAY(
			$gParent, 
			$parent, 
			ARRAY_CONCAT($current, $currentDes, $currentAccess),
		), $(i,item) $item <> NULL
	   );


	  LET $parentChain:=
		ARRAY_SELECT(
			  $parentData, $(i, item)
			  ARRAY_TAKE($item, 2)
	   );


	  LET $parentLinks :=
	  ARRAY_SELECT(
		  $parentData, $(i, item) 
			  LET $eFormType 		:= ARRAY_FIRST($item);
			  LET $recordID 		:= ARRAY_INDEX($item, 2);
			  LET $descriptiveLabel := ARRAY_INDEX($item, 3);
			  LET $hasReadAccess	:= ARRAY_INDEX($item, 4);
			  LET $URL	:= "records/" & URLENCODE($eFormType) & "/" & $recordID ;
			  LET $showEFormType := $eFormType <> "Vendor";

			  LET $labelColour := 
				  IF(
				   AND($eFormType = [EFormType], $recordID = [RecordID]),
					  "background: #ffffff; color: #5D5D5D;",
					 $hasReadAccess = TRUE,
					  "background: #ffffff; color: #4D87B7;",
					  "background: #ffffff; color: #5D5D5D;"

				  );



			  LET $labeltext:= 
					  IF( $showEFormType == TRUE, " " & COALESCE(HASH_GET($altFormName, $eFormType), $eFormType) ) &
					  IF(NOT(ISBLANK($descriptiveLabel)), " " & $descriptiveLabel);

			  LET $label :=  
				  "<span style=""margin-left: 5px;" & $labelColour & """>" &
				  HTMLENCODE($labeltext) & 
				  "</span>";

			  RETURN 
			  IF( 
				  $hasReadAccess == TRUE, 
				  "<a style=""text-decoration-line: none;"" href=" & HTMLENCODE($URL) & ">" & $label & "</a>",
				  $label 
			  )
	  );

	   LET $separator :=   "<svg style=""vertical-align: bottom; margin-left: 5px; height: 18px;"" xmlns=""http://www.w3.org/2000/svg"" viewBox=""0 0 256 512""><path fill=""#5D5D5D"" d=""M246.6 278.6c12.5-12.5 12.5-32.8 0-45.3l-128-128c-9.2-9.2-22.9-11.9-34.9-6.9s-19.8 16.6-19.8 29.6l0 256c0 12.9 7.8 24.6 19.8 29.6s25.7 2.2 34.9-6.9l128-128z""/></svg>";

	  LET $breadcrumb := "<div style=""height: 23px; padding-left: 5px; font-size: 10pt; color: #5D5D5D; "">You are here:" & ARRAY_JOIN($parentLinks, $separator) & "</div>";

	  RETURN 
	  HASH(
		  "Parent Structure",	$parentStructure,
		  "Parent Data", 		$parentData,
		  "Parent Chain",		$parentChain,
		  "Breadcrumb",		$breadcrumb
		)
	 )
)

Binding.Expression:
LET $standard := ARRAY("Request");
LET $response := IF([Date Submitted] <> NULL(), ARRAY("Response"));	
LET $smeComms := IF(AND([Date Submitted] <> NULL(), [Is System User] == TRUE), ARRAY("Internal Communication"));
LET $raiseNewRecord := IF([Date Submitted] <> NULL(), ARRAY("Raise New Record"));	

RETURN 
ARRAY_DISTINCT(
	ARRAY_WHERE(
		ARRAY_CONCAT($standard, $response, $smeComms, $raiseNewRecord), $(i, item) 	
		$item <> NULL()	
	)
)

LET $standard := ARRAY("Request");
LET $response := IF([Date Submitted] <> NULL(), ARRAY("Response"));	
LET $smeComms := IF(AND([Date Submitted] <> NULL(), [Is System User] == TRUE), ARRAY("Internal Communication"));
LET $raiseNewRecord := IF([Date Submitted] <> NULL(), ARRAY("Raise New Record"));	

LET $values := 
ARRAY_DISTINCT(
	ARRAY_WHERE(
		ARRAY_CONCAT($standard, $response, $smeComms, $raiseNewRecord), $(i, item) 	
		$item <> NULL()	
	)
);

RETURN ARRAY_SELECT($values, $(i,item) HASH("value", $item, "text", ""))

LET $sourceEFormType := [Source EFormType];

LET $sourceRecordID := [Source RecordID];

LET $link := "records/" & HTMLENCODE($sourceEFormType) & "/" & $sourceRecordID;

RETURN "<div style=""line-height: 20px; text-align: center"">This record was raised in relation to <a href=""" & HTMLENCODE($link) & """>" & $sourceEFormType & " ID:" & $sourceRecordID & "</a></div>"

//get the available registers for the user
LET $extensionEnabled :=
AND(
  	ARRAY_INDEXOF([Enabled Contract Registers], "Extensions") > 0,
  	OR([Is System User] = TRUE, ARRAY_INDEXOF([Vendor Action List], "Extensions") > 0)
);

LET $variationEnabled :=
AND(
  	ARRAY_INDEXOF([Enabled Contract Registers], "Variations") > 0,
  	OR([Is System User] = TRUE, ARRAY_INDEXOF([Vendor Action List], "Variations") > 0)
);

LET $workOrderEnabled :=
AND(
  	ARRAY_INDEXOF([Enabled Contract Registers], "Work Orders") > 0,
  	OR([Is System User] = TRUE, ARRAY_INDEXOF([Vendor Action List], "Work Order") > 0)
);

RETURN
ARRAY_SORT(ARRAY_WHERE(
	ARRAY(
  		"Request for Information",
  		IF($extensionEnabled, "Extension"),
  		IF($variationEnabled, "Variation"),
  		IF($workOrderEnabled, "Work Order")
	), $(i,item) $item <> NULL()
))

LET $rows:= ARRAY_SELECT(
	[Related Record Data],
	$(i, item) (
		LET $href := EBMS_GET_EFORMRECORD_URL(ARRAY_INDEX($item, 1), ARRAY_INDEX($item, 2));
		LET $description := ARRAY_INDEX($item, 3);

		LET $colour := 
//		IF(
//			$event = "Date Approved", "#0a7c00", 
//			$event = "Date Submitted", "#0700a3", 
//			$date < NOW(), "#666666",
//			$date < DATEADD("day", 7, NOW()), "#FFFFFF",
			"#FFFFFF";
//		);

		LET $backcolour := "black";

		RETURN 
		ARRAY(
			HASH("value", ARRAY_INDEX($item, 1), "url", $href, "colour", $colour, "backcolour", $backcolour),
			HASH("value", ARRAY_INDEX($item, 2), "url", $href, "colour", $colour, "backcolour", $backcolour),
			HASH("value", ARRAY_INDEX($item, 3), "url", $href, "colour", $colour, "backcolour", $backcolour)
		)
	)
);


RETURN HASH(
	"columns", ARRAY(
		HASH(
			"heading","EForm", 
			"width", 390
		), 
		HASH(
			"heading","RecordID", 
			"width", 160
		), 
		HASH(
			"heading","Status", 
			"width", 130
		)
	), 
	"rows", $rows
)
