FieldEntry.Formula:
ARRAY_SELECT(
  [Document Data], $(i, item)
  LET $currentAssigned := HASH_GET($item, "Assigned Teams");

  LET $updatedAssigned := 
  ARRAY_DISTINCT(
	ARRAY_WHERE(
	  $currentAssigned, $(i, item) 
	  AND(
		$item <> NULL, 
		ARRAY_INDEXOF([Document Allocation - Teams], $item) = NULL
	  )
    )
  ); 

  LET $responseId := HASH_GET($item, "Response ID");
  LET $responseName := HASH_GET($item, "Response Name");

  LET $respondentLine := ARRAY_INDEXOF(EACH [Respondent], $responseName);
  LET $excluded := [Respondent Status]#$respondentLine = "Excluded";   
  RETURN 
  HASH_MERGE(
	$item, 
	HASH(
	  "Assigned Teams", 
	  IF($excluded = TRUE, NULL, $updatedAssigned)
	)
  )
)

FormulaDrivenChangeFieldValues.FieldValuesFormula:
LET $hashed := 
ARRAY_TO_HASH_GROUPED(
  [Document Data], 
  $(i, item) HASH_GET($item, "Respondent Source RecordID"),
  $(i, item) 
  ARRAY(
	HASH_GET($item, "Respondent Source Line"), 
	HASH_GET($item, "Assigned Teams")
  )
); 

LET $map :=  
INDEXES_TO_KEYS( 
  ARRAY_SELECT(
    HASH_KEYS($hashed), $(i, item) 
    LET $transposed := ARRAY_TRANSPOSE(HASH_GET($hashed, $item));
    RETURN INDEXES_TO_KEYS(ARRAY_LAST($transposed), ARRAY_FIRST($transposed))
  ), 
  HASH_KEYS($hashed)
);

LET $updates := 
ARRAY_SELECT(
  HASH_KEYS($hashed), $(d, recordID)
  LET $data := HASH_GET($hashed, $recordID);
  LET $maxLine := GREATEST(HASH_KEYS($data)); 

  LET $assignEachLine := 
  ARRAY_SELECT( 
	RANGE(1, $maxLine), $(i, line) 
	HASH_GET($data, $line)
  );
  RETURN  

  HASH(
    "Assigned Teams",
      $assignEachLine
  )
);

RETURN ARRAY_FIRST($updates)

