	ARRAY(
		ARRAY(
			EBMS_GET_CURRENT_USER(), 
			NOW(),
			"<p>" & HTMLENCODE([New Message]) & "</p>"
		)
	)
);

RETURN 
ARRAY_WHERE(
	$data, $(i,item) 
	AND(
		$item <> ARRAY(),
		$item <> NULL()
	)
)

LET $cleanData := 
ARRAY_REVERSE(
  ARRAY_WHERE(
    [Notes], $(i, item) 
    ARRAY_INDEX($item, 3) <> NULL()
  )
);

LET $formatted := 
ARRAY_SELECT(
	$cleanData, $(i, item) 
	LET $user := ARRAY_INDEX($item, 1); 
	LET $datePosted := ARRAY_INDEX($item, 2); 
	LET $message := IFERROR(ARRAY_INDEX($item, 3), "");
	LET $attachments := ARRAY_INDEX($item, 4);
	LET $isSystem := ROLE("system") == $user;
	LET $isCurrentUser := EBMS_GET_CURRENT_USER() == $user;
	LET $userDisplayName := EBMS_GET_ROLE_DISPLAYNAME($user); 
	LET $datePostedFormatted := DATEFORMAT($datePosted, "dd-MMM-yyyy hh:mm tt");
	//LET $class := IF($isCurrentUser, "right-top", "left-top");
	LET $colour := IF($isCurrentUser, "#CBE8FF" , "#E2E2E2");
	LET $classes := 
    IF( 
	  $isSystem = TRUE, 
      "filenotes-system-message",
      "talk-bubble"
	);

    //----
    LET $username := "<span style=""font-weight: 600;"">" & HTMLENCODE($userDisplayName) & "</span>";
    LET $date := "<span style=""" & IF($isSystem = TRUE, "float: right; padding-right: 15px;", /*"font-style: italic;"*/) & """> " & $datePostedFormatted & " </span>";
    LET $message := "<span style=""white-space: break-spaces; display: block; padding-bottom: 10px; text-align: left; word-wrap: break-word;"">" & $message & "</span>";
    LET $noteAdded := "<span style='background-color: " & $colour & ";' class='filenotes-noteadded'>" & IF($isSystem = TRUE, "SYSTEM MESSAGE", "NOTE ADDED") & "</span>";
    //----
    RETURN 
	IF( 
	  //System message
	  $isSystem = TRUE, 
	  "<div class=""" & $classes & """>" & $message & $noteAdded & $date & "</div>",

	  //User message
      "<div class=""" & $classes & """>" & $message & $noteAdded & $username & $date & "</div>"
	)
);

RETURN 
  IFERROR(
  	ARRAY_JOIN(
  		$formatted, ""
  	), 
  	NULL()
  ) & "<span class='endtag'></span>"

//$eventargs = ARRAY(businessnumbertype, businessnumber)
LET $businessNumberType := COALESCE(ARRAY_INDEX($eventargs, 1), [Business Number Type]);
LET $businessNumber := COALESCE(ARRAY_INDEX($eventargs, 2), [BusinessNumber]);

LET $format := 
DB_LOOKUP(
  "Business Number Format", 
  "Settings - Vendor", 
  "[RecordID] = 1 AND [Business Number Type] = " & DB_ENCODECONSTANT($businessNumberType)
);

LET $formatLength := LEN(REPLACESUBSTRING(" ", $format, ""));

LET $data := $businessNumber;
LET $isNumber := ISERROR(N(REPLACESUBSTRING(" ", $businessNumber, ""))) <> TRUE;
LET $filtered := ARRAY_JOIN(SPLIT($data, " "), "");
RETURN
IF( 
  $format = NULL, 
    $data,
  $isNumber,
    NUMBERFORMAT(LEFT($filtered, $formatLength), $format),
  $data
)

MailMerge.ResultFileNameTemplate:
{
LET $fileName := "Unsuccessful Letter_" & URLENCODE([Procurement Title]) & "-" & URLENCODE([Company Name]);

LET $illegalChars := 
ARRAY_DISTINCT(
	ARRAY_SELECT(
	  REGEX_MATCHES($fileName, "[\*\\\:\<\>\?\/\|\""]"), $(i, item) HASH_GET($item, "0")
	)
);

LET $replacesubstrings := $(needles, index, haystack, replacement) (
	LET $newHaystack := REPLACESUBSTRING(ARRAY_INDEX($needles, $index), $haystack, $replacement);

	RETURN 
  	IF(
		ARRAY_LENGTH($needles) <= $index,
		$newHaystack,
		CALL($replacesubstrings, $needles, $index + 1, $newHaystack, $replacement)
	)
);

LET $cleaned :=  CALL($replacesubstrings, $illegalChars, 1, $fileName, "");

RETURN 
IF( 
	$illegalChars = ARRAY(), 
	$fileName & ".docx", 
	$cleaned & ".docx"
)
}

{
LET $fileName := "Successful Letter_" & URLENCODE([Procurement Title]) & "-" & URLENCODE([Company Name]);

LET $illegalChars := 
