}

FieldEntry.Formula:
LET $data := 
ARRAY_SELECT( 
  EACH [Filename], $(ln, documentName) 
  HASH(
    "File Name",$documentName,
    "Respondent Name",[Company Name]#$ln,
    "VP Response RecordID", [VP Response ID]#$ln,
    "Respondent Source RecordID",[RecordID]#$ln,
    "Respondent Source Line", [LineNumber]#$ln,
    "Provided via Sourcing",[Provided via Sourcing]#$ln,
    "Document Status",[Document Status]#$ln,
    "Document ID", [Document ID]#$ln,
    "URL", [Attachment Direct URL]#$ln,
    "Response ID", [Integrated Response ID]#$ln,
    "Assigned Teams", [Assigned Teams]#$ln
  )
);

LET $filtered := 
ARRAY_WHERE(
  $data, $(i, item) 
  LET $fileName := HASH_GET($item, "File Name");  
  LET $documentStatus := HASH_GET($item, "Document Status");
  LET $fileNameNoSuffix := LEFT($fileName, IFERROR(FIND(".", $fileName), NULL) - 1);
  //LET $hidePrice := [Price Hidden During Process]; 
  LET $isNoPriceDocument := EBMS_CONTAINS($fileName, "no price.PDF");
  LET $isNotMainResponseDoc := ARRAY_INDEXOF($responseVPIds, $fileNameNoSuffix) < 0;

  RETURN
  AND(
	$fileName <> NULL, 
    //$documentStatus <> "Hidden", 
    $documentStatus <> "Removed", 
	IF( 
	  //$hidePrice = TRUE, 
	  //OR(
	    //$isNoPriceDocument,
	    //$isNotMainResponseDoc
	  //), 

	  1=1,//$hidePrice <> TRUE, 
	  NOT($isNoPriceDocument)
    ),
    NOT(
	  AND(
		RIGHT($fileName, 4) = ".zip",
	    LEFT($fileName, 8) = "Response"
	  )
	)
  )
); 

LET $hashed :=  
ARRAY_SELECT(
  $filtered, $(i, item) 
  INDEXES_TO_KEYS(
	$item, 
    $documentDataKeys
  )
);

RETURN $filtered

