FieldEntry.Formula:
LET $data := EACH [Assigned Teams];
LET $teamsToRemove := 
HASH_GET(
  $eventargs, 
  ARRAY_FIRST(HASH_KEYS($eventargs))
); 

LET $removeAll := HASH_GET($eventargs, "Remove All") == TRUE; 

RETURN 
ARRAY_SELECT(
  $data, $(i, assignmentList) 
  IF(
	$removeAll == TRUE, 
	NULL, 

    ARRAY_WHERE(
	  $assignmentList, $(a, assignment)
	  ARRAY_INDEXOF($teamsToRemove, $assignment) == NULL
    )
  )
)
