Column.Formula:
LET $current := ARRAY([EFormType], [RecordID]);
LET $descriptionColumns := 
HASH(
	"Contract",  					"Contract Number", 
	"Vendor", 						"Company Name",
	"Procurement", 					"Procurement Number",
	"Project", 						"Project Number",
    "Evaluation", 		    		"Description",
  	"Information Request",  		"File No.", 
	"Subpoena", 					"File No.",
	"Privacy Incident", 			"File No."
); 

LET $altFormName:= HASH(
  	"Insurance",	"Compliance"
  );

LET $getLabelText := $(eFormType, recordID)
(
  	LET $targetColumn := HASH_GET($descriptionColumns, $eFormType);
  	RETURN 
  	IF( $targetColumn = NULL, NULL,
		LET $isthisRecord := ARRAY($eFormType, $recordID) = $current;
		LET $title:= 
		IF( $isthisRecord = TRUE, 
			CURRENT_VALUE($targetColumn),
			ARRAY_FIRST(DB_LOOKUP(
				ARRAY($targetColumn),
				$eFormType, 
				"[RecordID] FAST_EQUAL_TO " & $recordID
			))
		  );
		//shortern the descriptive label/ title
		LET $charCount := LEN($title);
		LET $titleArray := SPLIT($title, " ");
		LET $wordCount := ARRAY_LENGTH($titleArray);

		LET $charLimit := 30; 
		LET $wordLimit := ARRAY_LENGTH( SPLIT( LEFT($title, $charLimit), " ") );

	  RETURN //ARRAY_TAKE($titleArray, $wordLimit)
		IF(
			$wordCount <= $wordLimit, $title,
			ARRAY_JOIN(
				ARRAY_TAKE($titleArray, $wordLimit), " "
			)& " ..."
		)
	  )

); 

LET $getReadAccess := $(eFormType, recordID)
( EBMS_GET_EFORMRECORD_PERMISSION($eFormType, $recordID, "read"));

LET $currentDes := CALL($getLabelText, [EFormType], [RecordID]);
LET $currentAccess := FALSE;

RETURN
IF(
	[Parent Structure] <> NULL, [Parent Structure],
  	(

	  LET $parent := 
	  IF( 
		[RecordID] > 0, 
		(
		  LET $record := EBMS_GET_PARENT_RECORD(ARRAY_FIRST($current), ARRAY_INDEX($current, 2));
		  RETURN 
			  IF(  ARRAY_FIRST($record) == NULL, NULL,
				ARRAY_CONCAT( 
				  $record, 
				  CALL($getLabelText, ARRAY_FIRST($record), ARRAY_INDEX($record, 2)),
				  CALL($getReadAccess, ARRAY_FIRST($record), ARRAY_INDEX($record, 2))
				  )

			  )
	  )); 

	  LET $gParent := 
	  IF( 
		 $parent <> NULL, 
		(
		  LET $record := EBMS_GET_PARENT_RECORD(ARRAY_FIRST($parent), ARRAY_INDEX($parent, 2));
		  RETURN 
			  IF(  ARRAY_FIRST($record) == NULL, NULL,
				ARRAY_CONCAT( 
				  $record, 
				  CALL($getLabelText, ARRAY_FIRST($record), ARRAY_INDEX($record, 2)),
				  CALL($getReadAccess, ARRAY_FIRST($record), ARRAY_INDEX($record, 2))
			  )
		  )
	  )); 

	  LET $hash :=  
	  HASH_MERGE(
		  IF( $parent <> NULL, HASH("parent", 	$parent)),
		  IF( $gParent <> NULL, HASH("gparent", 	$gParent))
	  );

	  LET $parentStructure := 	//2nd instance
	  IF( 
		  ARRAY_LENGTH(HASH_KEYS($hash)) >0, $hash, NULL
	  );

	  LET $parentData:= 
	  ARRAY_WHERE(
		ARRAY(
			$gParent, 
			$parent, 
			ARRAY_CONCAT($current, $currentDes, $currentAccess),
		), $(i,item) $item <> NULL
	   );


	  LET $parentChain:=
		ARRAY_SELECT(
			  $parentData, $(i, item)
			  ARRAY_TAKE($item, 2)
	   );


	  LET $parentLinks :=
	  ARRAY_SELECT(
		  $parentData, $(i, item) 
			  LET $eFormType 		:= ARRAY_FIRST($item);
			  LET $recordID 		:= ARRAY_INDEX($item, 2);
			  LET $descriptiveLabel := ARRAY_INDEX($item, 3);
			  LET $hasReadAccess	:= ARRAY_INDEX($item, 4);
			  LET $URL	:= "records/" & URLENCODE($eFormType) & "/" & $recordID ;
			  LET $showEFormType := $eFormType <> "Vendor";

			  LET $labelColour := 
				  IF(
				   AND($eFormType = [EFormType], $recordID = [RecordID]),
					  "background: #ffffff; color: #5D5D5D;",
					 $hasReadAccess = TRUE,
					  "background: #ffffff; color: #4D87B7;",
					  "background: #ffffff; color: #5D5D5D;"

				  );



			  LET $labeltext:= 
					  IF( $showEFormType == TRUE, " " & COALESCE(HASH_GET($altFormName, $eFormType), $eFormType) ) &
					  IF(NOT(ISBLANK($descriptiveLabel)), " " & $descriptiveLabel);

			  LET $label :=  
				  "<span style=""margin-left: 5px;" & $labelColour & """>" &
				  HTMLENCODE($labeltext) & 
				  "</span>";

			  RETURN 
			  IF( 
				  $hasReadAccess == TRUE, 
				  "<a style=""text-decoration-line: none;"" href=" & HTMLENCODE($URL) & ">" & $label & "</a>",
				  $label 
			  )
	  );

	   LET $separator :=   "<svg style=""vertical-align: bottom; margin-left: 5px; height: 18px;"" xmlns=""http://www.w3.org/2000/svg"" viewBox=""0 0 256 512""><path fill=""#5D5D5D"" d=""M246.6 278.6c12.5-12.5 12.5-32.8 0-45.3l-128-128c-9.2-9.2-22.9-11.9-34.9-6.9s-19.8 16.6-19.8 29.6l0 256c0 12.9 7.8 24.6 19.8 29.6s25.7 2.2 34.9-6.9l128-128z""/></svg>";

	  LET $breadcrumb := "<div style=""height: 23px; padding-left: 5px; font-size: 10pt; color: #5D5D5D; "">You are here:" & ARRAY_JOIN($parentLinks, $separator) & "</div>";

	  RETURN 
	  HASH(
		  "Parent Structure",	$parentStructure,
		  "Parent Data", 		$parentData,
		  "Parent Chain",		$parentChain,
		  "Breadcrumb",		$breadcrumb
		)
	 )
)

FieldEntry.Formula:
LET $iterations := ARRAY_LENGTH([Likelihood Array]) * ARRAY_LENGTH([Consequence Array]);
LET $likelihoodLength := ARRAY_LENGTH([Likelihood Array]); 
LET $consequenceLength := ARRAY_LENGTH([Consequence Array]); 

LET $result := 
ARRAY_SELECT(
	RANGE(0, $iterations), $(i, item)
	ARRAY_INDEX([Likelihood Array], CEILING($i / $consequenceLength))
);

RETURN $result

LET $iterations := ARRAY_LENGTH([Likelihood Array]) * ARRAY_LENGTH([Consequence Array]);
LET $likelihoodLength := ARRAY_LENGTH([Likelihood Array]); 
LET $consequenceLength := ARRAY_LENGTH([Consequence Array]); 

LET $result := 
ARRAY_SELECT(
	RANGE(0, $iterations), $(i, item)
	LET $magicNumber := FLOOR(($i - 1) / $consequenceLength) * $consequenceLength;	
	RETURN ARRAY_INDEX([Consequence Array], $i - $magicNumber));

RETURN $result

LET $likelihoods := EACH [Likelihood];
LET $likelihoodLength := ARRAY_LENGTH([Likelihood Array]);
LET $consequenceLength := ARRAY_LENGTH([Consequence Array]);

RETURN
ARRAY_SELECT(
	RANGE(0, ARRAY_LENGTH($likelihoods)), $(i, item) 
	LET $x := ARRAY_INDEXOF([Likelihood Array], [Likelihood]#$i);
	LET $y := ARRAY_INDEXOF([Consequence Array], [Consequence]#$i);
	RETURN 
	FLOOR((($x / $likelihoodLength) * ($y / $consequenceLength)) * 100)
)

Binding.Expression:
LET $totalW := 35 * 20 - 2;
LET $totalH := 20 * 10 - 8; 
LET $headingHeight := 24;

LET $colours := SPLIT([Risk Colours], ", ");
LET $textColours := SPLIT([Risk Text Colours], ", ");
LET $ratings := SPLIT([Risk Ratings], ", "); 
LET $fontsize := "13px";

LET $ratingtoColourMap :=
HASH(
	ARRAY_INDEX($ratings, 1), 1,
	ARRAY_INDEX($ratings, 2), 2,  
	ARRAY_INDEX($ratings, 3), 3, 
	ARRAY_INDEX($ratings, 4), 4
);

LET $map := 
ARRAY_TRANSPOSE(
	ARRAY(
		EACH [Consequence],
		EACH [Likelihood],
		EACH [Risk Rating],
		EACH [Risk Score]
	)
);

LET $consequences := [Consequence Array];
LET $likelihoods := ARRAY_REVERSE([Likelihood Array]);

// calculate actual col/row width/height
LET $w := $totalW / (ARRAY_LENGTH($consequences) + 1);
LET $h := ($totalH - $headingHeight) / (ARRAY_LENGTH($likelihoods));

LET $consequenceRow := 
"<tr>" & 
	"<td style=""background-color: white; width:" & $w & "px;height:" & $headingHeight & "px""></td>" 
	& ARRAY_JOIN(ARRAY_SELECT($consequences, $(col, consequence) (
	"<td style=""background-color: white; text-align: center; font-size: " & $fontsize & "; width:" & $w & "px"">" & HTMLENCODE($consequence) & "</td>")
	)) & 
"</tr>";

RETURN
 "<table style=""border-collapse:collapse"" cellpadding=""0"" cellspacing=""0"">" & 
	ARRAY_JOIN(ARRAY_SELECT(
		$likelihoods,
		$(row, likelihood) (
			"<tr><td style=""background-color: white; padding-right: 10px; font-size: " & $fontsize & "; text-align: right; height:" & $h & "px"">" & HTMLENCODE($likelihood) & "</td>" 
			& ARRAY_JOIN(ARRAY_SELECT(
				$consequences,
				$(col, consequence) (
					LET $key   := ARRAY($likelihood, $consequence);
					LET $matches := ARRAY_WHERE($map, $(i, item) ARRAY(ARRAY_INDEX($item, 2), ARRAY_INDEX($item, 1)) = $key);
					LET $riskRating := ARRAY_INDEX(ARRAY_FIRST($matches), 3);
					LET $riskScore := ARRAY_INDEX(ARRAY_FIRST($matches), 4);
					LET $colorIndex := HASH_GET($ratingtoColourMap, $riskRating);
					LET $color := ARRAY_INDEX($colours, $colorIndex);
					LET $textColor := ARRAY_INDEX($textColours, $colorIndex);

					LET $style := "display: inline-block; width: 49%; cursor: pointer; text-decoration: none; color: " & HTMLENCODE($textColor) & ";font-size: " & $fontsize & ";";

					RETURN "<td style=""text-align:center;border:0px;background-color:" & HTMLENCODE($color) & """> <div style=""" & HTMLENCODE($style) & """>" & HTMLENCODE($riskRating) & "</div></td>"

				)
			)) & "</tr>"
		)
	)) & 
	$consequenceRow & 
"</table>"
