FieldEntry.Formula:
ARRAY_WHERE(
	ARRAY_DISTINCT(
		ARRAY_CONCAT(
			[Treatment ID Array], 
			ARRAY([Temp])
		)
	), 
	$(i, item) 
	$item <> NULL()
)

LET $data := ARRAY_DISTINCT([Related Risk ID Array]);
LET $filteredData := ARRAY_WHERE($data, $(i, item)
	AND(
		$item <> [Select Risk ID],
		$item <> NULL()
	)
);

RETURN
ARRAY_CONCAT($filteredData, [Select Risk ID])

ARRAY_WHERE(
  ARRAY_DISTINCT(
	ARRAY_CONCAT(
  	  [Control ID Array],
  	  ARRAY([Temp Control RecordID])
	)
  ), $(i, item)
  $item <> NULL()
)

ARRAY_WHERE(
  ARRAY_DISTINCT(
	ARRAY_CONCAT(
  	  [Control ID Array],
  	  ARRAY([Select Control ID])
	)
  ), $(i, item)
  $item <> NULL()
)

ARRAY_WHERE(
	ARRAY_DISTINCT(
		ARRAY_CONCAT(
			[Treatment ID Array], 
			ARRAY([Temp])
		)
	), 
	$(i, item) 
	$item <> NULL()
)

ARRAY_WHERE(
	ARRAY_DISTINCT(
		ARRAY_CONCAT(
			[Treatment ID Array], 
			ARRAY([Select Treatment ID])
		)
	), 
	$(i, item) 
	$item <> NULL()
