FieldEntry.Formula:
LET $severities := EACH [Impact Severity Value]; 
LET $greatestSeverity := GREATEST($severities);
LET $line := ARRAY_INDEXOF($severities, $greatestSeverity);
RETURN ARRAY_INDEX(EACH [Impact Type], $line)

LET $severities := EACH [Impact Severity Value]; 
LET $greatestSeverity := GREATEST($severities);
LET $line := ARRAY_INDEXOF($severities, $greatestSeverity);
RETURN ARRAY_INDEX(EACH [Impact Category], $line)

LET $severities := EACH [Impact Severity Value]; LET $greatestSeverity := GREATEST($severities);
LET $targetRiskDescription := IF( $greatestSeverity = 1, "Minor Risk", $greatestSeverity = 2, "Moderate Risk", $greatestSeverity = 3, "Major Risk", $greatestSeverity = 4, "Extreme Risk" );
RETURN DB_LOOKUP( $targetRiskDescription, "Risk Type", "[Type] = " & DB_ENCODECONSTANT([Key Consequence Type]) & " AND " & "[Category] = " & DB_ENCODECONSTANT([Key Consequence Category]) )
