LET $result :=
ARRAY_FIRST(
	ARRAY_FIRST(
		DB_REPORT(
			ARRAY("SUM([This Claim Subtotal])"), 
			"Payment Certificate", 
			HASH("LineNumber", 1, "Status", "Approved"),
		  	HASH("parent", ARRAY("Contract", [Contract RecordID]))
		)
	)
);

RETURN 
COALESCE(
	$result, 0
)

IF( 
	[Section Heading] == TRUE, NULL(),

	/*AND(
		[Section Heading] <> TRUE,
		[% Complete] = NULL(), 
	), 0, */

	ROUND(IFERROR([Item Claimed Amount] / [Item Contracted Amount] * 100, 0),2)
)

IF(
	[Section Heading] ,
	ARRAY_LENGTH(
		ARRAY_WHERE(
			ARRAY_TAKE(
				EACH [Section Heading],
				[LineNumber]
			), $(i, item) $item = TRUE
		)
	),

	//thing
	ARRAY_LENGTH(ARRAY_WHERE(EACH [Section Heading],$(i, item) AND($item = TRUE, $i <= [LineNumber])))


)

LET $claimID := DB_LOOKUP( "ClaimID", "Payment Certificate", HASH("LineNumber", 1, "Status", "Approved"), HASH("parent", ARRAY("Contract", [Contract RecordID]), "sort", "[ClaimID] DESC") );
LET $temp := DB_REPORT( ARRAY( "[Amount Claimed]", "[Quantity Claimed]" ), // ARRAY("SUM([Value of Completed Work])"), "Payment Certificate", HASH("Schedule Item", [ID], "ClaimID", $claimID), HASH("parent", ARRAY("Contract", [Contract RecordID])) );
RETURN INDEXES_TO_KEYS( ARRAY_INDEX($temp, 1), ARRAY( "Amount Claimed", "Quantity Claimed" ) )

ABS(
