IF( NOT(ISBLANK([Description of Work - Rate Only])),
  [Section Heading ID - Rate Only] & "." & ARRAY_LENGTH(ARRAY_WHERE(EACH [Section Heading ID - Rate Only],$(i, item) AND($i <= [LineNumber], $item = [Section Heading ID - Rate Only]#[LineNumber]))) - 1
  )

