MergeField.Formula:
LET $lumpSumBoth :=
	ARRAY_TRANSPOSE(
		ARRAY(   
			EACH [ID], 

			EACH [Description of Work],

			EACH [Qty or Amt],

			LET $estimatedQuantity := EACH [Estimated Quantity];
			RETURN	ARRAY_SELECT($estimatedQuantity, $(i, item) NUMBERFORMAT($item, "N2"\)),

			EACH [Unit],


			LET $rate := EACH [Rate $];
			RETURN	ARRAY_SELECT($rate, $(i, item) NUMBERFORMAT($item, "C")),


		  	LET $itemContractedAmount := EACH [Item Contracted Amount];
			RETURN	ARRAY_SELECT($itemContractedAmount, $(i, item) NUMBERFORMAT($item, "C")),

		  	LET $itemClaimedAmount := EACH [Item Claimed Amount];
			RETURN	ARRAY_SELECT($itemClaimedAmount, $(i, item) NUMBERFORMAT($item, "C")),

			LET $percentComplete := EACH [% Complete];
			RETURN	ARRAY_SELECT($percentComplete, $(i, item) NUMBERFORMAT($item, "N2"\) & "%"),

			LET $itemRemainingAmount := EACH [Item Remaining Amount];
			RETURN	ARRAY_SELECT($itemRemainingAmount, $(i, item) NUMBERFORMAT($item, "C"))

		)
	);

LET $lumpSumBoth :=
	ARRAY_SELECT(
	  	$lumpSumBoth, $(i, item) 
	  	LET $sectionItem := ARRAY_INDEX($item, 1);
	  	LET $itemDescription := ARRAY_INDEX($item, 2);
	  	LET $values :=	
	  		ARRAY_SELECT(
		  		ARRAY_SKIP($item, 2),
		  			$(j, jtem)
				  	IF(
					  [Section Heading]#$i = TRUE,
					  NULL(),
					  $jtem
					)
				);
		  		RETURN 
			  	ARRAY_CONCAT(
				  	$sectionItem,
				  	$itemDescription,
		  			$values
			)
	 );

RETURN  $lumpSumBoth

LET $lumpSumAmount :=
	ARRAY_TRANSPOSE(
		ARRAY(   
			EACH [ID], 

			EACH [Description of Work],

		  	LET $itemContractedAmount := EACH [Item Contracted Amount];
			RETURN	ARRAY_SELECT($itemContractedAmount, $(i, item) NUMBERFORMAT($item, "C")),

		  	LET $itemClaimedAmount := EACH [Item Claimed Amount];
			RETURN	ARRAY_SELECT($itemClaimedAmount, $(i, item) NUMBERFORMAT($item, "C")),

			LET $percentComplete := EACH [% Complete];
			RETURN	ARRAY_SELECT($percentComplete, $(i, item) NUMBERFORMAT($item, "N2"\) & "%"),

			LET $itemRemainingAmount := EACH [Item Remaining Amount];
			RETURN	ARRAY_SELECT($itemRemainingAmount, $(i, item) NUMBERFORMAT($item, "C"))

		)
	);

LET $lumpSumAmount :=
	ARRAY_SELECT(
	  	$lumpSumAmount, $(i, item) 
	  	LET $sectionItem := ARRAY_INDEX($item, 1);
	  	LET $itemDescription := ARRAY_INDEX($item, 2);
	  	LET $values :=	
	  		ARRAY_SELECT(
		  		ARRAY_SKIP($item, 2),
		  			$(j, jtem)
				  	IF(
					  [Section Heading]#$i = TRUE,
					  NULL(),
					  $jtem
					)
				);
		  		RETURN 
			  	ARRAY_CONCAT(
				  	$sectionItem,
				  	$itemDescription,
		  			$values
			)
	 );

RETURN  $lumpSumAmount

LET $claimsData :=
		DB_LOOKUPALL(
			ARRAY(
			  	"Schedule Item - Rate Only",
				"Claim Quantity - Rate Only", 
				"Claim Amount - Rate Only"
			),
			"Payment Certificate",
			"[Status] = 'Approved' AND [Contract RecordID] = " & DB_ENCODECONSTANT([Contract RecordID])
		);

LET $claims:= ARRAY_TO_HASH_GROUPED(
					$claimsData,
					$(i,item) ARRAY_FIRST($item),
					$(i,item) ARRAY_SKIP($item, 1)
);
LET $claims:=
ARRAY_SELECT(
	HASH_KEYS($claims), $(k,key) //$key is the line id
	LET $data:= ARRAY_TRANSPOSE(HASH_GET($claims, $key));
	RETURN
	ARRAY_CONCAT( $key,
		ARRAY_SELECT($data, $(i,item) SUM($item))
	)
);
LET $claimsHash := ARRAY_TO_HASH_GROUPED(
					$claims,
					$(i,item) ARRAY_FIRST($item),
					$(i,item) ARRAY_SKIP($item, 1)
);


LET $rateOnlyItems :=
	ARRAY_TRANSPOSE(
		ARRAY(   
			EACH [ID - Rate Only], 

			EACH [Description of Work - Rate Only],

			EACH [Unit - Rate Only],

			LET $rate := EACH [Rate $ - Rate Only];
			RETURN	ARRAY_SELECT($rate, $(i, item) NUMBERFORMAT($item, "C"))
		)
	);

LET $rateOnlyItems :=
	ARRAY_SELECT(
	  	$rateOnlyItems, $(i, item) 
	  	LET $sectionItem := ARRAY_INDEX($item, 1);
	  	LET $itemDescription := ARRAY_INDEX($item, 2);
	  	LET $claims2:= ARRAY_FIRST(HASH_GET($claimsHash, $sectionItem));
	  	LET $values :=	
	  		ARRAY_SELECT(
		  		ARRAY_CONCAT(
						ARRAY_SLICE($item, 3,2), //unit and rate
						NUMBERFORMAT(ARRAY_INDEX($claims2, 1), "##.000"),//claim qty
				  		NUMBERFORMAT(ARRAY_INDEX($claims2, 2), "C")//claim amt
					),	
		  			$(j, jtem)
				  	IF(
					  [Section Heading - Rate Only]#$i = TRUE,
					  NULL(),
					  $jtem
					)
				);
		  		RETURN 
			  	ARRAY_CONCAT(
				  	$sectionItem,
				  	$itemDescription,
		  			$values
			)
	 );

RETURN  $rateOnlyItems

