			LET $percentComplete := EACH [% Complete];
			RETURN	ARRAY_SELECT($percentComplete, $(i, item) NUMBERFORMAT($item, "N2"\) & "%"),

			LET $itemRemainingAmount := EACH [Item Remaining Amount];
			RETURN	ARRAY_SELECT($itemRemainingAmount, $(i, item) NUMBERFORMAT($item, "C"))

		)
	);

LET $lumpSumQuantity :=
	ARRAY_SELECT(
	  	$lumpSumQuantity, $(i, item) 
	  	LET $sectionItem := ARRAY_INDEX($item, 1);
	  	LET $itemDescription := ARRAY_INDEX($item, 2);
	  	LET $values :=	
	  		ARRAY_SELECT(
		  		ARRAY_SKIP($item, 2),
		  			$(j, jtem)
				  	IF(
					  [Section Heading]#$i = TRUE,
					  NULL(),
					  $jtem
					)
				);
