Binding.Expression:
LET $set := 
ARRAY(
  "Schedule", 
  "Functions", 
  "Attachments"
); 

LET $filtered := ARRAY_WHERE($set, $(i, item) $item <> NULL);

RETURN ARRAY_SELECT($filtered, $(i,item) HASH("value", $item, "text", ""))

LET $matches := 
DB_LOOKUPALL(
	ARRAY("RecordID", "ClaimID"), 
	"Payment Certificate", 
	"[% Movement] > '0' AND [Status] = 'Approved' AND [Schedule Item]=" & DB_ENCODECONSTANT([ID]), 
	HASH("sort", "[RecordID] ASC", "parent", ARRAY("Contract", [Contract RecordID]))
);

LET $links := ARRAY_JOIN(
    ARRAY_SELECT(
	    $matches, 
		$(i, item) "<a href=""" & HTMLENCODE(EBMS_GET_EFORMRECORD_URL("Payment Certificate", ARRAY_INDEX($item, 1))) & """>" & HTMLENCODE(ARRAY_INDEX($item, 2)) & "</a>"
	), ", "
);

RETURN

"<div style=""width: 100; height: 100%; text-align: right; background-color: #FFFFFF; border: 0px solid #FFFFFF;"">" & COALESCE($links, "None") & "</div>"

LET $claimID := [ClaimID];

// claims
LET $claimsRaw := 
DB_LOOKUPALL(
	ARRAY("Schedule Item", "RecordID", "ClaimID", "Amount Claimed"), 
	"Payment Certificate", 
	HASH("ClaimID", $claimID),
	HASH("sort", "[RecordID] ASC", "parent", ARRAY("Contract", [Contract RecordID]))
);

LET $claims := ARRAY_DISTINCT(ARRAY_SELECT($claimsRaw, $(i, item) ARRAY(ARRAY_INDEX($item, 2), ARRAY_INDEX($item, 3)))); // Claim Number, Invoice Number

LET $claimHash := ARRAY_TO_HASH(
	$claimsRaw,
	$(i, item) ARRAY("schedule", ARRAY_INDEX($item, 1), ARRAY_INDEX($item, 2)),
	$(i, item) ARRAY_INDEX($item, 4)
);

// schedule of rates items
LET $scheduleItems := ARRAY_TRANSPOSE(
	ARRAY(
		ARRAY_WHERE(EACH [ID], $(i, item) AND( [Section Heading]#$i <> TRUE, [ID]#$i <> NULL ) ), 
		ARRAY_WHERE(EACH [Item Contracted Amount], $(i, item) AND([Section Heading]#$i <> TRUE, [ID]#$i <> NULL ) )
	)
);

LET $unioned := ARRAY_SELECT($scheduleItems, $(i, item) ARRAY("schedule",  ARRAY_INDEX($item, 1), ARRAY_INDEX($item, 2), ARRAY_INDEX($item, 1)));

LET $claimData := ARRAY_SELECT(
	$claims,
	$(ci, claim) ARRAY_SELECT(
		$unioned, 
		$(i, item) COALESCE(HASH_GET($claimHash, ARRAY(ARRAY_INDEX($item, 1), ARRAY_INDEX($item, 2), ARRAY_INDEX($claim, 1))), 0)
	)
);

LET $remainingDataRow := ARRAY_SELECT(
	$unioned,
	$(i, item) (
		LET $spent := SUM(
			ARRAY_SELECT(
				$claims, 
				$(ci, claim) HASH_GET($claimHash, ARRAY(ARRAY_INDEX($item, 1), ARRAY_INDEX($item, 2), ARRAY_INDEX($claim, 1)))
			)
		);
		LET $amount := ARRAY_INDEX($item, 3);
		RETURN $amount - $spent
	)
);

LET $rows := ARRAY_CONCAT($claimData, ARRAY($remainingDataRow));

LET $seriess := ARRAY_CONCAT(
	ARRAY_SELECT(
		$claims, $(i, item) 
		HASH("label", "Progress Payment - " & ARRAY_INDEX($item, 2))
	),
	ARRAY(
		HASH("label", "Remaining")
	)
);

RETURN HASH(
    "series", $seriess,
    "panes", ARRAY(
        HASH(
            "title",  "Lump Sum Items Claimed (%)",
            "labels", ARRAY_SELECT($unioned, $(i, item) ARRAY_INDEX($item, 4)),
            "layers", ARRAY_SELECT(
				$rows, 
                $(r, row) HASH("type", "bar", "data", $row)
            )
        )
    )
)

LET $paymentsSupported := 
