LET $set := [Module Data];

RETURN
ARRAY_SELECT(
	$set,	  
  	$(i, item) 
	  LET $name := ARRAY_INDEX($item, 1); 
	  LET $eform := ARRAY_INDEX($item, 2); 

	  RETURN
	  HASH(
		  "value", 	$eform,
		  "text", 	IF($eform = "Global Settings", $name, $eform)//$name
	  )
)

FormulaDrivenChangeFieldValues.FieldValuesFormula:
LET $columns := ARRAY( "Reminder Purpose", "Reminder Enabled", "Reminder Type", "Reminder Date", "End Date Type", "End Date", "Filter String", "Description", "Context Form", "RecordID" );
LET $data := DB_LOOKUP( $columns, "Reminder", "[Unique Reference] = " & DB_ENCODECONSTANT([Reminder Selection]) );
LET $hashed := INDEXES_TO_KEYS($data, $columns);
RETURN HASH( "Display - Description of Reminder", ARRAY(HASH_GET($hashed, "Description")), "Display - Reminder Summary", ARRAY(HASH_GET($hashed, "Description")), "Display - Filter String", ARRAY(HASH_GET($hashed, "Filter String")), "Display - Reminder Target EForm", ARRAY(HASH_GET($hashed, "Context Form")), "Display - Reminder Enabled", ARRAY(HASH_GET($hashed, "Reminder Enabled")), "Display - Reminder RecordID", ARRAY(HASH_GET($hashed, "RecordID")), "Mode", "Edit Existing" )

