records/Settings%20-%20Automation/1?view={
IF( 
  [Email Type] = "Reminder Rules", 
    "Record Reminders", 

  [Email Type] = "Custom Reminders", 
    "Custom Reminders", 

  [Email Type] = "Status Updates", 
    "Status Updates"
) & "&$Navigation=" & [Email Type]}

FieldEntry.Formula:
ARRAY_SELECT(
	EACH [DG - Target EForm], $(i, item) 
	(

		LET $columns := [Output Columns]#$i;
		LET $lookupColumns := ARRAY_CONCAT($columns, "RecordID");
		LET $alias := [DG - Column Alias]#$i;
		LET $numberOfColumns := ARRAY_LENGTH($columns); 
		LET $widths := SPLIT([Columns Widths]#$i, ",");
		LET $filter := [DG - Filter String]#$i;
		LET $targetEForm := [DG - Target EForm]#$i;
		LET $showViewLink := [DG - Show View Link]#$i;
		LET $sortData := [DG - Sort Data]#$i;
		LET $sort := [DG - Order By]#$i;
		LET $sortType := [DG - Order By Type]#$i;

		LET $sortString := 
		IF( 
			AND(
			  $sortData <> NULL(),
			  $sort <> NULL(),
			  $sortType <> NULL() 
			),
			ARRAY_JOIN(
			ARRAY_SELECT(
				$sortData, $(i,item)
				DB_ENCODEIDENTIFIER(ARRAY_INDEX($item, 1)) & " " & ARRAY_INDEX($item, 2)
			), 
			", ",
			),
			AND(
				$sort <> NULL(),
				$sortType <> NULL()
			), 
			DB_ENCODEIDENTIFIER($sort) & " " & $sortType, 

		  "[RecordID] ASC"
		);

		LET $format := [DG - Format]#$i;

		LET $data :=
		IF( 
			OR(
				ISBLANK($targetEForm),
				ISBLANK($columns)
			),

			ARRAY(NULL()),

			DB_LOOKUPALL(
				$lookupColumns,
				$targetEForm,
				COALESCE($filter, "1 = 1") & " AND [LineNumber] = 1",
				HASH("sort", $sortString)
			)
		);

		LET $thStyle := "text-align:left;background-color:#d0d0d0;border:solid #a0a0a0 1px;";
		LET $tdStyle := "text-align:left;background-color:white;border:solid #e0e0e0 1px;";
		LET $evenColumnWidth  := IF($numberOfColumns > 0, 100 / $numberOfColumns);

		LET $columns := 
		IF(
		  $showViewLink = TRUE, 
		  ARRAY_CONCAT("View", $columns),
		  $columns
		);
		LET $widths := 
		IF(
		  $showViewLink = TRUE, 
		  ARRAY_CONCAT("5", $widths),
		  $widths
		);

		LET $data := 
		ARRAY_SELECT(
		  $data, $(i,item) 
		  IF(
			  $showViewLink = TRUE, 
			  ARRAY_CONCAT(ARRAY("View"), $item), 
			  $item
		  )
		); 

		LET $columnsTableFormatted := 
		ARRAY_JOIN(
			ARRAY_SELECT(
			  $columns, $(c, column) 

				LET $width := 
				IF(
						ISBLANK([Columns Widths]#$i), 
						$evenColumnWidth,

						(
						LET $result := TRIM(ARRAY_INDEX($widths, $c));

						RETURN 
							IF(
								ISBLANK($result), 
								25, 
								N($result)
							)
						)
				);

				LET $result := 	
			   "<th style=""width: " & $width  & "%;" & $thStyle & """>" & 
					COALESCE(HASH_GET($alias, $column), $column) & 
				"</th>";

				RETURN $result
			)
		);

		LET $dataTableFormatted := 
		ARRAY_JOIN(
			ARRAY_SELECT(
				$data, $(r, row) 

				LET $recordID := ARRAY_LAST($row); 

				RETURN 
				"<tr>" & 
					ARRAY_JOIN(
						ARRAY_SELECT(
							ARRAY_TAKE($row, ARRAY_LENGTH($row) - 1), $(c,cell)

							LET $cellFormatting := 
							ARRAY_INDEX(
								$format, 
								IF($showViewLink = TRUE, $c - 1, $c)
							);

							LET $viewColumn := 
							AND(
								$showViewLink = TRUE, 
								$c = 1
							); 

						   LET $formattedCellValue := 
						   IF(
							 OR($cellFormatting = "Text", ISBLANK($cellFormatting)), $cell, 
							 $cellFormatting = "URL", "<a href=""" & HTMLENCODE($cell) & """>Link" & "</a>", 					 
							 $cellFormatting = "N", IFERROR(NUMBERFORMAT($cell, "N"), $cell), 
							 $cellFormatting = "C", IFERROR(NUMBERFORMAT($cell, "C"), $cell),
							 $cellFormatting = "Dollar no cents", "$" & IFERROR(NUMBERFORMAT($cell, "#,##0"), $cell),
							 $cellFormatting = "Display Name", IFERROR(EBMS_GET_ROLE_DISPLAYNAME($cell), $cell),
							 $cell 
						   );

							RETURN 
							"<td style=""" & $tdStyle & """>" & 
							IF(
								$viewColumn = TRUE, 
									"<a href=""" & HTMLENCODE(EBMS_GET_EFORMRECORD_URL($targetEForm, $recordID)) & """>View</a>",

								$cellFormatting = "URL",
									$formattedCellValue,

								HTMLENCODE(COALESCE($formattedCellValue, " "))
							)
						  & 
						"</td>"
						)
					) &
				"</tr>"
			)
		);

		LET $table := 
		"<table cellspacing=""0"" style=""border-collapse: collapse; width: 100%"">" &
			$columnsTableFormatted &
			$dataTableFormatted &
		"</table>";

		RETURN 
		$table
	)
)
