Column.DefaultValueFormula:
LET $currentRoles := EBMS_GET_ANCESTOR_ROLES(EBMS_GET_CURRENT_USER());

LET $currentUser := ARRAY_FIRST($currentRoles); 

LET $userGroupMapping:= 
  INDEXES_TO_KEYS(
    ARRAY_SELECT($currentRoles, $(i, item) TRUE),
    $currentRoles
  );  

LET $lookup := 
  DB_LOOKUPALL(
  	ARRAY(
  		"Module Name", 
  		"Module Settings EForm",
  	  	"Module Access",
	  	//header fields
	  	"Automation in Navigation",
	  	"Global Settings in Navigation"
  	), 
  	"Global Settings", 
  	"[RecordID] FAST_EQUAL_TO 1 AND [Module Name] IS NOT EMPTY AND [Module Type] FAST_EQUAL_TO 'Module'"
  );


LET $modules:= ARRAY_SELECT($lookup, $(i,item) ARRAY_TAKE($item, 3));

LET $data :=
ARRAY_SELECT(
	$modules, $(m, module)

	LET $moduleName 		:= 	ARRAY_INDEX($module, 1); 
	LET $moduleSettings 		:=	ARRAY_INDEX($module, 2); 

	LET $settingPage 	:=	
	"records/" & URLENCODE($moduleSettings) & "&[RecorDID]=1"; 

	LET $accessList := ARRAY_INDEX($module, 3); 

	LET $hasAccess :=
	ARRAY_LENGTH(
		ARRAY_WHERE(
			$accessList, $(i, item) 
			ARRAY_LENGTH(
				ARRAY_WHERE(
					$item, $(i, item) 
				  	HASH_GET($userGroupMapping, $item)= TRUE
				)
			) > 0
		)
	) > 0;

	RETURN
	IF( 
		$hasAccess == TRUE, 
		ARRAY(
			$moduleName,
			$moduleSettings
		)
	)
);


LET $extras:= 
INDEXES_TO_KEYS(
  ARRAY_SKIP( ARRAY_FIRST($lookup), 3),
  ARRAY("Automation in Navigation", "Global Settings in Navigation")
  );

LET $modulesPlus:= 
ARRAY_CONCAT(
  	$data, 
  	IF( HASH_GET( $extras, "Global Settings in Navigation") = TRUE, ARRAY(ARRAY("Settings - Global", "Global Settings"))),
	IF( HASH_GET( $extras, "Automation in Navigation") = TRUE, ARRAY(ARRAY("Settings - Automation", "Settings - Automation")))
);


RETURN 
ARRAY_WHERE(
			$modulesPlus, $(i, item) $item <> NULL()
		)

Binding.Expression:
LET $systemBuilder := EBMS_IS_USER_IN_ROLE(EBMS_GET_CURRENT_USER(), "System Builders"); 
LET $dashboardExists := NOT(ISBLANK([Module Dashboard]));
LET $set := 
ARRAY(
	HASH("text", "General", "value", "General"),
   HASH("text", "Lists", "value", "Lists"),
	HASH("text", "Dashboard Customisation", "value", "Dashboard"),
	IF($systemBuilder, HASH("text", "System Design", "value", "System Builders"))//,
  	//IF($dashboardExists, HASH("text", "Go to Dashboard", "value", "Go to Dashboard"))
);

RETURN 
ARRAY_WHERE($set, $(i, item) $item <> NULL())

LET $set := [Module Data];

RETURN
ARRAY_SELECT(
	$set,	  
  	$(i, item) 
	  LET $name := ARRAY_INDEX($item, 1); 
	  LET $eform := ARRAY_INDEX($item, 2); 

	  RETURN
	  HASH(
		  "value", 	$eform,
		  "text", 	IF($eform = "Global Settings", $name, $eform)//$name
	  )
)

LET $systemBuilder := EBMS_IS_USER_IN_ROLE(EBMS_GET_CURRENT_USER(), "System Builders"); 
LET $dashboardExists := NOT(ISBLANK([Module Dashboard]));
LET $set := 
ARRAY(
	HASH("text", "", "value", "General"),
    HASH("text", "", "value", "Lists"),
	HASH("text", "", "value", "Dashboard"),
	IF($systemBuilder, HASH("text", "", "value", "System Builders"))//,
  	//IF($dashboardExists, HASH("text", "Go to Dashboard", "value", "Go to Dashboard"))
);

RETURN 
ARRAY_WHERE($set, $(i, item) $item <> NULL())

FieldEntry.Formula:
ARRAY_WHERE(
	ARRAY_DISTINCT(
		ARRAY_CONCAT(
			[Prefix List],
			[New Prefix]
		)
	), $(i, item) 
	$item <> NULL()
)

ARRAY_WHERE(
	ARRAY_DISTINCT(
		ARRAY_CONCAT(
			[Prefix List],
			[Contract Number Prefix] & YEAR(NOW()) & "-"
		)
	), $(i, item) 
	$item <> NULL()
)

ARRAY_WHERE(
	ARRAY_DISTINCT(
		ARRAY_CONCAT(
			[Prefix List],
			[Contract Number Prefix] & FINANCIALYEAR(NOW()) & "-"
		)
	), $(i, item) 
	$item <> NULL()
)

LET $numberToGenerate := 5; 

LET $maxRecordID := ARRAY_FIRST(
	ARRAY_FIRST(
		DB_REPORT(
			"MAX([RecordID])", 
			"Vendor"
		)
	)
); 

LET $companyNames :=
ARRAY_DISTINCT(
	ARRAY_SELECT(
		RANGE(0, $numberToGenerate), $(i, item)	
		ARRAY_INDEX([List of Random Vendor Names], CEILING(RAND() * ARRAY_LENGTH([List of Random Vendor Names])))	
	)
);

RETURN 
ARRAY_SELECT(
	$companyNames, $(i, item) 
	HASH(
		"Company Name", $item, 
		"BusinessNumber", NUMBERFORMAT(CEILING(RAND() * 100000000), "000000000")
	)
)

LET $prefix := 
[Contract Number Prefix] & 
IF( 
	[Use Current Year Prefix], YEAR(NOW()) & "-",
	[Use Current Financial Year Prefix], FINANCIALYEAR(NOW()) & "-"
);

LET $RiskNumber := N(REPLACESUBSTRING($prefix, [Next Contract Number], ""));

LET $numberToGenerate := 5;

RETURN
ARRAY_SELECT(	
	RANGE(0, $numberToGenerate), $(i, item)		
	LET $RiskTitle := 
	ARRAY_INDEX([Actions for Contract Generation], CEILING(RAND()* ARRAY_LENGTH([Actions for Contract Generation])))
	& " of a " 
	& ARRAY_INDEX([Adjectives for Contract Generation], CEILING(RAND()* ARRAY_LENGTH([Adjectives for Contract Generation])))
	& " " 
	& ARRAY_INDEX([Nouns for Contract Generation], CEILING(RAND()* ARRAY_LENGTH([Nouns for Contract Generation])));

	LET $vendorDetails := 
	ARRAY_INDEX(
		[Random Vendor Data], 
		CEILING(RAND() * ARRAY_LENGTH([Random Vendor Data]))
	);

	LET $vendorRecordID := ARRAY_INDEX($vendorDetails, 1);
	LET $vendorName := ARRAY_INDEX($vendorDetails, 2);
	LET $vendorBusinessNumber := ARRAY_INDEX($vendorDetails, 3);
	LET $startDate :=  DATE(YEAR(NOW() - FLOOR(RAND() * 5) + 2), MONTH(NOW()) - FLOOR(RAND() * 5) + 2, FLOOR(RAND() * 28));
	LET $originalEndDate := DATE(YEAR($startDate), MONTH($startDate) + FLOOR(RAND() * 6), FLOOR(RAND() * 28));

	LET $departmentsList := 
		DB_LOOKUPALL(
			"Name", 
			"sys.Departments"
		);
	LET $departmentsCount := ARRAY_LENGTH($departmentsList);

	LET $department := ARRAY_INDEX($departmentsList, 1 + FLOOR(RAND() * $departmentsCount));

	RETURN
	HASH(
		"Contract Title", $RiskTitle, 
		"Initial Contract Value Incl. Tax", N(ROUND(RAND() * 100, 0) & "000"), 
		"Contract Number", [Contract Number Prefix] & $RiskNumber + $i, 
		"Start Date", $startDate,
		"Original End Date",  $originalEndDate, 
		"Current End Date",  $originalEndDate, 
		"BusinessNumber", $vendorBusinessNumber, 
		"Vendor Name", $vendorName, 
		"Vendor RecordID", $vendorRecordID, 
		"Department", $department
	)
)

LET $RiskRecordIDs := [Contract RecordIDs];
LET $numberToGenerate := 8;
LET $consequences := ARRAY( "Insignificant", "Minor", "Moderate", "Major", "Catastrophic" );
LET $likelihoods := ARRAY( "Rare", "Unlikely", "Possible", "Likely", "Almost Certain" );
LET $generatedData := ARRAY_SELECT( RANGE(0, $numberToGenerate), $(i, item)
LET $risk := "Large " & ARRAY_INDEX([Adjectives for Contract Generation], CEILING(RAND()* ARRAY_LENGTH([Adjectives for Contract Generation]))) & " " & ARRAY_INDEX([Nouns for Contract Generation], CEILING(RAND()* ARRAY_LENGTH([Nouns for Contract Generation]))) & " Affecting " & ARRAY_INDEX([Adjectives for Contract Generation], CEILING(RAND()* ARRAY_LENGTH([Adjectives for Contract Generation]))) & " " & ARRAY_INDEX([Nouns for Contract Generation], CEILING(RAND()* ARRAY_LENGTH([Nouns for Contract Generation])));
LET $consequence := ARRAY_INDEX( $consequences, CEILING(RAND() * ARRAY_LENGTH($consequences)) );
LET $likelihood := ARRAY_INDEX( $likelihoods, CEILING(RAND() * ARRAY_LENGTH($likelihoods)) );
RETURN HASH( "Risk", $risk, "Consequence", $consequence, "Likelihood", $likelihood ) );
RETURN $generatedData

LET $RiskRecordIDs := [Contract RecordIDs];

LET $numberToGenerate := 5;

LET $generatedData := 
ARRAY_SELECT(	
	RANGE(0, $numberToGenerate), $(i, item)		

	LET $varyDate := FLOOR(RAND() * 2) = 1;	
	LET $varyPrice := 
	OR(
		FLOOR(RAND() * 2) = 1,
		$varyDate = FALSE
	);

	LET $variationValue := FLOOR((FLOOR(RAND() * 100000) + 5000) / 100) * 50;

	RETURN
	HASH(
		"Variation Value", 		$variationValue,
		"Variation to Date", 		$varyDate,
		"Variation to Price", 		$varyPrice
	)
);

RETURN $generatedData

LET $RiskRecordIDs := [Contract RecordIDs];

LET $numberToGenerate := 5;

LET $generatedData := 
ARRAY_SELECT(	
	RANGE(0, $numberToGenerate), $(i, item)		

	LET $years := FLOOR(RAND() * 5);	
	LET $months := FLOOR(RAND() * 12);


	LET $expiryDate := 
	DATE(YEAR(NOW()) + FLOOR(RAND() * 3), MONTH(NOW()) + FLOOR(RAND() * 12));

	RETURN
	HASH(
		"Extension Years", $years,
		"Extension Months", $months,
		"Expiry Date", $expiryDate
	)
);

RETURN $generatedData
