RETURN
HASH(
  "guid", [Approval GUID]#$lineToPartApprove, 
  "username", [Username]#$lineToPartApprove, 
  "status", [Signer Status]#$lineToPartApprove, 
  "datetime", [Sign Date]#$lineToPartApprove,
  "Comment", [Sign Comments]#$lineToPartApprove
)

