FieldEntry.Formula:
LET $linesApproved := HASH_GET([Part Approval Prep],"LineToPartApprove");

LET $linesRejected := HASH_GET([Part Approval Prep],"LineToReject");

RETURN
ARRAY_SELECT(
  EACH [Name], $(i, item) 
  IF(
	OR(
	  	ARRAY_INDEXOF($linesApproved, $i) > 0,
	  	ARRAY_INDEXOF($linesRejected, $i) > 0
	), 	
	TRUE, 
	[Line Approval Processed]#$i)
  )

Plugins.DocuSign.SendEnvelopeTask.SignersFormula:
LET $data := ARRAY_TRANSPOSE(
	ARRAY(
	  	EACH [Custom Person], //1
	  	EACH [Username], //2
		EACH [Name], //3
		EACH [Email], //4
		EACH [Mobile] //5
	)
);

LET $demoMode := DB_LOOKUP("Demo Mode", "Global Settings", "[RecordID] = 1"); 
LET $demoEmail:=  DB_LOOKUP("DocuSign Email", "Settings - Approval", "[RecordID] = 1"); 

LET $signers := ARRAY_SELECT(
	$data,
	$(i, item) 
  		HASH(
			"name", ARRAY_INDEX($item, 3), 
			"email", IF($demoMode = TRUE, $demoEmail, COALESCE(ARRAY_INDEX($item, 4), $demoEmail)),
			"mobileNumber", ARRAY_INDEX($item, 5),

			"nameAnchor", "<<signer{0}name>>",
			"companyAnchor", "<<signer{0}company>>",
			"titleAnchor", "<<signer{0}title>>",
			"dateAnchor", "<<signer{0}date>>",
	  		"notes", 	IF(
		  					OR(
		  						ARRAY_INDEX($item, 1) == TRUE,
		  						EBMS_GET_USER_TYPE(ARRAY_INDEX($item, 2)) == "GUEST"
		  					), 
		  					"Submitter Notes: " & HASH_GET([Submitter Notes], "External Notes"), 
		  					"Submitter Notes: " & HASH_GET([Submitter Notes], "Internal Notes")
	  					)


				)
		);

RETURN $signers

FieldEntry.Formula:
LET $result := 
ARRAY_SELECT(
  [DocuSign Signer Statuses], $(i, item) 
  HASH_GET($item, "status")
);

RETURN 
ARRAY_SELECT(
  $result, $(i, item) 
  IF(
	[Bypass Approval]#$i = TRUE, "completed", 
	$item
  )
)

LET $result := 
ARRAY_SELECT(
  [DocuSign Signer Statuses], $(i, item) 
  HASH_GET($item, "signedDate")
);

RETURN 
ARRAY_SELECT(
  $result, $(i, item) 
  IF(
	[Bypass Approval]#$i = TRUE, [Sign Date]#$i, 
	$item
  )
)

LET $numberOfApprovals := ARRAY_LENGTH(EACH [Name]);

LET $linesToPartApprove := 
ARRAY_SELECT(
  EACH [Signer Status], $(i, currentStatus) 
  LET $changed := ARRAY_INDEX(EACH [loaded].[Signer Status], $i) <> $currentStatus;
  LET $complete := $currentStatus = "completed";
  LET $notFinalApproval := $i <> $numberOfApprovals;

  RETURN IF(AND($changed, $complete, $notFinalApproval), $i)
);

LET $result := ARRAY_WHERE($linesToPartApprove, $(i,item) $item <> NULL());
LET $numberOfPartApprovals := ARRAY_LENGTH($result);

RETURN 
HASH(
  "LineToPartApprove", $result,
  "NumberOfPartApproves", $numberOfPartApprovals
)
