RETURN ARRAY_LENGTH($filtered)

LET $data := 
DB_LOOKUPALL(
  ARRAY("Username", "DisplayName"), 
  "sys.Roles", 
  "[Type] <> 'GUEST' AND [Active] = TRUE AND [IsSystem] <> TRUE AND ([DisplayName] IS NOT EMPTY OR [Type] = 'GROUP')"
);

RETURN 
ARRAY_SELECT(
	$data, $(i, item) 
	LET $value := ARRAY_INDEX($item, 1);
	LET $text := 
	IF(
		ISBLANK(ARRAY_INDEX($item, 2)), 
		ARRAY_INDEX($item, 1), 
		ARRAY_INDEX($item, 2)
	);

	RETURN 
	HASH(
	  "value", $value,
	  "text", $text
  )
)

LET $recipientRoles := IF(
  	ARRAY_LENGTH([Recipients - Roles]) <> 0,
 	ARRAY_JOIN( ARRAY_SELECT([Recipients - Roles], $(i, item) EBMS_GET_ROLE_EMAIL_ADDRESSES($item)), ", "),
	NULL()
);

