FieldEntry.Formula:
LET $hash := 
HASH(
  "Field Mapping", 
	HASH(
	  //Import column -> Populate column
	  "Question #", 			NULL(), 
	  "Question Number", 		NULL(), 

	  "Question", 				"Question",

	  "Question Category",		"Question Category",
	  "Category",				"Question Category",

	  "Axis Name",				"Axis Name",
	  "Axis",					"Axis Name",

	  "Document Upload",		"Participant Document Upload",

	  "Answer Type", 			"Answer Type",

	  "Answer", 				"Answer",
	  "Score", 					"Score",

	  "Response Mandatory", 	"Mandatory",
	  "Upload Mandatory", 		"Document Upload Mandatory",

	  "Dropdown - Answer 1",	"Answer",
	  "Dropdown - Score 1", 	"Score",
	  "Dropdown - Answer 2",	"Answer",
	  "Dropdown - Score 2", 	"Score",
	  "Dropdown - Answer 3",	"Answer",
	  "Dropdown - Score 3", 	"Score",
	  "Dropdown - Answer 4",	"Answer",
	  "Dropdown - Score 4", 	"Score",

	  "Answer1", 				"Answer",
	  "Score1", 				"Score",
	  "Answer2", 				"Answer",
	  "Score2", 				"Score",
	  "Answer3", 				"Answer",
	  "Score3", 				"Score",
	  "Answer4", 				"Answer",
	  "Score4", 				"Score",

	  "Range - Lower Limit", 	"Answer - Lower Limit",
	  "Range - Upper Limit", 	"Answer - Upper Limit"
  )
);

LET $injectData := 
HASH(
	"Questionnaire", ARRAY([Name]),
	"Standard Questionnaire RecordID", ARRAY([RecordID])
); 

LET $fields := HASH_GET($hash, "Field Mapping"); 

LET $importColumns := ARRAY_FIRST([Imported Questions]); 

LET $toPopulate := ARRAY_WHERE(ARRAY_SELECT($importColumns, $(i, item) HASH_GET($fields, $item)), $(i, item) $item <> NULL());
LET $distinctToPopulate := ARRAY_DISTINCT($toPopulate);
LET $answerIndexes := ARRAY_WHERE(ARRAY_SELECT($toPopulate, $(i, item) IF($item = "Answer", $i)), $(i, item) $item <> NULL());
LET $scoreIndexes := ARRAY_WHERE(ARRAY_SELECT($toPopulate, $(i, item) IF($item = "Score", $i)), $(i, item) $item <> NULL());
LET $hashIndexes := HASH("Answer", $answerIndexes, "Score", $scoreIndexes);
LET $numberOfExtraAnswerColumns := ARRAY_LENGTH($answerIndexes) * 2 - 2;

LET $data := ARRAY_SKIP([Imported Questions], 1); 
LET $data := ARRAY_WHERE($data, $(i, item) AND(ARRAY_INDEX($item, 2) <> NULL(), $item <> ARRAY(NULL()))); 

LET $existingQuestionData := DB_LOOKUPALL(ARRAY("Question GUID", "RecordID"), "Standard Question", "[LineNumber] = 1 AND [Standard Questionnaire RecordID] = " & DB_ENCODECONSTANT([RecordID]));
LET $existingQuestionUUIDs := ARRAY_INDEX(ARRAY_TRANSPOSE($existingQuestionData), 1);

LET $dataToUpdate := 
ARRAY_WHERE(
  $data, $(i, item) 
  ARRAY_INDEXOF(
	$existingQuestionUUIDs, 
	ARRAY_INDEX($item, 1)
  ) > 0
);

LET $recordsToUpdate := 
ARRAY_SELECT(
  $dataToUpdate, $(i, item) 
  LET $UUID := ARRAY_INDEX($item, 1); 
  LET $index := ARRAY_INDEXOF($existingQuestionUUIDs, $UUID); 
  LET $recordID := ARRAY_INDEX(ARRAY_INDEX($existingQuestionData, $index), 2);
  RETURN $recordID
);

LET $dataToCreate := 
ARRAY_WHERE(
  $data, $(i, item) 
  ARRAY_INDEXOF(
	$existingQuestionUUIDs, 
	ARRAY_INDEX($item, 1)
  ) = NULL()
);

LET $recordsToCreate := ARRAY_LENGTH(ARRAY_DISTINCT(ARRAY_SELECT($dataToCreate, $(i, item) ARRAY(ARRAY_INDEX($item, 1), ARRAY_INDEX($item, 3)))));

LET $setupStructure := $(data)(
	LET $grouped := 
	ARRAY_TO_HASH_GROUPED(
		$data, 
		$(i, item) ARRAY_TAKE($item, 1),  
		$(i, item) ARRAY_SKIP($item, 1)
	);

	LET $values :=  
	ARRAY_SELECT(HASH_KEYS($grouped), $(i, item) HASH_GET($grouped, $item));

	LET $structuredData :=  
	ARRAY_SELECT(
		$values, $(i, question) 

		LET $arrayStructure := 
		ARRAY_SELECT(
			$distinctToPopulate, $(c, column)

			LET $targetLineData := 
			IF(
				OR(
					$column = "Answer", 
					$column = "Score"
				),
				ARRAY_SELECTMANY(
					HASH_GET($hashIndexes, $column), $(i, item) 
					LET $result := ARRAY_SELECTMANY($question, $(l, linedata) ARRAY_INDEX($linedata, $item));
					RETURN $result
				), 

				ARRAY_SELECT(
					$question, $(l, linedata) 
					LET $indexIsAfterAnswers := $c > ARRAY_INDEXOF($distinctToPopulate, "Answer");
					LET $result := 
					IF(
						$indexIsAfterAnswers, 
						ARRAY_INDEX($linedata, $c + $numberOfExtraAnswerColumns),
						ARRAY_INDEX($linedata, $c)
					);
					RETURN 
				    IF(
					  AND(
						$column = "Mandatory", 
						$result = NULL()
					  ), 
					  FALSE, 
					  $result
				    )  

				)
			);

			RETURN 
			ARRAY($column, $targetLineData)	
		);		
		LET $transposed := ARRAY_TRANSPOSE($arrayStructure);

		LET $hashedResult :=  
		INDEXES_TO_KEYS(
			ARRAY_INDEX($transposed, 2), 
			ARRAY_INDEX($transposed, 1) 
		);

		RETURN HASH_MERGE($hashedResult, $injectData)

	);

	RETURN $structuredData
);

LET $resultHash :=  
HASH(
	"Records to Update", $recordsToUpdate,
	"Update Data", CALL($setupStructure, $dataToUpdate),
	"# Records to Create", $recordsToCreate,
	"Create Data", CALL($setupStructure, $dataToCreate)
);

RETURN 
$resultHash

