LET $circleBackgrounds := 
ARRAY(
	"#c6d7e2", 
	"#c6e1e2", 
	"#c6e2d5", 
	"#c6e2c8", 
	"#d1e2c6", 
	"#dee2c6", 
	"#e2d8c6", 
	"#e2c6c6", 
	"#e2c6d4", 
	"#dec6e2", 
	"#d1c6e2", 
	"#c6c7e2", 
);

LET $indicatorHeight := 45; 
LET $indicatorWidth := 45; 

LET $width := 670 * 1.8;
LET $height := 313 * 1.8;

LET $maxYAxisScore := [Max Score - Axis 2];
LET $maxXAxisScore := [Max Score];

LET $heightRescaled := $height - $indicatorHeight;
LET $widthRescaled := $width - $indicatorWidth;


LET $targetRecords := [Aggregate RecordIDs];

LET $scores := 
ARRAY_SELECT(
  $targetRecords, $(i, item) 
  DB_LOOKUP(ARRAY("Axis 1 Score", "Axis 2 Score", "Participant Name", "RecordID"), "Questionnaire", HASH("LineNumber", 1, "RecordID", $item))
); 

LET $dotData := 
ARRAY_SELECT(
  $scores, $(i, item)
  LET $yAxisScore := ARRAY_INDEX($item, 2);
  LET $xAxisScore := ARRAY_INDEX($item, 1);
  LET $name := ARRAY_INDEX($item, 3);
  LET $recordID := ARRAY_INDEX($item, 4);

  LET $percentageYScore := IFERROR((($maxYAxisScore - $yAxisScore) / $maxYAxisScore), 1);
  LET $percentageXScore := IFERROR($xAxisScore / $maxXAxisScore, 1);

  LET $top := $heightRescaled * $percentageYScore;
  LET $left := $widthRescaled * $percentageXScore;  

  LET $colour := ARRAY_INDEX($circleBackgrounds, MOD($i, ARRAY_LENGTH($circleBackgrounds)));

  RETURN
  HASH(
	"background", $colour,
	"top", FLOOR(COALESCE(NULL(), $top)),
	"left", FLOOR(COALESCE(NULL(), $left)),
	"recordID", $recordID,
	"text", ARRAY_FIRST(SPLIT($name, " "))
  )
); 

LET $combiningMatches :=
ARRAY_SELECT(
	$dotData, $(i, item) 

	LET $left := HASH_GET($item, "left");
	LET $top := HASH_GET($item, "top");

	LET $matchingPosition := 
	ARRAY_WHERE(
		$dotData, $(j, jitem) 
		LET $thisLeft := HASH_GET($jitem, "left");
		LET $thisTop := HASH_GET($jitem, "top");

		RETURN 
		AND(
			$left = $thisLeft,
			$top = $thisTop,
			$i < $j
		)
	);

	LET $toBeRemoved := 
	ARRAY_WHERE(
		$dotData, $(j, jitem) 
		LET $thisLeft := HASH_GET($jitem, "left");
		LET $thisTop := HASH_GET($jitem, "top");

		RETURN 
		AND(
			$left = $thisLeft,
			$top = $thisTop,
			$i > $j
		)
	);

	RETURN 
	IF( 
		ARRAY_LENGTH($matchingPosition) > 0, 
		(
			LET $textToAdd := ARRAY_SELECT($matchingPosition, $(i, item) HASH_GET($item, "text"));
			LET $currentText := HASH_GET($item, "text");
			LET $newText := ARRAY_CONCAT($currentText, $textToAdd);

			LET $recordIDToAdd := ARRAY_SELECT($matchingPosition, $(i, item) HASH_GET($item, "recordID"));
			LET $currentrecordID := HASH_GET($item, "recordID");
			LET $newrecordID := ARRAY_CONCAT($currentrecordID, $recordIDToAdd);

			LET $keys := ARRAY_WHERE(HASH_KEYS($item), $(i, item) AND($item <> "text", $item <> "recordID"));
			LET $values := ARRAY_SELECT($keys, $(k, key) HASH_GET($item, $key));
			RETURN HASH_MERGE(INDEXES_TO_KEYS($values, $keys), HASH("text", $newText), HASH("recordID", $newrecordID))
		),

		ARRAY_LENGTH($toBeRemoved) > 0, 
		NULL(), 

		$item
	)
);

LET $dots := ARRAY_WHERE($combiningMatches, $(i, item) $item <> NULL()); 

LET $dots := 
ARRAY_SELECT(
  $dots, $(i, item)
  LET $background := 	HASH_GET($item, "background");
  LET $top := 			HASH_GET($item, "top");
  LET $left := 			HASH_GET($item, "left");
  LET $recordID := 		HASH_GET($item, "recordID");
  LET $text := 			HASH_GET($item, "text");

  LET $links := 
  IF( 
	ARRAY_LENGTH($recordID) > 1, 
  	ARRAY_JOIN(ARRAY_SELECT(
	  $recordID, $(r, record) 
	  "<a style=""text-decoration: none;"" target=""_blank"" href=""records/Questionnaire/" & $record & """><div style=""color: black;"">" & ARRAY_INDEX($text, $r) & "</div></a>"
	), ""), 
	"<a style=""width: 100%; text-decoration: none;"" target=""_blank"" href=""records/Questionnaire/" & $recordID & """><div style=""color: black;"">" & $text & "</div></a>"
  ); 

  RETURN
  IF(
	$recordID <> NULL(),
    "<td class=""result"" style=""display: grid; align-items: center; opacity: 0.75; border-color:" & $background & "; background: " & $background & "; top: " & $top & "px; left: " & $left & "px"">" & 
	$links & 
	"</td>"
  )
); 

LET $dots := ARRAY_JOIN($dots, "");

RETURN
"<table id=""q-graph-2"">
 <tbody>
   <tr>
    <td><div class=""segmentHeading"">" & HTMLENCODE([Quadrant Name - 1]) & "</div></td>
    <td><div class=""segmentHeading"">" & HTMLENCODE([Quadrant Name - 2]) & "</div></td>
  </tr>
